import { useCallback, useState, useEffect } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const categories = [
  { value: 'ASET', label: 'ASET' },
  { value: 'KEWAJIBAN', label: 'KEWAJIBAN' },
  { value: 'MODAL', label: 'MODAL' },
  { value: 'PENDAPATAN', label: 'PENDAPATAN' },
  { value: 'BIAYA', label: 'BIAYA' },
  { value: 'PENDAPATAN_LAIN', label: 'PENDAPATAN LAIN' },
];

const accountTypes = {
  ASET: [
    'Kas/Bank',
    'Akun Piutang',
    'Persediaan',
    'Aktiva Lancar lainnya',
    'Aktiva Tetap',
    'Akumulasi Penyusutan',
  ],
  KEWAJIBAN: ['Akun Hutang', 'Hutang Lancar Lainnya', 'Hutang Jangka Panjang'],
  MODAL: ['Ekuitas'],
  PENDAPATAN: ['Pendapatan'],
  BIAYA: ['Harga Pokok Penjualan', 'Beban', 'Beban Lain-lain'],
  PENDAPATAN_LAIN: ['Pendapatan Lain'],
};

export default function ChartOfAccount() {
  const today = new Date().toISOString().split('T')[0];

  const [accounts, setAccounts] = useState([]);
  const [form, setForm] = useState({
    name: '',
    category: '',
    account_type: '',
    is_group: false,
    parent_id: '',
    opening_balance: '',
    as_of_date: today,
  });

  const [editingId, setEditingId] = useState(null);
  const [filterCategory, setFilterCategory] = useState('');
  const [filterType, setFilterType] = useState('');
  const [search, setSearch] = useState('');
  const [parents, setParents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);

  const fetchAccounts = useCallback(async () => {
    const res = await api.get('/api/chart-of-accounts', {
      params: {
        category: filterCategory,
        type: filterType,
        search,
      },
    });
    setAccounts(res.data);

    // Set parents for dropdown (only group accounts)
    setParents(res.data.filter((a) => a.is_group));
  }, [filterCategory, filterType, search]);

  useEffect(() => {
    fetchAccounts();
  }, [fetchAccounts]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));

    // Reset dependent fields when category changes
    if (name === 'category') {
      setForm((prev) => ({
        ...prev,
        account_type: '',
        parent_id: '',
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name.trim()) return alert('Nama akun wajib diisi.');
    if (!form.category) return alert('Kategori wajib dipilih.');
    if (!form.account_type) return alert('Tipe akun wajib dipilih.');
    if (!form.as_of_date) return alert('Tanggal wajib diisi.');
    if (
      !form.is_group &&
      !form.parent_id &&
      accounts.some((a) => a.category === form.category)
    )
      return alert('Pilih akun induk untuk akun detail.');

    try {
      const payload = {
        ...form,
        opening_balance:
          form.opening_balance === '' ? 0 : parseFloat(form.opening_balance),
      };

      if (editingId) {
        await api.put(`/api/chart-of-accounts/${editingId}`, payload);
      } else {
        await api.post('/api/chart-of-accounts', payload);
      }

      setForm({
        name: '',
        category: '',
        account_type: '',
        is_group: false,
        parent_id: '',
        opening_balance: '',
        as_of_date: today,
      });
      setEditingId(null);
      setShowModal(false);
      fetchAccounts();
    } catch (err) {
      console.error(err);
      alert('Terjadi kesalahan saat menyimpan akun.');
    }
  };

  const handleEdit = (account) => {
    setForm({
      name: account.name,
      category: account.category,
      account_type: account.account_type,
      is_group: account.is_group,
      parent_id: account.parent_id || '',
      opening_balance: account.opening_balance,
      as_of_date: account.as_of_date,
    });
    setEditingId(account.id);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    try {
      await api.delete(`/api/chart-of-accounts/${confirmDeleteId}`);
      setConfirmDeleteId(null);
      fetchAccounts();
    } catch (err) {
      console.error(err);
      alert('Gagal menghapus akun.');
    }
  };

  const renderAccountTree = (nodes, level = 0) => {
    return nodes
      .filter((node) => node.parent_id === null || node.parent_id === '')
      .map((node) => (
        <>
          <tr key={node.id} className={level > 0 ? 'bg-gray-50' : ''}>
            <td className="border p-2">
              {' '.repeat(level * 2)}
              {node.account_no}
            </td>
            <td className="border p-2">
              {' '.repeat(level * 2)}
              {node.name}
            </td>
            <td className="border p-2">{node.category}</td>
            <td className="border p-2">{node.account_type}</td>
            <td className="border p-2">
              {parseFloat(node.opening_balance || 0).toLocaleString('id-ID')}
            </td>
            <td className="border p-2 text-center space-x-2">
              <button
                onClick={() => handleEdit(node)}
                className="bg-blue-50 hover:bg-blue-100 text-blue-600 hover:text-blue-800 px-3 py-1 rounded-md transition duration-200"
                title="Edit"
              >
                <i className="bi bi-pencil-fill"></i>
              </button>
              <button
                onClick={() => handleDelete(node.id)}
                className="bg-red-50 hover:bg-red-100 text-red-600 hover:text-red-800 px-3 py-1 rounded-md transition duration-200"
                title="Hapus"
              >
                <i className="bi bi-trash-fill"></i>
              </button>
            </td>
          </tr>
          {accounts
            .filter((child) => child.parent_id === node.id)
            .sort((a, b) => a.account_no.localeCompare(b.account_no))
            .map((child) => renderAccountTree([child], level + 1))}
        </>
      ));
  };

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Chart of Accounts</h2>
          <button
            onClick={() => {
              setEditingId(null);
              setForm({
                name: '',
                category: '',
                account_type: '',
                is_group: false,
                parent_id: '',
                opening_balance: '',
                as_of_date: today,
              });
              setShowModal(true);
            }}
            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded transition"
          >
            + New Account
          </button>
        </div>

        <div className="mb-4 flex gap-2">
          <select
            onChange={(e) => setFilterCategory(e.target.value)}
            className="border p-2 rounded flex-1"
            value={filterCategory}
          >
            <option value="">All Categories</option>
            {categories.map((cat) => (
              <option key={cat.value} value={cat.value}>
                {cat.label}
              </option>
            ))}
          </select>

          <select
            onChange={(e) => setFilterType(e.target.value)}
            className="border p-2 rounded flex-1"
            value={filterType}
            disabled={!filterCategory}
          >
            <option value="">All Types</option>
            {filterCategory &&
              accountTypes[filterCategory].map((type) => (
                <option key={type} value={type}>
                  {type}
                </option>
              ))}
          </select>

          <input
            type="text"
            placeholder="Search by name"
            onChange={(e) => setSearch(e.target.value)}
            className="border p-2 rounded flex-1"
          />
        </div>

        <div className="overflow-x-auto">
          <table className="w-full border">
            <thead>
              <tr className="bg-gray-100">
                <th className="border p-2">Account No</th>
                <th className="border p-2">Name</th>
                <th className="border p-2">Category</th>
                <th className="border p-2">Type</th>
                <th className="border p-2">Balance</th>
                <th className="border p-2 text-center">Action</th>
              </tr>
            </thead>
            <tbody>
              {accounts.length > 0 ? (
                renderAccountTree(accounts)
              ) : (
                <tr>
                  <td colSpan="6" className="text-center text-gray-500 py-4">
                    No accounts found
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        {/* Modal Form */}
        {showModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
            <div className="bg-white p-6 rounded w-full max-w-lg relative shadow-lg">
              <button
                className="absolute top-2 right-2 text-gray-500 hover:text-black"
                onClick={() => {
                  setShowModal(false);
                  setEditingId(null);
                }}
              >
                ✕
              </button>
              <h3 className="text-lg font-semibold mb-4">
                {editingId ? 'Edit Account' : 'Create New Account'}
              </h3>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Account Name
                  </label>
                  <input
                    name="name"
                    value={form.name}
                    onChange={handleChange}
                    placeholder="Account name"
                    className="border p-2 w-full rounded"
                    required
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Category
                    </label>
                    <select
                      name="category"
                      value={form.category}
                      onChange={handleChange}
                      className="border p-2 w-full rounded"
                      required
                    >
                      <option value="">-- Select Category --</option>
                      {categories.map((cat) => (
                        <option key={cat.value} value={cat.value}>
                          {cat.label}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Account Type
                    </label>
                    <select
                      name="account_type"
                      value={form.account_type}
                      onChange={handleChange}
                      className="border p-2 w-full rounded"
                      required
                      disabled={!form.category}
                    >
                      <option value="">-- Select Type --</option>
                      {form.category &&
                        accountTypes[form.category].map((type) => (
                          <option key={type} value={type}>
                            {type}
                          </option>
                        ))}
                    </select>
                  </div>
                </div>

                <div className="flex items-center gap-2">
                  <input
                    type="checkbox"
                    name="is_group"
                    checked={form.is_group}
                    onChange={handleChange}
                    id="is_group"
                    className="h-4 w-4"
                  />
                  <label
                    htmlFor="is_group"
                    className="text-sm font-medium text-gray-700"
                  >
                    This is a group account
                  </label>
                </div>

                {!form.is_group && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Parent Account
                    </label>
                    <select
                      name="parent_id"
                      value={form.parent_id}
                      onChange={handleChange}
                      className="border p-2 w-full rounded"
                      disabled={!form.category}
                    >
                      <option value="">-- Select Parent --</option>
                      {parents
                        .filter((p) => p.category === form.category)
                        .map((p) => (
                          <option key={p.id} value={p.id}>
                            {p.account_no} - {p.name}
                          </option>
                        ))}
                    </select>
                  </div>
                )}

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Opening Balance
                    </label>
                    <input
                      type="number"
                      name="opening_balance"
                      value={form.opening_balance}
                      onChange={handleChange}
                      placeholder="0"
                      className="border p-2 w-full rounded"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      As of Date
                    </label>
                    <input
                      type="date"
                      name="as_of_date"
                      value={form.as_of_date}
                      onChange={handleChange}
                      className="border p-2 w-full rounded"
                      required
                    />
                  </div>
                </div>

                <div className="flex justify-end gap-2 pt-4">
                  <button
                    type="button"
                    onClick={() => {
                      setShowModal(false);
                      setEditingId(null);
                    }}
                    className="px-4 py-2 border rounded text-gray-600 hover:bg-gray-50"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                  >
                    {editingId ? 'Update' : 'Save'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Delete Confirmation Modal */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-sm">
              <h2 className="text-lg font-semibold mb-4 text-red-600 flex items-center">
                <i className="bi bi-exclamation-triangle-fill mr-2 text-xl" />
                Confirm Deletion
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Are you sure you want to delete this account?
              </p>
              <div className="flex justify-end gap-3">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 rounded text-gray-600 hover:bg-gray-100"
                >
                  Cancel
                </button>
                <button
                  onClick={confirmDelete}
                  className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded"
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
}
