import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';

const ContactTab = ({ form, setForm }) => {
  const [editingIndex, setEditingIndex] = useState(null);
  const [contactForm, setContactForm] = useState(initialContact());
  const [isModalOpen, setIsModalOpen] = useState(false);

  function initialContact() {
    return {
      temp_id: uuidv4(),
      first_name: '',
      last_name: '',
      title: '',
      business_phone1: '',
      business_phone2: '',
      mobile_phone: '',
      pager: '',
      email: '',
      extension1: '',
      extension2: '',
      fax: '',
      home_phone: '',
      notes: '',
    };
  }

  const handleChange = (field, value) => {
    setContactForm((prev) => ({ ...prev, [field]: value }));
  };

  const saveContact = () => {
    if (editingIndex !== null) {
      const updated = [...form.customer_contacts];
      updated[editingIndex] = contactForm;
      setForm((prev) => ({ ...prev, customer_contacts: updated }));
      setEditingIndex(null);
    } else {
      setForm((prev) => ({
        ...prev,
        customer_contacts: [...(prev.customer_contacts || []), contactForm],
      }));
    }
    setContactForm(initialContact());
    setIsModalOpen(false);
  };

  const editContact = (index) => {
    setEditingIndex(index);
    setContactForm(form.customer_contacts[index]);
    setIsModalOpen(true);
  };

  const deleteContact = (index) => {
    const updated = [...form.customer_contacts];
    updated.splice(index, 1);
    setForm((prev) => ({ ...prev, customer_contacts: updated }));
    if (editingIndex === index) {
      setEditingIndex(null);
      setContactForm(initialContact());
      setIsModalOpen(false);
    }
  };

  const cancelEdit = () => {
    setEditingIndex(null);
    setContactForm(initialContact());
    setIsModalOpen(false);
  };

  const openAddContactModal = () => {
    setEditingIndex(null);
    setContactForm(initialContact());
    setIsModalOpen(true);
  };

  return (
    <div className="space-y-6">
      {/* Kontak Table */}
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th
                scope="col"
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
              >
                Nama
              </th>
              <th
                scope="col"
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
              >
                Nama Depan
              </th>
              <th
                scope="col"
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
              >
                Jabatan
              </th>
              <th
                scope="col"
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
              >
                Telepon
              </th>
              <th
                scope="col"
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
              >
                Aksi
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {form.customer_contacts?.map((contact, i) => (
              <tr key={contact.temp_id || i}>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {contact.first_name} {contact.last_name}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                  {contact.first_name}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                  {contact.title}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                  {contact.business_phone1}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                  <button
                    onClick={() => editContact(i)}
                    className="text-blue-600 hover:text-blue-900 mr-4"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => deleteContact(i)}
                    className="text-red-600 hover:text-red-900"
                  >
                    Hapus
                  </button>
                </td>
              </tr>
            ))}
            {form.customer_contacts?.length === 0 && (
              <tr>
                <td
                  colSpan="5"
                  className="px-6 py-4 text-center text-sm text-gray-500"
                >
                  Belum ada kontak
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Add Contact Button - Updated to match modal button size */}
      <div className="flex justify-end">
        <button
          onClick={openAddContactModal}
          className="inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-blue-600 text-base font-medium text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:w-auto sm:text-sm"
        >
          Tambah Kontak Baru
        </button>
      </div>

      {/* Modal for Contact Form */}
      {isModalOpen && (
        <div className="fixed z-10 inset-0 overflow-y-auto">
          <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 transition-opacity" aria-hidden="true">
              <div className="absolute inset-0 bg-gray-500 opacity-75" onClick={() => setIsModalOpen(false)}></div>
            </div>
            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-4xl sm:w-full">
              <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <h3 className="text-lg font-medium text-gray-900 mb-4">
                  {editingIndex !== null ? 'Edit Kontak' : 'Tambah Kontak Baru'}
                </h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {/* ... (rest of the form fields remain the same) ... */}
                </div>
              </div>
              <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                {editingIndex !== null && (
                  <button
                    onClick={cancelEdit}
                    className="w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:ml-3 sm:w-auto sm:text-sm"
                  >
                    Batal
                  </button>
                )}
                <button
                  onClick={saveContact}
                  className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-blue-600 text-base font-medium text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:ml-3 sm:w-auto sm:text-sm"
                >
                  {editingIndex !== null ? 'Update Kontak' : 'Tambah Kontak'}
                </button>
                <button
                  onClick={() => setIsModalOpen(false)}
                  className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
                >
                  Tutup
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ContactTab;
