import React, { useEffect, useState } from 'react';
import Layout from '../../components/Layout';
import api from '../../axios';

const MstTax = () => {
  const [taxes, setTaxes] = useState([]);
  const [coaList, setCoaList] = useState([]);
  const [form, setForm] = useState({
    id: null,
    nama: '',
    nilai: '',
    kode: '',
    keterangan: '',
    akun_pajak_penjualan: '',
    akun_pajak_pembelian: '',
  });
  const [isEditing, setIsEditing] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [taxRes, coaRes] = await Promise.all([
        api.get('/api/taxes'),
        api.get('/api/coa'),
      ]);
      setTaxes(taxRes.data);
      setCoaList(coaRes.data);
    } catch (err) {
      console.error('Gagal mengambil data:', err);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const getNomorAkun = (id) => {
    const coa = coaList.find((item) => item.id === parseInt(id));
    return coa ? coa.nomor_akun : '';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        await api.put(`/taxes/${form.id}`, form);
      } else {
        await api.post('/api/taxes', form);
      }
      fetchData();
      closeModal();
    } catch (error) {
      console.error('Gagal menyimpan data', error);
      alert('Terjadi kesalahan saat menyimpan data.');
    }
  };

  const handleEdit = (tax) => {
    setForm({ ...tax, id: tax.id });
    setIsEditing(true);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    try {
      await api.delete(`/taxes/${confirmDeleteId}`);
      fetchData();
      setConfirmDeleteId(null);
    } catch (err) {
      console.error('Gagal menghapus data', err);
      alert('Gagal menghapus data.');
    }
  };

  const openModal = () => {
    setForm({
      id: null,
      nama: '',
      nilai: '',
      kode: '',
      keterangan: '',
      akun_pajak_penjualan: '',
      akun_pajak_pembelian: '',
    });
    setIsEditing(false);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setForm({
      id: null,
      nama: '',
      nilai: '',
      kode: '',
      keterangan: '',
      akun_pajak_penjualan: '',
      akun_pajak_pembelian: '',
    });
    setIsEditing(false);
  };

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold">Master Data Pajak</h2>
          <button
            onClick={openModal}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            + Tambah
          </button>
        </div>

        <table className="w-full border">
          <thead className="bg-gray-100">
            <tr>
              <th className="border p-2">Nama</th>
              <th className="border p-2">Nilai (%)</th>
              <th className="border p-2">Kode</th>
              <th className="border p-2">Akun Penjualan</th>
              <th className="border p-2">Akun Pembelian</th>
              <th className="border p-2 text-center">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {taxes.length > 0 ? (
              taxes.map((tax) => (
                <tr key={tax.id}>
                  <td className="border p-2">{tax.nama}</td>
                  <td className="border p-2">{tax.nilai}</td>
                  <td className="border p-2">{tax.kode}</td>
                  <td className="border p-2">
                    {tax.akun_penjualan?.nama} ({tax.akun_penjualan?.nomor_akun}
                    )
                  </td>
                  <td className="border p-2">
                    {tax.akun_pembelian?.nama} ({tax.akun_pembelian?.nomor_akun}
                    )
                  </td>
                  <td className="border p-2 flex justify-center gap-2">
                    <button
                      onClick={() => handleEdit(tax)}
                      className="bg-blue-50 hover:bg-blue-100 text-blue-600 px-3 py-2 rounded"
                      title="Edit"
                    >
                      <i className="bi bi-pencil-fill"></i>
                    </button>
                    <button
                      onClick={() => handleDelete(tax.id)}
                      className="bg-red-50 hover:bg-red-100 text-red-600 px-3 py-2 rounded"
                      title="Hapus"
                    >
                      <i className="bi bi-trash-fill"></i>
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="text-center p-4">
                  Tidak ada data.
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Modal Tambah/Edit */}
        {showModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/30 backdrop-blur-sm">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-md">
              <h2 className="text-lg font-semibold mb-4">
                {isEditing ? 'Edit Pajak' : 'Tambah Pajak'}
              </h2>
              <form onSubmit={handleSubmit}>
                <div className="mb-4">
                  <label className="block mb-1">Nama</label>
                  <input
                    type="text"
                    name="nama"
                    value={form.nama}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block mb-1">Nilai (%)</label>
                  <input
                    type="number"
                    name="nilai"
                    value={form.nilai}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block mb-1">Kode</label>
                  <input
                    type="text"
                    name="kode"
                    value={form.kode}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block mb-1">Keterangan</label>
                  <textarea
                    name="keterangan"
                    value={form.keterangan}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                  />
                </div>
                <div className="mb-4">
                  <label className="block mb-1">Akun Pajak Penjualan</label>
                  <select
                    name="akun_pajak_penjualan"
                    value={form.akun_pajak_penjualan}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  >
                    <option value="">-- Pilih Akun --</option>
                    {coaList.map((coa) => (
                      <option key={coa.id} value={coa.id}>
                        {coa.nama}
                      </option>
                    ))}
                  </select>
                  <input
                    type="text"
                    readOnly
                    value={getNomorAkun(form.akun_pajak_penjualan)}
                    placeholder="Nomor Akun"
                    className="mt-2 w-full border px-3 py-2 rounded bg-gray-100"
                  />
                </div>
                <div className="mb-4">
                  <label className="block mb-1">Akun Pajak Pembelian</label>
                  <select
                    name="akun_pajak_pembelian"
                    value={form.akun_pajak_pembelian}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  >
                    <option value="">-- Pilih Akun --</option>
                    {coaList.map((coa) => (
                      <option key={coa.id} value={coa.id}>
                        {coa.nama}
                      </option>
                    ))}
                  </select>
                  <input
                    type="text"
                    readOnly
                    value={getNomorAkun(form.akun_pajak_pembelian)}
                    placeholder="Nomor Akun"
                    className="mt-2 w-full border px-3 py-2 rounded bg-gray-100"
                  />
                </div>
                <div className="flex justify-end gap-2">
                  <button
                    type="button"
                    onClick={closeModal}
                    className="bg-gray-300 px-4 py-2 rounded"
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="bg-blue-600 text-white px-4 py-2 rounded"
                  >
                    {isEditing ? 'Perbarui' : 'Simpan'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Modal Konfirmasi Hapus */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/30 backdrop-blur-sm">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-sm">
              <h2 className="text-lg font-semibold mb-4 text-red-600 flex items-center">
                <i className="bi bi-exclamation-triangle-fill mr-2 text-xl" />{' '}
                Konfirmasi Hapus
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Apakah Anda yakin ingin menghapus data pajak ini?
              </p>
              <div className="flex justify-end gap-3">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 rounded text-gray-600 hover:text-black"
                >
                  Batal
                </button>
                <button
                  onClick={confirmDelete}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
                >
                  Hapus
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default MstTax;
