import React, { useEffect, useState } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

export default function MstTax() {
  const [form, setForm] = useState({
    nama: '',
    nilai: '',
    kode: '',
    keterangan: '',
    akun_pajak_penjualan: '',
    akun_pajak_pembelian: '',
  });

  const [taxes, setTaxes] = useState([]);
  const [coaList, setCoaList] = useState([]);
  const [editingId, setEditingId] = useState(null);

  const fetchData = async () => {
    const [taxRes, coaRes] = await Promise.all([
      api.get('/api/taxes'),
      api.get('/api/coa'),
    ]);
    setTaxes(taxRes.data);
    setCoaList(coaRes.data);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const getNomorAkun = (id) => {
    const coa = coaList.find((item) => item.id === parseInt(id));
    return coa ? coa.nomor_akun : '';
  };

  const resetForm = () => {
    setForm({
      nama: '',
      nilai: '',
      kode: '',
      keterangan: '',
      akun_pajak_penjualan: '',
      akun_pajak_pembelian: '',
    });
    setEditingId(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await api.put(`/taxes/${editingId}`, form);
      } else {
        await api.post('/api/taxes', form);
      }
      resetForm();
      fetchData();
    } catch (error) {
      console.error('Gagal menyimpan data', error);
      alert('Terjadi kesalahan saat menyimpan data.');
    }
  };

  const handleEdit = (tax) => {
    setForm({
      nama: tax.nama,
      nilai: tax.nilai,
      kode: tax.kode,
      keterangan: tax.keterangan,
      akun_pajak_penjualan: tax.akun_pajak_penjualan,
      akun_pajak_pembelian: tax.akun_pajak_pembelian,
    });
    setEditingId(tax.id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Yakin ingin menghapus data ini?')) {
      await api.delete(`/taxes/${id}`);
      fetchData();
    }
  };

  return (
    <Layout>
      <div className="p-4">
        <h2 className="text-xl font-semibold mb-4">Master Data Pajak</h2>

        <form
          onSubmit={handleSubmit}
          className="grid grid-cols-1 md:grid-cols-2 gap-4 bg-white p-4 rounded shadow mb-8"
        >
          <div>
            <label className="block mb-1 font-medium">Nama</label>
            <input
              type="text"
              name="nama"
              value={form.nama}
              onChange={handleChange}
              required
              className="w-full border px-3 py-2 rounded"
            />
          </div>

          <div>
            <label className="block mb-1 font-medium">Nilai (%)</label>
            <input
              type="number"
              name="nilai"
              value={form.nilai}
              onChange={handleChange}
              required
              className="w-full border px-3 py-2 rounded"
            />
          </div>

          <div>
            <label className="block mb-1 font-medium">Kode</label>
            <input
              type="text"
              name="kode"
              value={form.kode}
              onChange={handleChange}
              required
              className="w-full border px-3 py-2 rounded"
            />
          </div>

          <div>
            <label className="block mb-1 font-medium">Keterangan</label>
            <textarea
              name="keterangan"
              value={form.keterangan}
              onChange={handleChange}
              className="w-full border px-3 py-2 rounded"
            />
          </div>

          <div>
            <label className="block mb-1 font-medium">
              Akun Pajak Penjualan
            </label>
            <select
              name="akun_pajak_penjualan"
              value={form.akun_pajak_penjualan}
              onChange={handleChange}
              required
              className="w-full border px-3 py-2 rounded"
            >
              <option value="">-- Pilih Akun --</option>
              {coaList.map((coa) => (
                <option key={coa.id} value={coa.id}>
                  {coa.nama}
                </option>
              ))}
            </select>
            <input
              type="text"
              readOnly
              value={getNomorAkun(form.akun_pajak_penjualan)}
              className="w-full border mt-2 px-3 py-2 rounded bg-gray-100 text-gray-600"
              placeholder="Nomor Akun"
            />
          </div>

          <div>
            <label className="block mb-1 font-medium">
              Akun Pajak Pembelian
            </label>
            <select
              name="akun_pajak_pembelian"
              value={form.akun_pajak_pembelian}
              onChange={handleChange}
              required
              className="w-full border px-3 py-2 rounded"
            >
              <option value="">-- Pilih Akun --</option>
              {coaList.map((coa) => (
                <option key={coa.id} value={coa.id}>
                  {coa.nama}
                </option>
              ))}
            </select>
            <input
              type="text"
              readOnly
              value={getNomorAkun(form.akun_pajak_pembelian)}
              className="w-full border mt-2 px-3 py-2 rounded bg-gray-100 text-gray-600"
              placeholder="Nomor Akun"
            />
          </div>

          <div className="col-span-full flex gap-2 mt-2">
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded"
            >
              {editingId ? 'Update' : 'Simpan'}
            </button>
            {editingId && (
              <button
                type="button"
                onClick={resetForm}
                className="bg-gray-300 px-4 py-2 rounded"
              >
                Batal
              </button>
            )}
          </div>
        </form>

        <div className="overflow-auto">
          <table className="min-w-full bg-white border border-gray-200 rounded shadow">
            <thead className="bg-gray-100 text-left">
              <tr>
                <th className="px-4 py-2 border">Nama</th>
                <th className="px-4 py-2 border">Nilai (%)</th>
                <th className="px-4 py-2 border">Kode</th>
                <th className="px-4 py-2 border">Akun Penjualan</th>
                <th className="px-4 py-2 border">Akun Pembelian</th>
                <th className="px-4 py-2 border text-center">Aksi</th>
              </tr>
            </thead>
            <tbody>
              {taxes.map((tax) => (
                <tr key={tax.id} className="hover:bg-gray-50">
                  <td className="px-4 py-2 border">{tax.nama}</td>
                  <td className="px-4 py-2 border">{tax.nilai}</td>
                  <td className="px-4 py-2 border">{tax.kode}</td>
                  <td className="px-4 py-2 border">
                    {tax.akun_penjualan?.nama} ({tax.akun_penjualan?.nomor_akun}
                    )
                  </td>
                  <td className="px-4 py-2 border">
                    {tax.akun_pembelian?.nama} ({tax.akun_pembelian?.nomor_akun}
                    )
                  </td>
                  <td className="px-4 py-2 border text-center space-x-2">
                    <button
                      onClick={() => handleEdit(tax)}
                      className="bg-yellow-100 text-yellow-800 px-3 py-1 rounded hover:bg-yellow-200"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(tax.id)}
                      className="bg-red-100 text-red-800 px-3 py-1 rounded hover:bg-red-200"
                    >
                      Hapus
                    </button>
                  </td>
                </tr>
              ))}
              {taxes.length === 0 && (
                <tr>
                  <td
                    colSpan="6"
                    className="text-center px-4 py-6 text-gray-500"
                  >
                    Tidak ada data pajak.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </Layout>
  );
}
