import React, { useState, useEffect } from 'react';
import Layout from '../../components/Layout';
import api from '../../axios';

const CustomerTypePage = () => {
  const [types, setTypes] = useState([]);
  const [name, setName] = useState('');
  const [editingId, setEditingId] = useState(null);

  const fetchData = async () => {
    try {
      const response = await api.get('/api/customer-types');
      setTypes(response.data);
    } catch (err) {
      alert('Gagal memuat data');
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await api.put(`/api/customer-types/${editingId}`, { name });
      } else {
        await api.post('/api/customer-types', { name });
      }
      setName('');
      setEditingId(null);
      fetchData();
    } catch (err) {
      alert('Terjadi kesalahan saat menyimpan data.');
    }
  };

  const handleEdit = (type) => {
    setName(type.name);
    setEditingId(type.id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Yakin ingin menghapus?')) {
      try {
        await api.delete(`/api/customer-types/${id}`);
        fetchData();
      } catch (err) {
        alert('Gagal menghapus data.');
      }
    }
  };

  return (
    <Layout>
      <div className="p-6">
        <h1 className="text-2xl font-bold mb-4">Tipe Pelanggan</h1>
        <form onSubmit={handleSubmit} className="mb-4 flex gap-2">
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Nama Tipe"
            className="border px-3 py-2 rounded w-64"
            required
          />
          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded"
          >
            {editingId ? 'Update' : 'Tambah'}
          </button>
        </form>

        <table className="w-full border border-gray-200">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2 text-left">ID</th>
              <th className="border p-2 text-left">Nama</th>
              <th className="border p-2 text-left">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {types.map((type) => (
              <tr key={type.id}>
                <td className="border p-2">{type.id}</td>
                <td className="border p-2">{type.name}</td>
                <td className="border p-2 space-x-2">
                  <button
                    onClick={() => handleEdit(type)}
                    className="bg-yellow-500 px-3 py-1 text-white rounded"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(type.id)}
                    className="bg-red-600 px-3 py-1 text-white rounded"
                  >
                    Hapus
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </Layout>
  );
};

export default CustomerTypePage;
