import React, { useState, useEffect } from 'react';
// import Layout from '../../components/Layout';
import AddressTab from './tabs/AddressTab';
import TermTab from './tabs/TermTab';
import SalesTab from './tabs/SalesTab';
import ContactTab from './tabs/ContactTab';
import ShipToTab from './tabs/ShipToTab';
import CatatanTab from './tabs/CatatanTab'; 
import api from '../../axios';

const CustomerForm = ({ id, onSuccess }) => {
  const [form, setForm] = useState({
    customer_number: '',
    is_active: true,
    name: '',
    address: '',
    city: '',
    province: '',
    postal_code: '',
    country: '',
    phone: '',
    fax: '',
    contact_person: '',
    email: '',
    website: '',
    payment_term_id: '',
    credit_limit: '',
    max_invoice_amount: '',
    currency_id: '',
    opening_balance: 0,
    balance_date: '',
    payment_message: '',
    tax1_id: '',
    tax2_id: '',
    tax_number: '',
    tax_code: '',
    tax_address1: '',
    tax_address2: '',
    customer_type_id: '',
    price_level: 1,
    default_sales_discount: 0,
    notes: '',
    customer_contacts: [],
    ship_to_addresses: [],
  });

  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('alamat');

  useEffect(() => {
    if (id) {
      api.get('/sanctum/csrf-cookie').then(() => {
        api
          .get(`/customers/${id}`)
          .then((res) => {
            const data = res.data;

            setForm((prevForm) => ({
              ...prevForm,
              id: data.id,
              customer_number: data.customer_number,
              is_active: data.is_active,
              name: data.name,
              address: data.address,
              city: data.city,
              province: data.province,
              postal_code: data.postal_code,
              country: data.country,
              phone: data.phone,
              fax: data.fax,
              contact_person: data.contact_person,
              email: data.email,
              website: data.website,

              payment_term_id:
                data.payment_term_id || data.paymentTerm?.id || '',
              currency_id: data.currency_id || data.currency?.id || '',
              tax1_id: data.tax1_id || data.tax1?.id || '',
              tax2_id: data.tax2_id || data.tax2?.id || '',
              customer_type_id:
                data.customer_type_id || data.customerType?.id || '',

              credit_limit: data.credit_limit ?? '',
              max_invoice_amount: data.max_invoice_amount ?? '',
              opening_balance: data.opening_balance ?? '',
              balance_date: data.balance_date ?? '',
              default_sales_discount: data.default_sales_discount ?? '',
              payment_message: data.payment_message ?? '',
              tax_number: data.tax_number ?? '',
              tax_code: data.tax_code ?? '',
              tax_address1: data.tax_address1 ?? '',
              tax_address2: data.tax_address2 ?? '',
              price_level: data.price_level ?? 1,
              notes: data.notes ?? '',

              customer_contacts: data.contacts || [],
              ship_to_addresses: data.shipAddresses || [],
            }));
          })
          .catch((err) => {
            console.error('Gagal load customer', err);
          });
      });
    }
  }, [id]); // âœ… Sekarang aman, tidak perlu menambahkan `form` ke dependencies

  const handleSubmit = async () => {
    setLoading(true);
    try {
      const payload = {
        ...form,
        credit_limit: parseFloat(form.credit_limit) || 0,
        max_invoice_amount: parseFloat(form.max_invoice_amount) || 0,
        opening_balance: parseFloat(form.opening_balance) || 0,
        default_sales_discount: parseFloat(form.default_sales_discount) || 0,
      };

      if (id) {
        await api.put(`/api/customers/${id}`, payload);
      } else {
        await api.post('/api/customers', payload);
      }
      alert('Data berhasil disimpan!');
      if (onSuccess) onSuccess();
    } catch (err) {
      if (err.response && err.response.status === 422) {
        console.error('Validation Errors:', err.response.data.errors);
      } else {
        console.error('Unexpected Error:', err);
      }
      alert('Terjadi kesalahan saat menyimpan.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white w-full max-w-4xl rounded shadow-lg p-6 overflow-y-auto max-h-[90vh]">
        {/* Tabs Navigasi */}
        <div className="tabs mb-4">
          {['alamat', 'termin', 'penjualan', 'kontak', 'shipto', 'catatan'].map(
            (tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`mr-2 px-4 py-2 rounded ${
                  activeTab === tab ? 'bg-blue-600 text-white' : 'bg-gray-200'
                }`}
              >
                {tab.charAt(0).toUpperCase() + tab.slice(1)}
              </button>
            )
          )}
        </div>

        {/* Isi Tab */}
        <div className="p-4 border bg-white rounded shadow">
          {activeTab === 'alamat' && (
            <AddressTab form={form} setForm={setForm} />
          )}
          {activeTab === 'termin' && <TermTab form={form} setForm={setForm} />}
          {activeTab === 'penjualan' && (
            <SalesTab form={form} setForm={setForm} />
          )}
          {activeTab === 'kontak' && (
            <ContactTab form={form} setForm={setForm} />
          )}
          {activeTab === 'shipto' && (
            <ShipToTab form={form} setForm={setForm} />
          )}
          {activeTab === 'catatan' && (
            <CatatanTab form={form} setForm={setForm} />
          )}
        </div>

        {/* Tombol Simpan */}
        <div className="mt-4 flex justify-end gap-2">
          <button
            onClick={onSuccess}
            className="bg-gray-400 text-white px-6 py-2 rounded"
          >
            Tutup
          </button>
          <button
            onClick={handleSubmit}
            disabled={loading}
            className="bg-blue-600 text-white px-6 py-2 rounded"
          >
            {loading ? 'Menyimpan...' : 'Simpan'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default CustomerForm;
