import React, { useEffect, useState, useCallback } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const ChartOfAccounts = () => {
  const today = new Date().toISOString().split('T')[0];
  const [tipeAkuns, setTipeAkuns] = useState([]);
  const [currencies, setCurrencies] = useState([]);
  const [chartAccounts, setChartAccounts] = useState([]);
  const [filterTipe, setFilterTipe] = useState('');
  const [search, setSearch] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [sortBy, setSortBy] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc');
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);
  const [selectedTipeAkun, setSelectedTipeAkun] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const [formData, setFormData] = useState({
    nama: '',
    tipe_akun_id: '',
    nomor_akun: '',
    sub_account_of: null,
    saldo_awal: '',
    currency_id: '',
    kalender: today,
    isSub: false,
  });

  const fetchAccounts = useCallback(async () => {
    try {
      const res = await api.get('/api/chart-of-accounts', {
        params: { tipe: filterTipe, search },
      });
      setChartAccounts(res.data);
    } catch (error) {
      console.error('Gagal memuat akun:', error);
      alert('Gagal memuat data akun');
    }
  }, [filterTipe, search]);

  useEffect(() => {
    const fetchInitialData = async () => {
      setIsLoading(true);
      try {
        const [tipeRes, currencyRes] = await Promise.all([
          api.get('/api/tipe-akun'),
          api.get('/api/currencies'),
        ]);
        setTipeAkuns(tipeRes.data);
        setCurrencies(currencyRes.data);
        await fetchAccounts();
      } catch (error) {
        console.error('Gagal memuat data awal:', error);
        alert('Gagal memuat data awal');
      } finally {
        setIsLoading(false);
      }
    };
    fetchInitialData();
  }, [fetchAccounts]);

  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      fetchAccounts();
    }, 500);
    return () => clearTimeout(delayDebounce);
  }, [search, filterTipe, fetchAccounts]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    let updatedForm = {
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    };

    if (name === 'tipe_akun_id') {
      const tipe = tipeAkuns.find((t) => t.id === parseInt(value));
      setSelectedTipeAkun(tipe);

      if (!updatedForm.isSub) {
        updatedForm.nomor_akun = generateNomorAkun(value, false);
      }

      if (!tipe?.is_currency_required) {
        updatedForm.currency_id = '';
      }

      if (!tipe?.is_opening_balance_required) {
        updatedForm.saldo_awal = '';
      }
    }

    if (name === 'isSub') {
      updatedForm.sub_account_of = null;
      updatedForm.nomor_akun = generateNomorAkun(
        updatedForm.tipe_akun_id,
        checked,
        checked ? updatedForm.sub_account_of : null
      );
    }

    if (name === 'sub_account_of') {
      updatedForm.nomor_akun = generateNomorAkun(
        formData.tipe_akun_id,
        true,
        value
      );
    }

    setFormData(updatedForm);
    if (errors[name]) {
      setErrors((prev) => ({ ...prev, [name]: null }));
    }
  };

  const generateNomorAkun = (tipeId, isSub = false, parentId = null) => {
    if (!tipeId) return '';

    const tipe = tipeAkuns.find((t) => t.id === parseInt(tipeId));
    if (!tipe) return '';

    if (isSub && parentId) {
      const parent = chartAccounts.find((a) => a.id === parseInt(parentId));
      if (!parent) return '';

      const [seg1, seg2] = parent.nomor_akun.split('-');
      const subAccounts = chartAccounts.filter(
        (a) => a.sub_account_of === parent.id
      );

      const lastSubNumber =
        subAccounts.length > 0
          ? Math.max(
              ...subAccounts.map((a) => parseInt(a.nomor_akun.split('-')[2]))
            )
          : parseInt(parent.nomor_akun.split('-')[2]);

      return `${seg1}-${seg2}-${(lastSubNumber + 1)
        .toString()
        .padStart(3, '0')}`;
    } else {
      const kode = tipe.kode || '1-1';
      const [seg1, seg2] = kode.split('-');

      const mainAccounts = chartAccounts.filter(
        (a) => a.tipe_akun_id === parseInt(tipeId) && !a.sub_account_of
      );

      const lastMainNumber =
        mainAccounts.length > 0
          ? Math.max(
              ...mainAccounts.map((a) => parseInt(a.nomor_akun.split('-')[2]))
            )
          : 0;

      return `${seg1}-${seg2}-${(lastMainNumber + 1)
        .toString()
        .padStart(3, '0')}`;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrors({});

    // Client-side validation
    const newErrors = {};
    if (!formData.nama.trim()) newErrors.nama = 'Nama akun wajib diisi';
    if (!formData.tipe_akun_id)
      newErrors.tipe_akun_id = 'Tipe akun wajib dipilih';
    if (!formData.nomor_akun) newErrors.nomor_akun = 'Nomor akun wajib diisi';
    if (formData.isSub && !formData.sub_account_of) {
      newErrors.sub_account_of = 'Akun induk wajib dipilih untuk sub akun';
    }
    if (selectedTipeAkun?.is_opening_balance_required && !formData.saldo_awal) {
      newErrors.saldo_awal = 'Saldo awal wajib diisi untuk tipe akun ini';
    }
    if (selectedTipeAkun?.is_currency_required && !formData.currency_id) {
      newErrors.currency_id = 'Mata uang wajib dipilih untuk tipe akun ini';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      setIsLoading(false);
      return;
    }

    // Prepare payload
    const payload = {
      nama: formData.nama,
      tipe_akun_id: formData.tipe_akun_id,
      nomor_akun: formData.nomor_akun,
      sub_account_of: formData.isSub ? formData.sub_account_of : null,
      saldo_awal: formData.saldo_awal ? parseFloat(formData.saldo_awal) : 0,
      currency_id: selectedTipeAkun?.is_currency_required
        ? formData.currency_id
        : null,
      kalender: formData.kalender,
    };

    try {
      let response;
      if (editingId) {
        response = await api.put(
          `/api/chart-of-accounts/${editingId}`,
          payload
        );
      } else {
        response = await api.post('/api/chart-of-accounts', payload);
      }

      // Reset form
      setFormData({
        nama: '',
        tipe_akun_id: '',
        nomor_akun: '',
        sub_account_of: null,
        saldo_awal: '',
        currency_id: '',
        kalender: today,
        isSub: false,
      });
      setEditingId(null);
      setShowModal(false);
      setSelectedTipeAkun(null);

      await fetchAccounts();
      alert(editingId ? 'Akun berhasil diperbarui' : 'Akun berhasil dibuat');
    } catch (err) {
      console.error('Gagal menyimpan akun:', err);

      if (err.response?.data?.errors) {
        setErrors(err.response.data.errors);
      } else {
        alert(err.response?.data?.message || 'Gagal menyimpan akun');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleEdit = (account) => {
    const tipe = tipeAkuns.find((t) => t.id === account.tipe_akun_id);
    setSelectedTipeAkun(tipe);

    setFormData({
      nama: account.nama,
      tipe_akun_id: account.tipe_akun_id,
      nomor_akun: account.nomor_akun,
      sub_account_of: account.sub_account_of || null,
      saldo_awal: account.saldo_awal?.toString() || '',
      currency_id: account.currency_id || '',
      kalender: account.kalender,
      isSub: !!account.sub_account_of,
    });
    setEditingId(account.id);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    setIsLoading(true);
    try {
      await api.delete(`/api/chart-of-accounts/${confirmDeleteId}`);
      setConfirmDeleteId(null);
      await fetchAccounts();
      alert('Akun berhasil dihapus');
    } catch (err) {
      console.error('Gagal menghapus akun:', err);
      alert(err.response?.data?.message || 'Gagal menghapus akun');
    } finally {
      setIsLoading(false);
    }
  };

  const buildAccountTree = (accounts, parentId = null, level = 0) => {
    return accounts
      .filter((account) => account.sub_account_of === parentId)
      .flatMap((account) => [
        { ...account, level },
        ...buildAccountTree(accounts, account.id, level + 1),
      ]);
  };

  const filteredAccounts = chartAccounts.filter((account) => {
    const matchesType = filterTipe ? account.tipe_akun_id == filterTipe : true;
    const matchesSearch = search
      ? account.nama.toLowerCase().includes(search.toLowerCase()) ||
        account.nomor_akun.toLowerCase().includes(search.toLowerCase())
      : true;
    return matchesType && matchesSearch;
  });

  const sortedAccounts = [...buildAccountTree(filteredAccounts)].sort(
    (a, b) => {
      if (!sortBy) return 0;

      const aValue = sortBy === 'tipe' ? a.tipe_akun?.nama : a[sortBy];
      const bValue = sortBy === 'tipe' ? b.tipe_akun?.nama : b[sortBy];

      if (typeof aValue === 'string') {
        return sortOrder === 'asc'
          ? aValue.localeCompare(bValue)
          : bValue.localeCompare(aValue);
      }
      return sortOrder === 'asc' ? aValue - bValue : bValue - aValue;
    }
  );

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Daftar Akun</h2>
          <button
            onClick={() => {
              setEditingId(null);
              setSelectedTipeAkun(null);
              setFormData({
                nama: '',
                tipe_akun_id: '',
                nomor_akun: '',
                sub_account_of: null,
                saldo_awal: '',
                currency_id: '',
                kalender: today,
                isSub: false,
              });
              setShowModal(true);
            }}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:bg-blue-300"
            disabled={isLoading}
          >
            {isLoading ? 'Memuat...' : '+ Tambah Baru'}
          </button>
        </div>

        <div className="mb-4 flex flex-col md:flex-row gap-2">
          <select
            onChange={(e) => setFilterTipe(e.target.value)}
            className="border p-2 rounded"
            value={filterTipe}
            disabled={isLoading}
          >
            <option value="">Semua Tipe</option>
            {tipeAkuns.map((tipe) => (
              <option key={tipe.id} value={tipe.id}>
                {tipe.nama}
              </option>
            ))}
          </select>
          <input
            placeholder="Cari berdasarkan nama atau nomor akun"
            onChange={(e) => setSearch(e.target.value)}
            className="border p-2 rounded flex-grow"
            disabled={isLoading}
          />
        </div>

        <div className="overflow-x-auto">
          <table className="w-full border">
            <thead>
              <tr className="bg-gray-100">
                <th
                  className="border p-2 cursor-pointer"
                  onClick={() => handleSort('nomor_akun')}
                >
                  Nomor Akun{' '}
                  {sortBy === 'nomor_akun' && (sortOrder === 'asc' ? '↑' : '↓')}
                </th>
                <th className="border p-2">Nama Akun</th>
                <th
                  className="border p-2 cursor-pointer"
                  onClick={() => handleSort('tipe')}
                >
                  Tipe Akun{' '}
                  {sortBy === 'tipe' && (sortOrder === 'asc' ? '↑' : '↓')}
                </th>
                <th className="border p-2">Saldo Awal</th>
                <th className="border p-2">Mata Uang</th>
                <th className="border p-2 text-center">Aksi</th>
              </tr>
            </thead>
            <tbody>
              {isLoading && sortedAccounts.length === 0 ? (
                <tr>
                  <td colSpan="6" className="text-center py-4">
                    Memuat data...
                  </td>
                </tr>
              ) : sortedAccounts.length > 0 ? (
                sortedAccounts.map((account) => (
                  <tr key={account.id} className="hover:bg-gray-50">
                    <td className="border p-2">
                      {' '.repeat(account.level)}
                      {account.nomor_akun}
                    </td>
                    <td className="border p-2">
                      {' '.repeat(account.level)}
                      {account.nama}
                    </td>
                    <td className="border p-2">{account.tipe_akun?.nama}</td>
                    <td className="border p-2 text-right">
                      {parseFloat(account.saldo_awal || 0).toLocaleString(
                        'id-ID'
                      )}
                    </td>
                    <td className="border p-2 text-center">
                      {account.currency?.symbol || '-'}
                    </td>
                    <td className="border p-2 text-center space-x-2">
                      <button
                        onClick={() => handleEdit(account)}
                        className="bg-blue-50 hover:bg-blue-100 text-blue-600 hover:text-blue-800 px-3 py-2 rounded-md"
                        disabled={isLoading}
                      >
                        <i className="bi bi-pencil-fill"></i>
                      </button>
                      <button
                        onClick={() => handleDelete(account.id)}
                        className="bg-red-50 hover:bg-red-100 text-red-600 hover:text-red-800 px-3 py-2 rounded-md"
                        disabled={isLoading}
                      >
                        <i className="bi bi-trash-fill"></i>
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="text-center text-gray-500 py-4">
                    Tidak ada data ditemukan
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        {/* Modal Form */}
        {showModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
            <div className="bg-white p-6 rounded w-full max-w-lg relative shadow-lg mx-4">
              <button
                className="absolute top-2 right-2 text-gray-500 hover:text-black"
                onClick={() => {
                  setShowModal(false);
                  setEditingId(null);
                  setSelectedTipeAkun(null);
                }}
                disabled={isLoading}
              >
                ✕
              </button>
              <h3 className="text-lg font-semibold mb-4">
                {editingId ? 'Edit Akun' : 'Buat Akun Baru'}
              </h3>
              <form onSubmit={handleSubmit} className="space-y-3">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Nama Akun <span className="text-red-500">*</span>
                  </label>
                  <input
                    name="nama"
                    value={formData.nama}
                    onChange={handleChange}
                    className={`border p-2 w-full rounded ${
                      errors.nama ? 'border-red-500' : ''
                    }`}
                    disabled={isLoading}
                  />
                  {errors.nama && (
                    <p className="text-red-500 text-sm mt-1">{errors.nama}</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Tipe Akun <span className="text-red-500">*</span>
                  </label>
                  <select
                    name="tipe_akun_id"
                    value={formData.tipe_akun_id}
                    onChange={handleChange}
                    className={`border p-2 w-full rounded ${
                      errors.tipe_akun_id ? 'border-red-500' : ''
                    }`}
                    disabled={isLoading}
                  >
                    <option value="">-- Pilih Tipe Akun --</option>
                    {tipeAkuns.map((tipe) => (
                      <option key={tipe.id} value={tipe.id}>
                        {tipe.nama}
                      </option>
                    ))}
                  </select>
                  {errors.tipe_akun_id && (
                    <p className="text-red-500 text-sm mt-1">
                      {errors.tipe_akun_id}
                    </p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Nomor Akun <span className="text-red-500">*</span>
                  </label>
                  <input
                    name="nomor_akun"
                    value={formData.nomor_akun}
                    onChange={handleChange}
                    className={`border p-2 w-full rounded ${
                      errors.nomor_akun ? 'border-red-500' : ''
                    }`}
                    disabled={isLoading}
                  />
                  {errors.nomor_akun && (
                    <p className="text-red-500 text-sm mt-1">
                      {errors.nomor_akun}
                    </p>
                  )}
                </div>

                <div className="flex items-center gap-2">
                  <input
                    type="checkbox"
                    name="isSub"
                    checked={formData.isSub}
                    onChange={handleChange}
                    className="h-4 w-4"
                    disabled={isLoading}
                  />
                  <label className="text-sm font-medium text-gray-700">
                    Sub Akun dari
                  </label>
                </div>

                {formData.isSub && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Akun Induk <span className="text-red-500">*</span>
                    </label>
                    <select
                      name="sub_account_of"
                      value={formData.sub_account_of || ''}
                      onChange={handleChange}
                      className={`border p-2 w-full rounded ${
                        errors.sub_account_of ? 'border-red-500' : ''
                      }`}
                      disabled={isLoading || !formData.tipe_akun_id}
                    >
                      <option value="">-- Pilih Akun Induk --</option>
                      {chartAccounts
                        .filter(
                          (a) =>
                            a.tipe_akun_id == formData.tipe_akun_id &&
                            !a.sub_account_of
                        )
                        .map((a) => (
                          <option key={a.id} value={a.id}>
                            {a.nomor_akun} - {a.nama}
                          </option>
                        ))}
                    </select>
                    {errors.sub_account_of && (
                      <p className="text-red-500 text-sm mt-1">
                        {errors.sub_account_of}
                      </p>
                    )}
                  </div>
                )}

                {selectedTipeAkun?.is_opening_balance_required && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Saldo Awal <span className="text-red-500">*</span>
                    </label>
                    <input
                      type="number"
                      name="saldo_awal"
                      value={formData.saldo_awal}
                      onChange={handleChange}
                      className={`border p-2 w-full rounded ${
                        errors.saldo_awal ? 'border-red-500' : ''
                      }`}
                      disabled={isLoading}
                      step="0.01"
                    />
                    {errors.saldo_awal && (
                      <p className="text-red-500 text-sm mt-1">
                        {errors.saldo_awal}
                      </p>
                    )}
                  </div>
                )}

                {selectedTipeAkun?.is_currency_required && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Mata Uang <span className="text-red-500">*</span>
                    </label>
                    <select
                      name="currency_id"
                      value={formData.currency_id}
                      onChange={handleChange}
                      className={`border p-2 w-full rounded ${
                        errors.currency_id ? 'border-red-500' : ''
                      }`}
                      disabled={isLoading}
                    >
                      <option value="">-- Pilih Mata Uang --</option>
                      {currencies.map((currency) => (
                        <option key={currency.id} value={currency.id}>
                          {currency.name} ({currency.symbol})
                        </option>
                      ))}
                    </select>
                    {errors.currency_id && (
                      <p className="text-red-500 text-sm mt-1">
                        {errors.currency_id}
                      </p>
                    )}
                  </div>
                )}

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Tanggal <span className="text-red-500">*</span>
                  </label>
                  <input
                    type="date"
                    name="kalender"
                    value={formData.kalender}
                    onChange={handleChange}
                    className={`border p-2 w-full rounded ${
                      errors.kalender ? 'border-red-500' : ''
                    }`}
                    disabled={isLoading}
                  />
                  {errors.kalender && (
                    <p className="text-red-500 text-sm mt-1">
                      {errors.kalender}
                    </p>
                  )}
                </div>

                <div className="flex justify-end gap-2 pt-4">
                  <button
                    type="button"
                    onClick={() => {
                      setShowModal(false);
                      setEditingId(null);
                      setSelectedTipeAkun(null);
                    }}
                    className="px-4 py-2 rounded border text-gray-600 hover:bg-gray-100 disabled:opacity-50"
                    disabled={isLoading}
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:bg-blue-300"
                    disabled={isLoading}
                  >
                    {isLoading ? 'Menyimpan...' : 'Simpan'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Delete Confirmation Modal */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-sm mx-4">
              <h2 className="text-lg font-semibold mb-4 text-red-600">
                Konfirmasi Hapus
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Apakah Anda yakin ingin menghapus akun ini? Aksi ini tidak dapat
                dibatalkan.
              </p>
              <div className="flex justify-end gap-3">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 rounded border text-gray-600 hover:bg-gray-100 disabled:opacity-50"
                  disabled={isLoading}
                >
                  Batal
                </button>
                <button
                  onClick={confirmDelete}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded disabled:bg-red-300"
                  disabled={isLoading}
                >
                  {isLoading ? 'Menghapus...' : 'Hapus'}
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default ChartOfAccounts;
