import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';

const ShipToTab = ({ form, setForm }) => {
  const [newAddress, setNewAddress] = useState(initialAddress());

  function initialAddress() {
    return {
      temp_id: uuidv4(),
      address1: '',
      address2: '',
      city: '',
      province: '',
      postal_code: '',
      country: '',
    };
  }

  const handleAdd = () => {
    if (!newAddress.address1) return;

    setForm((prev) => ({
      ...prev,
      ship_to_addresses: [...(prev.ship_to_addresses || []), newAddress],
    }));
    setNewAddress(initialAddress());
  };

  const handleDelete = (index) => {
    const updated = [...form.ship_to_addresses];
    updated.splice(index, 1);
    setForm((prev) => ({ ...prev, ship_to_addresses: updated }));
  };

  const handleChange = (index, field, value) => {
    const updated = [...form.ship_to_addresses];
    updated[index][field] = value;
    setForm((prev) => ({ ...prev, ship_to_addresses: updated }));
  };

  return (
    <div className="space-y-4">
      {/* Tabel data */}
      <table className="w-full table-auto border text-sm">
        <thead className="bg-gray-100">
          <tr>
            <th className="border px-2 py-1">Address 1</th>
            <th className="border px-2 py-1">Address 2</th>
            <th className="border px-2 py-1">City</th>
            <th className="border px-2 py-1">State/Prov</th>
            <th className="border px-2 py-1">Zip Code</th>
            <th className="border px-2 py-1">Country</th>
            <th className="border px-2 py-1">Aksi</th>
          </tr>
        </thead>
        <tbody>
          {form.ship_to_addresses?.map((address, i) => (
            <tr key={address.temp_id}>
              <td className="border px-2 py-1">
                <input
                  type="text"
                  value={address.address1}
                  onChange={(e) => handleChange(i, 'address1', e.target.value)}
                  className="w-full border-none outline-none"
                />
              </td>
              <td className="border px-2 py-1">
                <input
                  type="text"
                  value={address.address2}
                  onChange={(e) => handleChange(i, 'address2', e.target.value)}
                  className="w-full border-none outline-none"
                />
              </td>
              <td className="border px-2 py-1">
                <input
                  type="text"
                  value={address.city}
                  onChange={(e) => handleChange(i, 'city', e.target.value)}
                  className="w-full border-none outline-none"
                />
              </td>
              <td className="border px-2 py-1">
                <input
                  type="text"
                  value={address.province}
                  onChange={(e) => handleChange(i, 'province', e.target.value)}
                  className="w-full border-none outline-none"
                />
              </td>
              <td className="border px-2 py-1">
                <input
                  type="text"
                  value={address.postal_code}
                  onChange={(e) =>
                    handleChange(i, 'postal_code', e.target.value)
                  }
                  className="w-full border-none outline-none"
                />
              </td>
              <td className="border px-2 py-1">
                <input
                  type="text"
                  value={address.country}
                  onChange={(e) => handleChange(i, 'country', e.target.value)}
                  className="w-full border-none outline-none"
                />
              </td>
              <td className="border px-2 py-1 text-center">
                <button
                  onClick={() => handleDelete(i)}
                  className="text-red-500"
                >
                  Hapus
                </button>
              </td>
            </tr>
          ))}
          {(!form.ship_to_addresses || form.ship_to_addresses.length === 0) && (
            <tr>
              <td colSpan="7" className="text-center py-2 text-gray-500">
                Belum ada alamat pengiriman
              </td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Form Tambah Baris */}
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-2">
        <input
          type="text"
          placeholder="Address 1"
          value={newAddress.address1}
          onChange={(e) =>
            setNewAddress((prev) => ({ ...prev, address1: e.target.value }))
          }
          className="border px-2 py-1 rounded"
        />
        <input
          type="text"
          placeholder="Address 2"
          value={newAddress.address2}
          onChange={(e) =>
            setNewAddress((prev) => ({ ...prev, address2: e.target.value }))
          }
          className="border px-2 py-1 rounded"
        />
        <input
          type="text"
          placeholder="City"
          value={newAddress.city}
          onChange={(e) =>
            setNewAddress((prev) => ({ ...prev, city: e.target.value }))
          }
          className="border px-2 py-1 rounded"
        />
        <input
          type="text"
          placeholder="Province"
          value={newAddress.province}
          onChange={(e) =>
            setNewAddress((prev) => ({ ...prev, province: e.target.value }))
          }
          className="border px-2 py-1 rounded"
        />
        <input
          type="text"
          placeholder="Postal Code"
          value={newAddress.postal_code}
          onChange={(e) =>
            setNewAddress((prev) => ({ ...prev, postal_code: e.target.value }))
          }
          className="border px-2 py-1 rounded"
        />
        <input
          type="text"
          placeholder="Country"
          value={newAddress.country}
          onChange={(e) =>
            setNewAddress((prev) => ({ ...prev, country: e.target.value }))
          }
          className="border px-2 py-1 rounded"
        />
      </div>

      <div className="flex justify-end">
        <button
          onClick={handleAdd}
          className="mt-2 bg-blue-500 text-white px-4 py-1 rounded"
        >
          Tambah Alamat
        </button>
      </div>
    </div>
  );
};

export default ShipToTab;
