import { useEffect, useState } from 'react';
import api from '../../../axios';

const TerminTab = ({ formData, onChange }) => {
  const [paymentTerms, setPaymentTerms] = useState([]);
  const [selectedTerm, setSelectedTerm] = useState(null);
  const [currencies, setCurrencies] = useState([]);
  const [taxes, setTaxes] = useState([]);
  const [taxTypes, setTaxTypes] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    const term = paymentTerms.find(
      (t) => t.id === parseInt(formData.termin_id)
    );
    setSelectedTerm(term || null);
  }, [formData.termin_id, paymentTerms]);

  const fetchData = async () => {
    try {
      const [termRes, currRes, taxRes, typeRes] = await Promise.all([
        api.get('/api/payment_terms'),
        api.get('/api/currencies'),
        api.get('/api/mst_tax'),
        api.get('/api/type_taxes'),
      ]);
      setPaymentTerms(termRes.data);
      setCurrencies(currRes.data);
      setTaxes(taxRes.data);
      setTaxTypes(typeRes.data);
    } catch (err) {
      console.error('Fetch error:', err);
    }
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
      {/* Termin */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Termin
        </label>
        <select
          name="termin_id"
          value={formData.termin_id}
          onChange={onChange}
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        >
          <option value="">-- Pilih Termin --</option>
          {paymentTerms.map((term) => (
            <option key={term.id} value={term.id}>
              {term.name}
            </option>
          ))}
        </select>
      </div>

      {/* Mata Uang */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Mata Uang
        </label>
        <select
          name="currency_id"
          value={formData.currency_id}
          onChange={onChange}
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        >
          <option value="">-- Pilih Mata Uang --</option>
          {currencies.map((c) => (
            <option key={c.id} value={c.id}>
              {c.name}
            </option>
          ))}
        </select>
      </div>

      {/* Saldo Awal */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Saldo Awal
        </label>
        <input
          type="number"
          name="saldo_awal"
          value={formData.saldo_awal}
          onChange={onChange}
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        />
      </div>

      {/* Per Tanggal */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Per Tanggal
        </label>
        <input
          type="date"
          name="saldo_awal_tanggal"
          value={formData.saldo_awal_tanggal}
          onChange={onChange}
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        />
      </div>

      {/* Default Keterangan */}
      <div className="md:col-span-2">
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Default Keterangan
        </label>
        <textarea
          name="default_keterangan"
          rows="2"
          value={formData.default_keterangan}
          onChange={onChange}
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        ></textarea>
      </div>

      {/* Pajak 1 */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Pajak 1
        </label>
        <select
          name="pajak_1_id"
          value={formData.pajak_1_id}
          onChange={onChange}
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        >
          <option value="">-- Pilih Pajak --</option>
          {taxes.map((t) => (
            <option key={t.id} value={t.id}>
              {t.nama}
            </option>
          ))}
        </select>
      </div>

      {/* Pajak 2 */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Pajak 2
        </label>
        <select
          name="pajak_2_id"
          value={formData.pajak_2_id}
          onChange={onChange}
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        >
          <option value="">-- Pilih Pajak --</option>
          {taxes.map((t) => (
            <option key={t.id} value={t.id}>
              {t.nama}
            </option>
          ))}
        </select>
      </div>

      {/* NPWP */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          NPWP Pemasok
        </label>
        <input
          type="text"
          name="npwp"
          value={formData.npwp}
          onChange={onChange}
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        />
      </div>

      {/* Tax Type */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Jenis Pajak
        </label>
        <select
          name="tax_type_id"
          value={formData.tax_type_id}
          onChange={onChange}
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        >
          <option value="">-- Pilih Jenis Pajak --</option>
          {taxTypes.map((t) => (
            <option key={t.id} value={t.id}>
              {t.nama}
            </option>
          ))}
        </select>
      </div>

      {/* Pesan */}
      <div className="md:col-span-2">
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Pesan
        </label>
        <textarea
          name="pesan"
          rows="2"
          value={formData.pesan}
          onChange={onChange}
          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
        ></textarea>
      </div>

      {/* Info Termin */}
      {selectedTerm && (
        <div className="md:col-span-2 grid grid-cols-1 md:grid-cols-3 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Pay Within Days
            </label>
            <input
              type="text"
              value={selectedTerm.pay_within_days}
              readOnly
              className="w-full px-3 py-2 bg-gray-100 border border-gray-300 rounded-md shadow-sm"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Discount %
            </label>
            <input
              type="text"
              value={selectedTerm.discount_percent}
              readOnly
              className="w-full px-3 py-2 bg-gray-100 border border-gray-300 rounded-md shadow-sm"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Due Days
            </label>
            <input
              type="text"
              value={selectedTerm.due_days}
              readOnly
              className="w-full px-3 py-2 bg-gray-100 border border-gray-300 rounded-md shadow-sm"
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default TerminTab;
