import React, { useEffect, useState } from 'react';
import Layout from '../../components/Layout';
import api from '../../axios';

const MstTax = () => {
  const [taxes, setTaxes] = useState([]);
  const [form, setForm] = useState({
    id: null,
    nama: '',
    persen: '',
    akun_id: '',
  });
  const [showModal, setShowModal] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);
  const [akunOptions, setAkunOptions] = useState([]);

  useEffect(() => {
    fetchTaxes();
    fetchAkunOptions();
  }, []);

  const fetchTaxes = async () => {
    try {
      const response = await api.get('/api/pajak');
      setTaxes(response.data);
    } catch (err) {
      console.error('Gagal mengambil data pajak:', err);
    }
  };

  const fetchAkunOptions = async () => {
    try {
      const res = await api.get('/api/akun');
      setAkunOptions(res.data);
    } catch (err) {
      console.error('Gagal mengambil akun:', err);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      nama: form.nama,
      persen: form.persen,
      akun_id: form.akun_id,
    };

    try {
      if (isEditing) {
        await api.put(`/api/pajak/${form.id}`, payload);
      } else {
        await api.post('/api/pajak', payload);
      }

      fetchTaxes();
      closeModal();
    } catch (err) {
      console.error('Gagal menyimpan pajak:', err);
    }
  };

  const handleEdit = (item) => {
    setForm({
      id: item.id,
      nama: item.nama,
      persen: item.persen,
      akun_id: item.akun_id,
    });
    setIsEditing(true);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    try {
      await api.delete(`/api/pajak/${confirmDeleteId}`);
      fetchTaxes();
      setConfirmDeleteId(null);
    } catch (err) {
      console.error('Gagal menghapus pajak:', err);
    }
  };

  const openModal = () => {
    setForm({
      id: null,
      nama: '',
      persen: '',
      akun_id: '',
    });
    setIsEditing(false);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setForm({ id: null, nama: '', persen: '', akun_id: '' });
    setIsEditing(false);
  };

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold">Master Pajak</h2>
          <button
            onClick={openModal}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            + Pajak Baru
          </button>
        </div>

        <table className="w-full border">
          <thead className="bg-gray-100">
            <tr>
              <th className="border p-2">Nama Pajak</th>
              <th className="border p-2 text-center">Persen</th>
              <th className="border p-2">Akun</th>
              <th className="border p-2 text-center">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {taxes.length > 0 ? (
              taxes.map((tax) => (
                <tr key={tax.id}>
                  <td className="border p-2">{tax.nama}</td>
                  <td className="border p-2 text-center">{tax.persen}%</td>
                  <td className="border p-2">
                    {akunOptions.find((a) => a.id === tax.akun_id)?.nama || '-'}
                  </td>
                  <td className="border p-2 text-center">
                    <div className="flex justify-center gap-2">
                      <button
                        onClick={() => handleEdit(tax)}
                        className="bg-blue-50 hover:bg-blue-100 text-blue-600 hover:text-blue-800 px-3 py-2 rounded-md transition duration-200"
                        title="Edit"
                      >
                        <i className="bi bi-pencil-fill"></i>
                      </button>
                      <button
                        onClick={() => handleDelete(tax.id)}
                        className="bg-red-50 hover:bg-red-100 text-red-600 hover:text-red-800 px-3 py-2 rounded-md transition duration-200"
                        title="Hapus"
                      >
                        <i className="bi bi-trash-fill"></i>
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4" className="text-center p-4">
                  Tidak ada data pajak.
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Modal Form */}
        {showModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/30">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-md">
              <h2 className="text-lg font-semibold mb-4">
                {isEditing ? 'Edit Pajak' : 'Tambah Pajak'}
              </h2>
              <form onSubmit={handleSubmit}>
                <div className="mb-4">
                  <label className="block mb-1">Nama</label>
                  <input
                    type="text"
                    name="nama"
                    value={form.nama}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block mb-1">Persen</label>
                  <input
                    type="number"
                    name="persen"
                    value={form.persen}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block mb-1">Akun</label>
                  <select
                    name="akun_id"
                    value={form.akun_id}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  >
                    <option value="">-- Pilih Akun --</option>
                    {akunOptions.map((akun) => (
                      <option key={akun.id} value={akun.id}>
                        {akun.nama}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="flex justify-end gap-2">
                  <button
                    type="button"
                    onClick={closeModal}
                    className="bg-gray-300 px-4 py-2 rounded"
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="bg-blue-600 text-white px-4 py-2 rounded"
                  >
                    {isEditing ? 'Perbarui' : 'Simpan'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Modal Konfirmasi Hapus */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/30">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-sm">
              <h2 className="text-lg font-semibold mb-4 text-red-600 flex items-center">
                <i className="bi bi-exclamation-triangle-fill mr-2 text-xl" />
                Konfirmasi Hapus
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Apakah Anda yakin ingin menghapus pajak ini?
              </p>
              <div className="flex justify-end gap-3">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 rounded text-gray-600 hover:text-black"
                >
                  Batal
                </button>
                <button
                  onClick={confirmDelete}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
                >
                  Hapus
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default MstTax;
