import React, { useEffect, useState } from 'react';
import api from '../../axios';
import CustomerFormModal from './CustomerFormModal';
import Layout from '../../components/Layout'; // ✅ ditambahkan

const CustomerList = () => {
  const [customers, setCustomers] = useState([]);
  const [openModal, setOpenModal] = useState(false);
  const [editId, setEditId] = useState(null);

  const fetchCustomers = async () => {
    const res = await api.get('/api/customers');
    setCustomers(res.data);
  };

  useEffect(() => {
    fetchCustomers();
  }, []);

  return (
    <Layout title="Daftar Pelanggan">
      {' '}
      {/* ✅ dibungkus dengan Layout */}
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-xl font-bold">Daftar Pelanggan</h1>
        <button
          onClick={() => {
            setEditId(null);
            setOpenModal(true);
          }}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          + Tambah
        </button>
      </div>
      <table className="w-full table-auto border">
        <thead>
          <tr className="bg-gray-200">
            <th className="px-2 py-1">Nomor</th>
            <th className="px-2 py-1">Nama</th>
            <th className="px-2 py-1">Telepon</th>
            <th className="px-2 py-1">Kontak</th>
            <th className="px-2 py-1">Saldo</th>
            <th className="px-2 py-1">Mata Uang</th>
            <th className="px-2 py-1">Aksi</th>
          </tr>
        </thead>
        <tbody>
          {customers.map((cust) => (
            <tr key={cust.id} className="border-t">
              <td className="px-2 py-1">{cust.customer_number}</td>
              <td className="px-2 py-1">{cust.name}</td>
              <td className="px-2 py-1">{cust.phone}</td>
              <td className="px-2 py-1">{cust.contact_person}</td>
              <td className="px-2 py-1">{cust.opening_balance}</td>
              <td className="px-2 py-1">{cust.currency?.code || '-'}</td>
              <td className="px-2 py-1">
                <button
                  className="text-blue-600 mr-2"
                  onClick={() => {
                    setEditId(cust.id);
                    setOpenModal(true);
                  }}
                >
                  Edit
                </button>
                <button
                  className="text-red-600"
                  onClick={async () => {
                    if (window.confirm('Yakin hapus data?')) {
                      await api.delete(`/customers/${cust.id}`);
                      fetchCustomers();
                    }
                  }}
                >
                  Hapus
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* Modal */}
      <CustomerFormModal
        open={openModal}
        onClose={() => setOpenModal(false)}
        customerId={editId}
        onSuccess={() => {
          fetchCustomers();
          setOpenModal(false);
        }}
      />
    </Layout>
  );
};

export default CustomerList;
