import React, { useEffect, useState, useCallback } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const ChartOfAccounts = () => {
  const today = new Date().toISOString().split('T')[0];
  const [tipeAkuns, setTipeAkuns] = useState([]);
  const [currencies, setCurrencies] = useState([]);
  const [chartAccounts, setChartAccounts] = useState([]);
  const [subAccounts, setSubAccounts] = useState([]);
  const [filterTipe, setFilterTipe] = useState('');
  const [search, setSearch] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [sortBy, setSortBy] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc');
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);
  const [selectedTipeAkun, setSelectedTipeAkun] = useState(null);

  const [formData, setFormData] = useState({
    nama: '',
    tipe_akun_id: '',
    nomor_akun: '',
    sub_account_of: '',
    saldo_awal: '',
    currency_id: '',
    kalender: today,
    isSub: false,
  });

  const fetchAccounts = useCallback(async () => {
    try {
      const res = await api.get('/api/chart-of-accounts', {
        params: { tipe: filterTipe, search },
      });
      setChartAccounts(res.data);
      setSubAccounts(res.data);
    } catch (error) {
      console.error('Gagal memuat akun:', error.message);
    }
  }, [filterTipe, search]);

  // Fetch tipe akun sekali saat pertama render
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const [tipeRes, currencyRes] = await Promise.all([
          api.get('/api/tipe-akun'),
          api.get('/api/currencies'),
        ]);
        setTipeAkuns(tipeRes.data);
        setCurrencies(currencyRes.data);
      } catch (error) {
        console.error('Gagal memuat data awal:', error);
      }
    };
    fetchInitialData();
  }, []);


  // Jalankan fetchAccounts dengan delay saat search atau filterTipe berubah
  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      fetchAccounts();
    }, 500); // jeda 500ms

    return () => clearTimeout(delayDebounce); // bersihkan timer jika input berubah sebelum 500ms
  }, [search, filterTipe, fetchAccounts]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    let updatedForm = {
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    };

    if (name === 'tipe_akun_id' && !formData.isSub) {
      updatedForm.nomor_akun = generateNomorAkun(value, false);
    }

    if (name === 'isSub') {
      updatedForm.sub_account_of = '';
      updatedForm.nomor_akun = '';
    }

    if (name === 'sub_account_of') {
      updatedForm.nomor_akun = generateNomorAkun(
        formData.tipe_akun_id,
        true,
        value
      );
    }

    setFormData(updatedForm);
  };

  const handleTipeAkunChange = (e) => {
    const tipeId = e.target.value;
    const tipe = tipeAkuns.find((t) => t.id === parseInt(tipeId));

    setSelectedTipeAkun(tipe);

    const newNumber = generateNomorAkun(
      tipeId,
      formData.isSub,
      formData.sub_account_of
    );

    setFormData((prev) => ({
      ...prev,
      tipe_akun_id: tipeId,
      nomor_akun: newNumber,
      // Reset currency jika tipe akun tidak membutuhkannya
      currency_id: tipe?.is_currency_required ? prev.currency_id : '',
      // Reset saldo awal jika tipe akun tidak membutuhkannya
      saldo_awal: tipe?.is_opening_balance_required ? prev.saldo_awal : '',
    }));
  };

  const generateNomorAkun = (tipeId, isSub = false, indukId = null) => {
    if (isSub && indukId) {
      const induk = chartAccounts.find((a) => a.id === parseInt(indukId));
      if (!induk) return '';

      const [seg1, seg2] = induk.nomor_akun.split('-');

      // Ambil sub-akun langsung dari akun induk
      const subList = chartAccounts
        .filter((a) => a.sub_account_of === induk.id)
        .map((a) => parseInt(a.nomor_akun.split('-')[2]));

      // Nomor dasar sub-akun: ambil dari nomor akun induk, misal 1000 jadi base 1100
      const baseNumber =
        Math.floor(parseInt(induk.nomor_akun.split('-')[2]) / 1000) * 1000 +
        100;

      // Jika sudah ada sub-akun, ambil angka terbesar lalu +100
      const nextSubNumber =
        subList.length > 0 ? Math.max(...subList) + 100 : baseNumber;

      return `${seg1}-${seg2}-${nextSubNumber}`;
    } else {
      // AKUN UTAMA
      const tipe = tipeAkuns.find((t) => t.id === parseInt(tipeId));
      if (!tipe) return '';

      const kode = tipe.kode || '1-1';
      const [seg1, seg2] = kode.split('-');
      const safeSeg1 = seg1 || '1';
      const safeSeg2 = seg2 || '1';

      const akunUtama = chartAccounts.filter(
        (a) => a.tipe_akun_id === parseInt(tipeId) && !a.sub_account_of
      );

      const existingMainNumbers = akunUtama.map((a) =>
        parseInt(a.nomor_akun.split('-')[2])
      );

      const nextMainNumber =
        existingMainNumbers.length > 0
          ? Math.max(...existingMainNumbers) + 1000
          : 1000;

      return `${safeSeg1}-${safeSeg2}-${nextMainNumber}`;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.nama.trim()) return alert('Nama akun wajib diisi.');
    if (!formData.tipe_akun_id) return alert('Tipe akun wajib dipilih.');
    if (!formData.kalender) return alert('Tanggal wajib diisi.');
    if (formData.isSub && !formData.sub_account_of)
      return alert('Pilih akun induk jika ini sub akun.');

    // CEK NOMOR AKUN SUDAH DIPAKAI OLEH AKUN LAIN
    const isDuplicateNomor = chartAccounts.some(
      (a) => a.nomor_akun === formData.nomor_akun && a.id !== editingId // Jangan bandingkan dengan akun yang sedang diedit
    );
    if (isDuplicateNomor) {
      return alert(
        'Nomor akun sudah digunakan. Silakan pilih/ganti nomor lain.'
      );
    }

    const payload = {
      ...formData,
      saldo_awal:
        formData.saldo_awal === '' ? 0 : parseFloat(formData.saldo_awal),
      sub_account_of: formData.isSub ? formData.sub_account_of : null,
    };

    try {
      if (editingId) {
        await api.put(`/api/chart-of-accounts/${editingId}`, payload);
      } else {
        await api.post('/api/chart-of-accounts', payload);
      }

      setFormData({
        nama: '',
        tipe_akun_id: '',
        nomor_akun: '',
        sub_account_of: '',
        saldo_awal: '',
        kalender: today,
        isSub: false,
      });
      setEditingId(null);
      setShowModal(false);
      fetchAccounts();
    } catch (err) {
      if (err.response && err.response.status === 409) {
        // 409 Conflict dari backend karena nomor akun unik
        alert('Gagal menyimpan: Nomor akun sudah digunakan.');
      } else {
        console.error(err);
        alert('Gagal menyimpan data.');
      }
    }
  };

  const handleEdit = (akun) => {
    setFormData({
      nama: akun.nama,
      tipe_akun_id: akun.tipe_akun_id,
      nomor_akun: akun.nomor_akun,
      sub_account_of: akun.sub_account_of || '',
      saldo_awal: akun.saldo_awal,
      kalender: akun.kalender,
      isSub: !!akun.sub_account_of,
    });
    setEditingId(akun.id);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    try {
      await api.delete(`/api/chart-of-accounts/${confirmDeleteId}`);
      setConfirmDeleteId(null);
      fetchAccounts();
    } catch (err) {
      console.error(err);
      alert('Gagal menghapus akun.');
    }
  };

  const handleSort = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('asc');
    }
  };

  const buildAccountTree = (list, parentId = null, level = 0) => {
    return list
      .filter((a) => a.sub_account_of === parentId)
      .flatMap((a) => [
        { ...a, level },
        ...buildAccountTree(list, a.id, level + 1),
      ]);
  };

  const filteredChartAccounts = chartAccounts.filter((a) => {
    const matchTipe = filterTipe
      ? String(a.tipe_akun_id) === String(filterTipe)
      : true;
    const matchSearch = search
      ? a.nama.toLowerCase().includes(search.toLowerCase())
      : true;
    return matchTipe && matchSearch;
  });

  const treeData = buildAccountTree(filteredChartAccounts);
  const sortedTreeData = [...treeData].sort((a, b) => {
    if (!sortBy) return 0;

    const valA = sortBy === 'tipe' ? a.tipe_akun?.nama || '' : a[sortBy] || '';
    const valB = sortBy === 'tipe' ? b.tipe_akun?.nama || '' : b[sortBy] || '';

    if (typeof valA === 'string') {
      return sortOrder === 'asc'
        ? valA.localeCompare(valB)
        : valB.localeCompare(valA);
    }

    if (typeof valA === 'number') {
      return sortOrder === 'asc' ? valA - valB : valB - valA;
    }

    return 0;
  });

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Chart of Accounts</h2>
          <button
            onClick={() => {
              setEditingId(null);
              setFormData({
                nama: '',
                tipe_akun_id: '',
                nomor_akun: '',
                sub_account_of: '',
                saldo_awal: '',
                kalender: today,
                isSub: false,
              });
              setShowModal(true);
            }}
            className="bg-blue-600 text-white px-4 py-2 rounded"
          >
            + New
          </button>
        </div>

        <div className="mb-4">
          <select
            onChange={(e) => setFilterTipe(e.target.value)}
            className="border p-2 mr-2"
            value={filterTipe}
          >
            <option value="">All Types</option>
            {tipeAkuns.map((tipe) => (
              <option key={tipe.id} value={tipe.id}>
                {tipe.nama}
              </option>
            ))}
          </select>
          <input
            placeholder="Search by name"
            onChange={(e) => setSearch(e.target.value)}
            className="border p-2"
          />
        </div>

        <table className="w-full border">
          <thead>
            <tr className="bg-gray-100">
              <th
                className="border p-2 cursor-pointer"
                onClick={() => handleSort('nomor_akun')}
              >
                Account No{' '}
                {sortBy === 'nomor_akun' && (sortOrder === 'asc' ? '↑' : '↓')}
              </th>
              <th className="border p-2">Name</th>
              <th
                className="border p-2 cursor-pointer"
                onClick={() => handleSort('tipe')}
              >
                Type {sortBy === 'tipe' && (sortOrder === 'asc' ? '↑' : '↓')}
              </th>
              <th className="border p-2">Balance</th>
              <th className="border text-center p-2">Action</th>
            </tr>
          </thead>

          <tbody>
            {sortedTreeData.length > 0 ? (
              sortedTreeData.map((a) => (
                <tr key={a.id}>
                  <td className="border p-2">
                    {' '.repeat(a.level) + a.nomor_akun}
                  </td>
                  <td className="border p-2">{' '.repeat(a.level) + a.nama}</td>
                  <td className="border p-2">{a.tipe_akun?.nama}</td>
                  <td className="border p-2">
                    {parseFloat(a.saldo_awal || 0).toLocaleString()}
                  </td>
                  <td className="border p-2 text-center space-x-2">
                    <button
                      onClick={() => handleEdit(a)}
                      className="bg-blue-50 hover:bg-blue-100 text-blue-600 hover:text-blue-800 px-3 py-2 rounded-md transition duration-200"
                      title="Edit"
                    >
                      <i className="bi bi-pencil-fill"></i>
                    </button>
                    <button
                      onClick={() => handleDelete(a.id)}
                      className="bg-red-50 hover:bg-red-100 text-red-600 hover:text-red-800 px-3 py-2 rounded-md transition duration-200"
                      title="Hapus"
                    >
                      <i className="bi bi-trash-fill"></i>
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="text-center text-gray-500 py-4">
                  Data tidak ditemukan
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Modal Form */}
        {showModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
          <div className="bg-white p-6 rounded w-full max-w-lg relative shadow-lg">
            {/* ... (tombol close dan header modal) */}
            <form onSubmit={handleSubmit} className="space-y-3">
              {/* ... (input nama, tipe akun, nomor akun, sub account) */}

              {/* Saldo Awal - hanya tampilkan jika diperlukan */}
              {(selectedTipeAkun?.is_opening_balance_required || formData.saldo_awal !== '') && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Saldo Awal {selectedTipeAkun?.is_opening_balance_required && '*'}
                  </label>
                  <input
                    type="number"
                    name="saldo_awal"
                    value={formData.saldo_awal}
                    onChange={handleChange}
                    placeholder="Saldo Awal"
                    className="border p-2 w-full"
                    required={selectedTipeAkun?.is_opening_balance_required}
                  />
                </div>
              )}

              {/* Mata Uang - hanya tampilkan jika diperlukan */}
              {(selectedTipeAkun?.is_currency_required || formData.currency_id !== '') && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Mata Uang {selectedTipeAkun?.is_currency_required && '*'}
                  </label>
                  <select
                    name="currency_id"
                    value={formData.currency_id}
                    onChange={handleChange}
                    className="border p-2 w-full"
                    required={selectedTipeAkun?.is_currency_required}
                  >
                    <option value="">-- Pilih Mata Uang --</option>
                    {currencies.map(currency => (
                      <option key={currency.id} value={currency.id}>
                        {currency.name} ({currency.symbol})
                      </option>
                    ))}
                  </select>
                </div>
              )}

              <input
                type="date"
                name="kalender"
                value={formData.kalender}
                onChange={handleChange}
                className="border p-2 w-full"
              />

              {/* ... (tombol batal dan simpan) */}
            </form>
          </div>
        </div>
      )}

        {/* Modal Konfirmasi Hapus */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-sm">
              <h2 className="text-lg font-semibold mb-4 text-red-600">
                Konfirmasi Hapus
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Apakah Anda yakin ingin menghapus akun ini?
              </p>
              <div className="flex justify-end gap-3">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 rounded text-gray-600 hover:text-black"
                >
                  Batal
                </button>
                <button
                  onClick={confirmDelete}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
                >
                  Hapus
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default ChartOfAccounts;
