// Final Version: ChartOfAccount.jsx (with Virtual Kategori Utama Header)

import { useCallback, useState, useEffect } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const accountTypes = [
  'Kas/Bank',
  'Piutang Usaha',
  'Persediaan',
  'Aset Lancar Lainnya',
  'Aset Tetap',
  'Akumulasi Penyusutan',
  'Hutang Usaha',
  'Kewajiban Lancar Lainnya',
  'Kewajiban Jangka Panjang',
  'Ekuitas',
  'Pendapatan',
  'Harga Pokok Penjualan',
  'Beban',
  'Beban Lainnya',
  'Pendapatan Lainnya',
];

const accountCategoryMap = {
  'Kas/Bank': 'ASET',
  'Piutang Usaha': 'ASET',
  'Persediaan': 'ASET',
  'Aset Lancar Lainnya': 'ASET',
  'Aset Tetap': 'ASET',
  'Akumulasi Penyusutan': 'ASET',

  'Hutang Usaha': 'LIABILITAS',
  'Kewajiban Lancar Lainnya': 'LIABILITAS',
  'Kewajiban Jangka Panjang': 'LIABILITAS',

  'Ekuitas': 'EKUITAS',

  'Pendapatan': 'PENDAPATAN',
  'Harga Pokok Penjualan': 'PENDAPATAN',

  'Beban': 'BEBAN',
  'Beban Lainnya': 'BEBAN',

  'Pendapatan Lainnya': 'LAINNYA',
};

const accountCategoryPrefix = {
  'ASET': '1-00000',
  'LIABILITAS': '2-00000',
  'EKUITAS': '3-00000',
  'PENDAPATAN': '4-00000',
  'BEBAN': '5-00000',
  'LAINNYA': '6-00000',
};

export default function ChartOfAccount() {
  const today = new Date().toISOString().split('T')[0];

  const [accounts, setAccounts] = useState([]);
  const [form, setForm] = useState({
    name: '',
    account_type: '',
    is_sub_account: false,
    parent_id: '',
    opening_balance: '',
    as_of_date: today,
  });

  const [editingId, setEditingId] = useState(null);
  const [filterType, setFilterType] = useState('');
  const [search, setSearch] = useState('');
  const [parents, setParents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);

  const fetchAccounts = useCallback(async () => {
    const res = await api.get('/api/chart-of-accounts', {
      params: { type: filterType, search },
    });
    setAccounts(res.data);
    setParents(res.data.filter((a) => !a.is_sub_account));
  }, [filterType, search]);

  useEffect(() => {
    fetchAccounts();
  }, [fetchAccounts]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.name.trim()) return alert('Nama akun wajib diisi.');
    if (!form.account_type) return alert('Tipe akun wajib dipilih.');
    if (!form.as_of_date) return alert('Tanggal wajib diisi.');
    if (form.is_sub_account && !form.parent_id)
      return alert('Pilih akun induk jika ini sub akun.');
    if (form.opening_balance && isNaN(parseFloat(form.opening_balance)))
      return alert('Opening balance harus berupa angka.');

    try {
      const payload = {
        ...form,
        opening_balance:
          form.opening_balance === '' ? 0 : parseFloat(form.opening_balance),
      };

      if (editingId) {
        await api.put(`/api/chart-of-accounts/${editingId}`, payload);
      } else {
        await api.post('/api/chart-of-accounts', payload);
      }

      setForm({
        name: '',
        account_type: '',
        is_sub_account: false,
        parent_id: '',
        opening_balance: '',
        as_of_date: today,
      });
      setEditingId(null);
      setShowModal(false);
      fetchAccounts();
    } catch (err) {
      console.error(err);
      alert('Terjadi kesalahan saat menyimpan akun.');
    }
  };

  const handleEdit = (account) => {
    setForm({
      name: account.name,
      account_type: account.account_type,
      is_sub_account: account.is_sub_account,
      parent_id: account.parent_id || '',
      opening_balance: account.opening_balance,
      as_of_date: account.as_of_date,
    });
    setEditingId(account.id);
    setShowModal(true);
  };

  const handleDelete = (id) => setConfirmDeleteId(id);

  const confirmDelete = async () => {
    try {
      await api.delete(`/api/chart-of-accounts/${confirmDeleteId}`);
      setConfirmDeleteId(null);
      fetchAccounts();
    } catch (err) {
      console.error(err);
      alert('Gagal menghapus akun.');
    }
  };

  const buildAccountTree = (list, parentId = null, level = 0) => {
    return list
      .filter((a) => a.parent_id === parentId)
      .flatMap((a) => [
        { ...a, level },
        ...buildAccountTree(list, a.id, level + 1),
      ]);
  };

  const buildTreeWithCategoryHeaders = (list) => {
    const grouped = {};
    list.forEach((a) => {
      const category = accountCategoryMap[a.account_type] || 'LAINNYA';
      if (!grouped[category]) grouped[category] = [];
      grouped[category].push(a);
    });

    const result = [];
    Object.entries(grouped).forEach(([category, items]) => {
      result.push({
        id: `virtual-${category}`,
        name: category,
        account_no: accountCategoryPrefix[category],
        is_virtual: true,
      });
      const sorted = buildAccountTree(items);
      result.push(...sorted);
    });
    return result;
  };

  const treeData = buildTreeWithCategoryHeaders(accounts);

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl">Daftar Akun</h2>
          <button
            onClick={() => {
              setEditingId(null);
              setForm({
                name: '',
                account_type: '',
                is_sub_account: false,
                parent_id: '',
                opening_balance: '',
                as_of_date: today,
              });
              setShowModal(true);
            }}
            className="bg-blue-600 text-white px-4 py-2 rounded"
          >
            + Akun Baru
          </button>
        </div>

        <div className="mb-4">
          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="border p-2 mr-2"
          >
            <option value="">Semua Tipe</option>
            {accountTypes.map((type) => (
              <option key={type} value={type}>{type}</option>
            ))}
          </select>
          <input
            placeholder="Cari nama akun"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="border p-2"
          />
        </div>

        <table className="w-full border">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2">No Akun</th>
              <th className="border p-2">Nama</th>
              <th className="border p-2">Tipe</th>
              <th className="border p-2">Saldo</th>
              <th className="border p-2 text-center">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {treeData.length > 0 ? (
              treeData.map((a) =>
                a.is_virtual ? (
                  <tr key={a.id} className="bg-gray-200 font-semibold text-gray-700">
                    <td className="border p-2">{a.account_no}</td>
                    <td className="border p-2" colSpan={4}>{a.name}</td>
                  </tr>
                ) : (
                  <tr key={a.id}>
                    <td className="border p-2">{'â€ƒ'.repeat(a.level || 0) + a.account_no}</td>
                    <td className="border p-2">{'â€ƒ'.repeat(a.level || 0) + a.name}</td>
                    <td className="border p-2">{a.account_type}</td>
                    <td className="border p-2">{parseFloat(a.opening_balance || 0).toLocaleString()}</td>
                    <td className="border p-2 text-center space-x-2">
                      <button
                        onClick={() => handleEdit(a)}
                        className="bg-blue-500 text-white px-2 py-1 rounded"
                      >âœŽ</button>
                      <button
                        onClick={() => handleDelete(a.id)}
                        className="bg-red-500 text-white px-2 py-1 rounded"
                      >ðŸ—‘</button>
                    </td>
                  </tr>
                )
              )
            ) : (
              <tr>
                <td colSpan="5" className="text-center text-gray-500 py-4">
                  Data tidak ditemukan
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </Layout>
  );
}
