import React, { useState, useEffect } from 'react';
import Layout from '../../components/Layout';
import api from '../../axios';

const CustomerTypePage = () => {
  const [types, setTypes] = useState([]);
  const [name, setName] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const fetchData = async () => {
    try {
      const response = await api.get('/api/customer-types');
      setTypes(response.data);
    } catch (err) {
      alert('Gagal memuat data');
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const openModal = (type = null) => {
    if (type) {
      setEditingId(type.id);
      setName(type.name);
    } else {
      setEditingId(null);
      setName('');
    }
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setName('');
    setEditingId(null);
    setIsModalOpen(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await api.put(`/api/customer-types/${editingId}`, { name });
      } else {
        await api.post('/api/customer-types', { name });
      }
      closeModal();
      fetchData();
    } catch (err) {
      alert('Terjadi kesalahan saat menyimpan data.');
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Yakin ingin menghapus?')) {
      try {
        await api.delete(`/api/customer-types/${id}`);
        fetchData();
      } catch (err) {
        alert('Gagal menghapus data.');
      }
    }
  };

  return (
    <Layout>
      <div className="p-6 relative">
        <h1 className="text-2xl font-bold mb-4">Tipe Pelanggan</h1>
        <button
          onClick={() => openModal()}
          className="bg-blue-600 text-white px-4 py-2 rounded mb-4"
        >
          Tambah Tipe
        </button>

        <table className="w-full border border-gray-200">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2 text-left w-20">No</th>
              <th className="border p-2 text-left">Nama</th>
              <th className="border p-2 text-left w-40">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {types.map((type, index) => (
              <tr key={type.id}>
                <td className="border p-2">{index + 1}</td>
                <td className="border p-2">{type.name}</td>
                <td className="border p-2 space-x-2">
                  <button
                    onClick={() => openModal(type)}
                    className="bg-yellow-500 px-3 py-1 text-white rounded"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(type.id)}
                    className="bg-red-600 px-3 py-1 text-white rounded"
                  >
                    Hapus
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Modal */}
        {isModalOpen && (
          <div className="fixed inset-0 bg-black bg-opacity-30 backdrop-blur-sm flex justify-center items-center z-50">
            <div className="bg-white p-6 rounded shadow-md w-80 relative">
              <h2 className="text-lg font-semibold mb-4">
                {editingId ? 'Edit Tipe' : 'Tambah Tipe'}
              </h2>
              <form onSubmit={handleSubmit} className="space-y-4">
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  placeholder="Nama Tipe"
                  className="border px-3 py-2 rounded w-full"
                  required
                />
                <div className="flex justify-end gap-2">
                  <button
                    type="button"
                    onClick={closeModal}
                    className="px-4 py-2 bg-gray-300 rounded"
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-blue-600 text-white rounded"
                  >
                    {editingId ? 'Update' : 'Simpan'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default CustomerTypePage;
