import React, { useState } from 'react';
import './Sidebar.css';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
  const [activeSubmenu, setActiveSubmenu] = useState(null);
  const location = useLocation();

  const toggleSubmenu = (id) => {
    setActiveSubmenu((prev) => (prev === id ? null : id));
  };

  const sidebarMenu = [
    {
      label: 'Dashboard',
      path: '/dashboard',
      icon: 'bi-speedometer2',
      submenu: false,
    },
    {
      label: 'Company',
      path: '/company',
      icon: 'bi-building',
      submenu: true,
      id: 'company',
      children: ['/company/profile', '/company/menu1', '/company/menu2'],
    },
    {
      label: 'Essential',
      path: '/essential',
      icon: 'bi-database',
      submenu: true,
      id: 'essential',
      children: ['/master/currency', '/accounting/chart-of-accounts'],
    },
    {
      label: 'Entry',
      path: '/entry',
      icon: 'bi-journal-text',
      submenu: true,
      id: 'entry',
      children: [
        '/entry/gl',
        '/entry/cash',
        '/entry/sales',
        '/entry/purchase',
        '/entry/inventory',
      ],
    },
    {
      label: 'Master Data',
      path: '/master',
      icon: 'bi-archive',
      submenu: true,
      id: 'masterdata',
      children: ['/master/category', '/master/tipe-akun'],
    },
    {
      label: 'Users',
      path: '/master/users',
      icon: 'bi-people',
      submenu: false,
    },
  ];

  return (
    <>
      <div className="sidebar">
        {sidebarMenu.map((menu) =>
          menu.submenu ? (
            <div
              key={menu.label}
              className={`sidebar-icon ${
                location.pathname.startsWith(menu.path) ? 'active' : ''
              }`}
              onClick={() => toggleSubmenu(menu.id)}
              title={menu.label}
            >
              <i className={`bi ${menu.icon}`}></i>
            </div>
          ) : (
            <Link
              key={menu.label}
              to={menu.path}
              className={`sidebar-icon ${
                location.pathname === menu.path ? 'active' : ''
              }`}
              title={menu.label}
            >
              <i className={`bi ${menu.icon}`}></i>
            </Link>
          )
        )}
      </div>

      {/* Submenu: Company */}
      {activeSubmenu === 'company' && (
        <div className="submenu" style={{ top: '60px' }}>
          <div className="submenu-header">
            <h3 className="submenu-title">Perusahaan</h3>
          </div>
          <div className="submenu-content">
            <div className="submenu-grid">
              <Link
                to="/company/profile"
                className={`menu-card ${
                  location.pathname === '/company/profile'
                    ? 'active-submenu'
                    : ''
                }`}
              >
                <i
                  className="bi bi-person-badge"
                  style={{ fontSize: '1.2rem' }}
                ></i>
                <div className="menu-card-title">Company Profile</div>
              </Link>
              <Link
                to="/company/menu1"
                className={`menu-card ${
                  location.pathname === '/company/menu1' ? 'active-submenu' : ''
                }`}
              >
                <i
                  className="bi bi-list-task"
                  style={{ fontSize: '1.2rem' }}
                ></i>
                <div className="menu-card-title">Menu 1</div>
              </Link>
              <Link
                to="/company/menu2"
                className={`menu-card ${
                  location.pathname === '/company/menu2' ? 'active-submenu' : ''
                }`}
              >
                <i className="bi bi-layers" style={{ fontSize: '1.2rem' }}></i>
                <div className="menu-card-title">Menu 2</div>
              </Link>
            </div>
          </div>
        </div>
      )}

      {/* Submenu: Essential */}
      {activeSubmenu === 'essential' && (
        <div className="submenu" style={{ top: '120px' }}>
          <div className="submenu-header">
            <h3 className="submenu-title">Essential Data</h3>
          </div>
          <div className="submenu-content">
            <div className="submenu-grid">
              <Link
                to="/master/currency"
                className={`menu-card ${
                  location.pathname === '/master/currency'
                    ? 'active-submenu'
                    : ''
                }`}
              >
                <i
                  className="bi bi-currency-dollar"
                  style={{ fontSize: '1.2rem' }}
                ></i>
                <div className="menu-card-title">Currency</div>
              </Link>
              <Link
                to="/accounting/chart-of-accounts"
                className={`menu-card ${
                  location.pathname === '/accounting/chart-of-accounts'
                    ? 'active-submenu'
                    : ''
                }`}
              >
                <i
                  className="bi bi-diagram-3"
                  style={{ fontSize: '1.2rem' }}
                ></i>
                <div className="menu-card-title">Chart Of Account</div>
              </Link>
            </div>
          </div>
        </div>
      )}

      {/* Submenu: Entry */}
      {activeSubmenu === 'entry' && (
        <div className="submenu" style={{ top: '180px' }}>
          <div className="submenu-header">
            <h3 className="submenu-title">Data Entry</h3>
          </div>
          <div className="submenu-content">
            <div className="submenu-grid">
              <Link
                to="/entry/gl"
                className={`menu-card ${
                  location.pathname === '/entry/gl' ? 'active-submenu' : ''
                }`}
              >
                <i
                  className="bi bi-journal-check"
                  style={{ fontSize: '1.2rem' }}
                ></i>
                <div className="menu-card-title">General Ledger</div>
              </Link>
              <Link
                to="/entry/cash"
                className={`menu-card ${
                  location.pathname === '/entry/cash' ? 'active-submenu' : ''
                }`}
              >
                <i
                  className="bi bi-cash-stack"
                  style={{ fontSize: '1.2rem' }}
                ></i>
                <div className="menu-card-title">Cash & Bank</div>
              </Link>
              <Link
                to="/entry/sales"
                className={`menu-card ${
                  location.pathname === '/entry/sales' ? 'active-submenu' : ''
                }`}
              >
                <i
                  className="bi bi-cart-check"
                  style={{ fontSize: '1.2rem' }}
                ></i>
                <div className="menu-card-title">Sales</div>
              </Link>
              <Link
                to="/entry/purchase"
                className={`menu-card ${
                  location.pathname === '/entry/purchase'
                    ? 'active-submenu'
                    : ''
                }`}
              >
                <i
                  className="bi bi-bag-check"
                  style={{ fontSize: '1.2rem' }}
                ></i>
                <div className="menu-card-title">Purchase</div>
              </Link>
              <Link
                to="/entry/inventory"
                className={`menu-card ${
                  location.pathname === '/entry/inventory'
                    ? 'active-submenu'
                    : ''
                }`}
              >
                <i
                  className="bi bi-box-seam"
                  style={{ fontSize: '1.2rem' }}
                ></i>
                <div className="menu-card-title">Inventory</div>
              </Link>
            </div>
          </div>
        </div>
      )}
      {/* Submenu: Master data */}
      {activeSubmenu === 'masterdata' && (
        <div className="submenu" style={{ top: '240px' }}>
          <div className="submenu-header">
            <h3 className="submenu-title">Master Data</h3>
          </div>
          <div className="submenu-content">
            <div className="submenu-grid">
              <Link
                to="/master/category"
                className={`menu-card ${
                  location.pathname === '/master/category'
                    ? 'active-submenu'
                    : ''
                }`}
              >
                <i className="bi bi-tags" style={{ fontSize: '1.2rem' }}></i>
                <div className="menu-card-title">Kategori</div>
              </Link>
              <Link
                to="/master/tipe-akun"
                className={`menu-card ${
                  location.pathname === '/master/tipe-akun'
                    ? 'active-submenu'
                    : ''
                }`}
              >
                <i className="bi bi-tags" style={{ fontSize: '1.2rem' }}></i>
                <div className="menu-card-title">Master Data Tipe Akun</div>
              </Link>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Sidebar;
