import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../axios';
import { useAuth } from './AuthContext';
import { useEffect } from 'react';


function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const { refreshAuth } = useAuth();

  const login = async (e) => {
    e.preventDefault();
    try {
      await api.get('/sanctum/csrf-cookie');
      await api.post('/login', { email, password }, { withCredentials: true });

      // Tunggu refreshAuth selesai
      const promise = new Promise((resolve) => {
        refreshAuth(); // panggil refreshAuth()
        const checkAuth = setInterval(() => {
          if (localStorage.getItem('isLoggedIn') === 'true') {
            clearInterval(checkAuth);
            resolve();
          }
        }, 100); // cek setiap 100ms
      });

      await promise; // tunggu sampai refreshAuth selesai
      navigate('/dashboard');
    } catch (err) {
      console.error('Login error:', err.response || err.message);
      alert('Login gagal');
    }
  };

  
  

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form onSubmit={login} className="bg-white p-6 rounded shadow-md w-96">
        <h2 className="text-2xl font-bold mb-4">Login</h2>
        <input
          className="block w-full mb-3 p-2 border rounded"
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          className="block w-full mb-3 p-2 border rounded"
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
          Login
        </button>
      </form>
    </div>
  );
}

export default Login;
