import { useState, useEffect } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const accountTypes = [
  'Cash/Bank',
  'Account Receivable',
  'Inventory',
  'Other Current Asset',
  'Fixed Asset',
  'Accumulated Depreciation',
  'Account Payable',
  'Other Current Liability',
];

export default function ChartOfAccount() {
  const today = new Date().toISOString().split('T')[0];

  const [accounts, setAccounts] = useState([]);
  const [form, setForm] = useState({
    name: '',
    account_type: '',
    is_sub_account: false,
    parent_id: '',
    opening_balance: '',
    as_of_date: today,
  });

  const [editingId, setEditingId] = useState(null);
  const [filterType, setFilterType] = useState('');
  const [search, setSearch] = useState('');
  const [parents, setParents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);

  const fetchAccounts = async () => {
    const res = await api.get('/api/chart-of-accounts', {
      params: { type: filterType, search },
    });
    setAccounts(res.data);
    setParents(res.data.filter((a) => !a.is_sub_account));
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name.trim()) return alert('Nama akun wajib diisi.');
    if (!form.account_type) return alert('Tipe akun wajib dipilih.');
    if (!form.as_of_date) return alert('Tanggal wajib diisi.');
    if (form.is_sub_account && !form.parent_id)
      return alert('Pilih akun induk jika ini sub akun.');
    if (form.opening_balance && isNaN(parseFloat(form.opening_balance)))
      return alert('Opening balance harus berupa angka.');

    try {
      const payload = {
        ...form,
        opening_balance:
          form.opening_balance === '' ? 0 : parseFloat(form.opening_balance),
      };

      if (editingId) {
        await api.put(`/api/chart-of-accounts/${editingId}`, payload);
      } else {
        await api.post('/api/chart-of-accounts', payload);
      }

      setForm({
        name: '',
        account_type: '',
        is_sub_account: false,
        parent_id: '',
        opening_balance: '',
        as_of_date: today,
      });
      setEditingId(null);
      setShowModal(false);
      fetchAccounts();
    } catch (err) {
      console.error(err);
      alert('Terjadi kesalahan saat menyimpan akun.');
    }
  };

  const handleEdit = (account) => {
    setForm({
      name: account.name,
      account_type: account.account_type,
      is_sub_account: account.is_sub_account,
      parent_id: account.parent_id || '',
      opening_balance: account.opening_balance,
      as_of_date: account.as_of_date,
    });
    setEditingId(account.id);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    try {
      await api.delete(`/api/chart-of-accounts/${confirmDeleteId}`);
      setConfirmDeleteId(null);
      fetchAccounts();
    } catch (err) {
      console.error(err);
      alert('Gagal menghapus akun.');
    }
  };

  const buildAccountTree = (list, parentId = null, level = 0) => {
    return list
      .filter((a) => a.parent_id === parentId)
      .flatMap((a) => [
        { ...a, level },
        ...buildAccountTree(list, a.id, level + 1),
      ]);
  };

  const treeData = buildAccountTree(accounts);

  useEffect(() => {
    fetchAccounts();
  }, [filterType, search]);

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl">Chart of Accounts</h2>
          <button
            onClick={() => {
              setEditingId(null);
              setForm({
                name: '',
                account_type: '',
                is_sub_account: false,
                parent_id: '',
                opening_balance: '',
                as_of_date: today,
              });
              setShowModal(true);
            }}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            + New
          </button>
        </div>

        <div className="mb-4">
          <select
            onChange={(e) => setFilterType(e.target.value)}
            className="border p-2 mr-2"
            value={filterType}
          >
            <option value="">All Types</option>
            {accountTypes.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
          <input
            placeholder="Search by name"
            onChange={(e) => setSearch(e.target.value)}
            className="border p-2"
          />
        </div>

        <table className="w-full border">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2">Account No</th>
              <th className="border p-2">Name</th>
              <th className="border p-2">Type</th>
              <th className="border p-2">Balance</th>
              <th className="border text-center p-2">Action</th>
            </tr>
          </thead>
          <tbody>
            {treeData.length > 0 ? (
              treeData.map((a) => (
                <tr key={a.id}>
                  <td className="border p-2">
                    {a.is_sub_account
                      ? ' '.repeat(a.level) + a.account_no
                      : a.account_no}
                  </td>
                  <td className="border p-2">
                    {a.is_sub_account ? ' '.repeat(a.level) + a.name : a.name}
                  </td>
                  <td className="border p-2">{a.account_type}</td>
                  <td className="border p-2">
                    {parseFloat(a.opening_balance || 0).toLocaleString()}
                  </td>
                  <td className="border p-2 text-center space-x-2">
                    <button
                      onClick={() => handleEdit(a)}
                      className="bg-blue-50 hover:bg-blue-100 text-blue-600 hover:text-blue-800 px-3 py-2 rounded-md transition duration-200"
                      title="Edit"
                    >
                      <i className="bi bi-pencil-fill"></i>
                    </button>
                    <button
                      onClick={() => handleDelete(a.id)}
                      className="bg-red-50 hover:bg-red-100 text-red-600 hover:text-red-800 px-3 py-2 rounded-md transition duration-200"
                      title="Hapus"
                    >
                      <i className="bi bi-trash-fill"></i>
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="text-center text-gray-500 py-4">
                  Data tidak ditemukan
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Modal Form */}
        {showModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
            <div className="bg-white p-6 rounded w-full max-w-lg relative shadow-lg">
              <button
                className="absolute top-2 right-2 text-gray-500 hover:text-black"
                onClick={() => {
                  setShowModal(false);
                  setEditingId(null);
                }}
              >
                ✕
              </button>
              <h3 className="text-lg font-semibold mb-4">
                {editingId ? 'Edit Account' : 'Create New Account'}
              </h3>
              <form onSubmit={handleSubmit} className="space-y-3">
                <input
                  name="name"
                  value={form.name}
                  onChange={handleChange}
                  placeholder="Name"
                  className="border p-2 w-full"
                />
                <select
                  name="account_type"
                  value={form.account_type}
                  onChange={handleChange}
                  className="border p-2 w-full"
                >
                  <option value="">-- Select Account Type --</option>
                  {accountTypes.map((type) => (
                    <option key={type} value={type}>
                      {type}
                    </option>
                  ))}
                </select>

                <label className="flex items-center gap-2">
                  <input
                    type="checkbox"
                    name="is_sub_account"
                    checked={form.is_sub_account}
                    onChange={handleChange}
                  />
                  Sub Account of
                </label>

                {form.is_sub_account && (
                  <select
                    name="parent_id"
                    value={form.parent_id}
                    onChange={handleChange}
                    className="border p-2 w-full"
                    disabled={!form.account_type}
                  >
                    {!form.account_type ? (
                      <option value="">Tentukan tipe dahulu</option>
                    ) : (
                      <>
                        <option value="">-- Pilih Akun Induk --</option>
                        {parents
                          .filter((p) => p.account_type === form.account_type)
                          .map((p) => (
                            <option key={p.id} value={p.id}>
                              {p.name}
                            </option>
                          ))}
                      </>
                    )}
                  </select>
                )}

                <input
                  type="number"
                  name="opening_balance"
                  value={form.opening_balance}
                  onChange={handleChange}
                  placeholder="Opening Balance (optional)"
                  className="border p-2 w-full"
                />
                <input
                  type="date"
                  name="as_of_date"
                  value={form.as_of_date}
                  onChange={handleChange}
                  className="border p-2 w-full"
                />

                <div className="flex justify-end gap-2">
                  <button
                    type="button"
                    onClick={() => {
                      setShowModal(false);
                      setEditingId(null);
                      setForm({
                        name: '',
                        account_type: '',
                        is_sub_account: false,
                        parent_id: '',
                        opening_balance: '',
                        as_of_date: today,
                      });
                    }}
                    className="text-gray-600"
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="bg-blue-600 text-white px-4 py-2 rounded"
                  >
                    Simpan
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Modal Konfirmasi Hapus */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-sm">
              <h2 className="text-lg font-semibold mb-4 text-red-600 flex items-center">
                <i className="bi bi-exclamation-triangle-fill mr-2 text-xl" />
                Konfirmasi Hapus
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Apakah Anda yakin ingin menghapus akun ini?
              </p>
              <div className="flex justify-end gap-3">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 rounded text-gray-600 hover:text-black"
                >
                  Batal
                </button>
                <button
                  onClick={confirmDelete}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
                >
                  Hapus
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
}
