import { useEffect, useState } from 'react';
import api from '../../axios';
import AlamatTab from './PemasokTab/AlamatTab';
import TerminTab from './PemasokTab/TerminTab';
import KontakTab from './PemasokTab/KontakTab';
import CatatanTab from './PemasokTab/CatatanTab';

const PemasokForm = ({
  show,
  onClose,
  editMode = false,
  data = null,
  onSuccess,
}) => {
  const [tab, setTab] = useState('alamat');
  const [formData, setFormData] = useState({
    is_active: true,
    no_pemasok: '',
    nama: '',
    alamat: '',
    kota: '',
    provinsi: '',
    kode_pos: '',
    negara: '',
    telepon: '',
    faksimili: '',
    kontak: '',
    email: '',
    website: '',
    termin_id: '',
    currency_id: '',
    saldo_awal: 0,
    saldo_awal_tanggal: '',
    default_keterangan: '',
    pajak_1_id: '',
    pajak_2_id: '',
    npwp: '',
    tax_type_id: '',
    pesan: '',
    catatan: '',
    kontak_pemasok: [],
  });

  useEffect(() => {
    if (editMode && data) {
      setFormData({
        ...data,
        kontak_pemasok: data.kontak || [],
      });
    }
  }, [editMode, data]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleKontakChange = (kontakList) => {
    setFormData((prev) => ({
      ...prev,
      kontak_pemasok: kontakList,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editMode) {
        await api.put(`/api/pemasok/${data.id}`, formData);
      } else {
        await api.post('/api/pemasok', formData);
      }
      onSuccess();
      onClose();
    } catch (err) {
      console.error('Submit error:', err.response?.data || err.message);
      alert('Gagal menyimpan data. Periksa input!');
    }
  };

  if (!show) return null;

  return (
    <div className="modal fade show d-block" tabIndex="-1" role="dialog">
      <div className="modal-dialog modal-xl" role="document">
        <div className="modal-content">
          <form onSubmit={handleSubmit}>
            <div className="modal-header">
              <h5 className="modal-title">
                {editMode ? 'Edit Pemasok' : 'Tambah Pemasok'}
              </h5>
              <button
                type="button"
                className="btn-close"
                onClick={onClose}
              ></button>
            </div>
            <div className="modal-body">
              <ul className="nav nav-tabs mb-3">
                <li className="nav-item">
                  <button
                    type="button"
                    className={`nav-link ${tab === 'alamat' ? 'active' : ''}`}
                    onClick={() => setTab('alamat')}
                  >
                    Alamat
                  </button>
                </li>
                <li className="nav-item">
                  <button
                    type="button"
                    className={`nav-link ${tab === 'termin' ? 'active' : ''}`}
                    onClick={() => setTab('termin')}
                  >
                    Termin
                  </button>
                </li>
                <li className="nav-item">
                  <button
                    type="button"
                    className={`nav-link ${tab === 'kontak' ? 'active' : ''}`}
                    onClick={() => setTab('kontak')}
                  >
                    Kontak
                  </button>
                </li>
                <li className="nav-item">
                  <button
                    type="button"
                    className={`nav-link ${tab === 'catatan' ? 'active' : ''}`}
                    onClick={() => setTab('catatan')}
                  >
                    Catatan
                  </button>
                </li>
              </ul>

              {/* === Tab Content === */}
              <div>
                {tab === 'alamat' && (
                  <AlamatTab formData={formData} onChange={handleChange} />
                )}
                {tab === 'termin' && (
                  <TerminTab formData={formData} onChange={handleChange} />
                )}
                {tab === 'kontak' && (
                  <KontakTab
                    kontakList={formData.kontak_pemasok}
                    onChange={handleKontakChange}
                  />
                )}
                {tab === 'catatan' && (
                  <CatatanTab
                    value={formData.catatan}
                    onChange={(val) =>
                      setFormData((prev) => ({
                        ...prev,
                        catatan: val,
                      }))
                    }
                  />
                )}
              </div>
            </div>

            <div className="modal-footer">
              <button type="submit" className="btn btn-success">
                Simpan
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={onClose}
              >
                Batal
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PemasokForm;
