import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';

const ContactTab = ({ form, setForm }) => {
  const [editingIndex, setEditingIndex] = useState(null);
  const [contactForm, setContactForm] = useState(initialContact());

  function initialContact() {
    return {
      temp_id: uuidv4(), // Untuk identifikasi lokal
      first_name: '',
      last_name: '',
      title: '',
      business_phone1: '',
      business_phone2: '',
      mobile_phone: '',
      pager: '',
      email: '',
      extension1: '',
      extension2: '',
      fax: '',
      home_phone: '',
      notes: '',
    };
  }

  const handleChange = (field, value) => {
    setContactForm((prev) => ({ ...prev, [field]: value }));
  };

  const saveContact = () => {
    if (editingIndex !== null) {
      const updated = [...form.contacts];
      updated[editingIndex] = contactForm;
      setForm((prev) => ({ ...prev, contacts: updated }));
      setEditingIndex(null);
    } else {
      setForm((prev) => ({
        ...prev,
        contacts: [...(prev.contacts || []), contactForm],
      }));
    }
    setContactForm(initialContact());
  };

  const editContact = (index) => {
    setEditingIndex(index);
    setContactForm(form.contacts[index]);
  };

  const deleteContact = (index) => {
    const updated = [...form.contacts];
    updated.splice(index, 1);
    setForm((prev) => ({ ...prev, contacts: updated }));
    if (editingIndex === index) {
      setEditingIndex(null);
      setContactForm(initialContact());
    }
  };

  const cancelEdit = () => {
    setEditingIndex(null);
    setContactForm(initialContact());
  };

  return (
    <div className="grid gap-6">
      {/* Kontak Table */}
      <table className="w-full table-auto border text-sm">
        <thead className="bg-gray-100">
          <tr>
            <th className="border px-2 py-1">Nama</th>
            <th className="border px-2 py-1">Nama Depan</th>
            <th className="border px-2 py-1">Jabatan</th>
            <th className="border px-2 py-1">Telepon</th>
            <th className="border px-2 py-1">Aksi</th>
          </tr>
        </thead>
        <tbody>
          {form.contacts?.map((contact, i) => (
            <tr key={contact.temp_id}>
              <td className="border px-2 py-1">
                {contact.first_name} {contact.last_name}
              </td>
              <td className="border px-2 py-1">{contact.first_name}</td>
              <td className="border px-2 py-1">{contact.title}</td>
              <td className="border px-2 py-1">{contact.business_phone1}</td>
              <td className="border px-2 py-1 text-center">
                <button
                  onClick={() => editContact(i)}
                  className="text-blue-500 mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => deleteContact(i)}
                  className="text-red-500"
                >
                  Hapus
                </button>
              </td>
            </tr>
          ))}
          {form.contacts?.length === 0 && (
            <tr>
              <td colSpan="5" className="text-center py-2 text-gray-500">
                Belum ada kontak
              </td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Form Kontak */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 border p-4 rounded">
        <div>
          <label>Nama Depan</label>
          <input
            type="text"
            value={contactForm.first_name}
            onChange={(e) => handleChange('first_name', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label>Belakang</label>
          <input
            type="text"
            value={contactForm.last_name}
            onChange={(e) => handleChange('last_name', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label>Jabatan</label>
          <input
            type="text"
            value={contactForm.title}
            onChange={(e) => handleChange('title', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label>Bisnis 1</label>
          <input
            type="text"
            value={contactForm.business_phone1}
            onChange={(e) => handleChange('business_phone1', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label>Bisnis 2</label>
          <input
            type="text"
            value={contactForm.business_phone2}
            onChange={(e) => handleChange('business_phone2', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label>Seluler</label>
          <input
            type="text"
            value={contactForm.mobile_phone}
            onChange={(e) => handleChange('mobile_phone', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label>Pager</label>
          <input
            type="text"
            value={contactForm.pager}
            onChange={(e) => handleChange('pager', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label>E-mail</label>
          <input
            type="email"
            value={contactForm.email}
            onChange={(e) => handleChange('email', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label>Ext</label>
          <input
            type="text"
            value={contactForm.extension1}
            onChange={(e) => handleChange('extension1', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label>Ext</label>
          <input
            type="text"
            value={contactForm.extension2}
            onChange={(e) => handleChange('extension2', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label>Faksimili</label>
          <input
            type="text"
            value={contactForm.fax}
            onChange={(e) => handleChange('fax', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div>
          <label>Rumah</label>
          <input
            type="text"
            value={contactForm.home_phone}
            onChange={(e) => handleChange('home_phone', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>
        <div className="md:col-span-2">
          <label>Catatan</label>
          <textarea
            value={contactForm.notes}
            onChange={(e) => handleChange('notes', e.target.value)}
            className="w-full border px-2 py-1 rounded"
          />
        </div>

        <div className="md:col-span-2 flex justify-end gap-2">
          {editingIndex !== null && (
            <button
              onClick={cancelEdit}
              className="px-4 py-1 bg-gray-300 rounded"
            >
              Batal
            </button>
          )}
          <button
            onClick={saveContact}
            className="px-4 py-1 bg-blue-500 text-white rounded"
          >
            {editingIndex !== null ? 'Update' : 'Tambah'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ContactTab;
