import React, { useState } from 'react';
import './Sidebar.css';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
  const [activeSubmenu, setActiveSubmenu] = useState(null);
  const location = useLocation();

  const toggleSubmenu = (id) => {
    setActiveSubmenu((prev) => (prev === id ? null : id));
  };

  const sidebarMenu = [
    {
      label: 'Dashboard',
      path: '/dashboard',
      icon: 'bi-speedometer2',
      submenu: false,
    },
    {
      label: 'Company',
      path: '/company',
      icon: 'bi-building',
      submenu: true,
      id: 'company',
    },
    {
      label: 'Essential',
      path: '/essential',
      icon: 'bi-gear',
      submenu: true,
      id: 'essential',
    },
    {
      label: 'Entry',
      path: '/entry',
      icon: 'bi-journal-text',
      submenu: true,
      id: 'entry',
    },
  ];

  return (
    <>
      <div className="sidebar">
        {sidebarMenu.map((menu) =>
          menu.submenu ? (
            <div
              key={menu.label}
              className={`sidebar-icon ${
                location.pathname.startsWith(menu.path) ? 'active' : ''
              }`}
              onClick={() => toggleSubmenu(menu.id)}
              title={menu.label}
            >
              <i className={`bi ${menu.icon}`}></i>
            </div>
          ) : (
            <Link
              key={menu.label}
              to={menu.path}
              className={`sidebar-icon ${
                location.pathname === menu.path ? 'active' : ''
              }`}
              title={menu.label}
            >
              <i className={`bi ${menu.icon}`}></i>
            </Link>
          )
        )}
      </div>

      {/* Submenu: Company */}
      {activeSubmenu === 'company' && (
        <div className="submenu" style={{ top: '60px' }}>
          <div className="submenu-header">
            <h3 className="submenu-title">Perusahaan</h3>
          </div>
          <div className="submenu-content">
            <div className="submenu-grid">
              <Link
                to="/company/profile"
                className={`menu-card ${
                  location.pathname === '/company/profile'
                    ? 'active-submenu'
                    : ''
                }`}
                title="Company Profile"
              >
                <div className="menu-card-title">Company Profile</div>
              </Link>
              <Link
                to="/company/menu1"
                className={`menu-card ${
                  location.pathname === '/company/menu1' ? 'active-submenu' : ''
                }`}
                title="Menu 1"
              >
                <div className="menu-card-title">Menu 1</div>
              </Link>
              <Link
                to="/company/menu2"
                className={`menu-card ${
                  location.pathname === '/company/menu2' ? 'active-submenu' : ''
                }`}
                title="Menu 2"
              >
                <div className="menu-card-title">Menu 2</div>
              </Link>
            </div>
          </div>
        </div>
      )}

      {/* Submenu: Essential */}
      {activeSubmenu === 'essential' && (
        <div className="submenu" style={{ top: '120px' }}>
          <div className="submenu-header">
            <h3 className="submenu-title">Essential Data</h3>
          </div>
          <div className="submenu-content">
            <div className="submenu-grid">
              <Link
                to="/essential/currency"
                className={`menu-card ${
                  location.pathname === '/essential/currency'
                    ? 'active-submenu'
                    : ''
                }`}
                title="Currency"
              >
                <div className="menu-card-title">Currency</div>
              </Link>
              <Link
                to="/essential/coa"
                className={`menu-card ${
                  location.pathname === '/essential/coa' ? 'active-submenu' : ''
                }`}
                title="Chart Of Account"
              >
                <div className="menu-card-title">Chart Of Account</div>
              </Link>
            </div>
          </div>
        </div>
      )}

      {/* Submenu: Entry */}
      {activeSubmenu === 'entry' && (
        <div className="submenu" style={{ top: '180px' }}>
          <div className="submenu-header">
            <h3 className="submenu-title">Data Entry</h3>
          </div>
          <div className="submenu-content">
            <div className="submenu-grid">
              <Link
                to="/entry/gl"
                className={`menu-card ${
                  location.pathname === '/entry/gl' ? 'active-submenu' : ''
                }`}
                title="General Ledger"
              >
                <div className="menu-card-title">General Ledger</div>
              </Link>
              <Link
                to="/entry/cash"
                className={`menu-card ${
                  location.pathname === '/entry/cash' ? 'active-submenu' : ''
                }`}
                title="Cash & Bank"
              >
                <div className="menu-card-title">Cash & Bank</div>
              </Link>
              <Link
                to="/entry/sales"
                className={`menu-card ${
                  location.pathname === '/entry/sales' ? 'active-submenu' : ''
                }`}
                title="Sales"
              >
                <div className="menu-card-title">Sales</div>
              </Link>
              <Link
                to="/entry/purchase"
                className={`menu-card ${
                  location.pathname === '/entry/purchase'
                    ? 'active-submenu'
                    : ''
                }`}
                title="Purchase"
              >
                <div className="menu-card-title">Purchase</div>
              </Link>
              <Link
                to="/entry/inventory"
                className={`menu-card ${
                  location.pathname === '/entry/inventory'
                    ? 'active-submenu'
                    : ''
                }`}
                title="Inventory"
              >
                <div className="menu-card-title">Inventory</div>
              </Link>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Sidebar;
