import React, { useEffect, useState } from 'react';
import Layout from '../../components/Layout';
import api from '../../axios';

const MstTax = () => {
  const [taxes, setTaxes] = useState([]);
  const [form, setForm] = useState({ nama: '', persen: '', akun_id: '' });
  const [editItem, setEditItem] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);
  const [akuns, setAkuns] = useState([]);

  useEffect(() => {
    fetchTaxes();
    fetchAkun();
  }, []);

  const fetchTaxes = async () => {
    try {
      const res = await api.get('/api/mst-tax');
      setTaxes(res.data.data);
    } catch (err) {
      console.error('Gagal mengambil data pajak:', err);
    }
  };

  const fetchAkun = async () => {
    try {
      const res = await api.get('/api/akun');
      setAkuns(res.data.data);
    } catch (err) {
      console.error('Gagal mengambil data akun:', err);
    }
  };

  const handleChange = (e) => {
    setForm((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const openModal = () => {
    setForm({ nama: '', persen: '', akun_id: '' });
    setEditItem(null);
    setShowModal(true);
  };

  const closeModal = () => {
    setForm({ nama: '', persen: '', akun_id: '' });
    setEditItem(null);
    setShowModal(false);
  };

  const handleEdit = (item) => {
    setForm({
      nama: item.nama,
      persen: item.persen,
      akun_id: item.akun_id || '',
    });
    setEditItem(item);
    setShowModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editItem) {
        await api.put(`/api/mst-tax/${editItem.id}`, form);
      } else {
        await api.post('/api/mst-tax', form);
      }
      fetchTaxes();
      closeModal();
    } catch (err) {
      console.error('Gagal menyimpan data pajak:', err);
    }
  };

  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    try {
      await api.delete(`/api/mst-tax/${confirmDeleteId}`);
      fetchTaxes();
      setConfirmDeleteId(null);
    } catch (err) {
      console.error('Gagal menghapus data:', err);
    }
  };

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold">Master Pajak</h2>
          <button
            onClick={openModal}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            + Pajak Baru
          </button>
        </div>

        <table className="w-full border">
          <thead className="bg-gray-100">
            <tr>
              <th className="border p-2 w-16 text-center">No</th>
              <th className="border p-2">Nama Pajak</th>
              <th className="border p-2 text-center">Persen</th>
              <th className="border p-2">Akun</th>
              <th className="border p-2 text-center">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {taxes.length > 0 ? (
              taxes.map((item, index) => (
                <tr key={item.id}>
                  <td className="border p-2 text-center">{index + 1}</td>
                  <td className="border p-2">{item.nama}</td>
                  <td className="border p-2 text-center">{item.persen} %</td>
                  <td className="border p-2">
                    {akuns.find((a) => a.id === item.akun_id)?.nama || '-'}
                  </td>
                  <td className="border p-2 text-center">
                    <div className="flex justify-center gap-2">
                      <button
                        onClick={() => handleEdit(item)}
                        className="bg-blue-50 hover:bg-blue-100 text-blue-600 hover:text-blue-800 px-3 py-2 rounded-md transition duration-200"
                        title="Edit"
                      >
                        <i className="bi bi-pencil-fill"></i>
                      </button>
                      <button
                        onClick={() => handleDelete(item.id)}
                        className="bg-red-50 hover:bg-red-100 text-red-600 hover:text-red-800 px-3 py-2 rounded-md transition duration-200"
                        title="Hapus"
                      >
                        <i className="bi bi-trash-fill"></i>
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="text-center p-4">
                  Tidak ada data pajak.
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Modal Tambah / Edit */}
        {showModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/20">
            <div className="bg-white p-6 rounded w-full max-w-md relative shadow-lg">
              <h2 className="text-lg mb-4 font-semibold">
                {editItem ? 'Edit Pajak' : 'Tambah Pajak'}
              </h2>
              <form onSubmit={handleSubmit}>
                <div className="mb-4">
                  <label className="block text-sm font-medium mb-1">
                    Nama Pajak
                  </label>
                  <input
                    name="nama"
                    value={form.nama}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-sm font-medium mb-1">
                    Persen
                  </label>
                  <input
                    name="persen"
                    type="number"
                    value={form.persen}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-sm font-medium mb-1">Akun</label>
                  <select
                    name="akun_id"
                    value={form.akun_id}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  >
                    <option value="">-- Pilih Akun --</option>
                    {akuns.map((akun) => (
                      <option key={akun.id} value={akun.id}>
                        {akun.nama}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="flex justify-end gap-2">
                  <button
                    type="button"
                    onClick={closeModal}
                    className="px-4 py-2 bg-gray-300 rounded"
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-blue-600 text-white rounded"
                  >
                    {editItem ? 'Perbarui' : 'Simpan'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Modal Konfirmasi Hapus */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/20">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-sm">
              <h2 className="text-lg font-semibold mb-4 text-red-600 flex items-center">
                <i className="bi bi-exclamation-triangle-fill mr-2 text-xl" />
                Konfirmasi Hapus
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Apakah Anda yakin ingin menghapus pajak ini?
              </p>
              <div className="flex justify-end gap-3">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 rounded text-gray-600 hover:text-black"
                >
                  Batal
                </button>
                <button
                  onClick={confirmDelete}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
                >
                  Hapus
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default MstTax;
