import { useState } from 'react';
import Layout from '../../components/Layout'; // pastikan path ini sesuai struktur foldermu

export default function CompanyProfile() {
  const [activeTab, setActiveTab] = useState('general');

  return (
    <Layout>
      <div className="p-4">
        <div className="bg-white shadow-sm rounded-lg p-6">
          <h4 className="text-xl font-semibold mb-4">Company Profile</h4>

          {/* Tabs */}
          <div className="border-b border-gray-200 mb-4">
            <nav className="-mb-px flex space-x-6" aria-label="Tabs">
              {['general', 'period', 'tax'].map((tab) => (
                <button
                  key={tab}
                  onClick={() => setActiveTab(tab)}
                  className={`pb-2 px-1 border-b-2 font-medium text-sm capitalize ${
                    activeTab === tab
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  {tab === 'general'
                    ? 'General'
                    : tab === 'period'
                    ? 'Accounting Period'
                    : 'Tax'}
                </button>
              ))}
            </nav>
          </div>

          {/* Tab Content */}
          <div className="bg-gray-50 rounded-md p-4 border">
            {activeTab === 'general' && (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                <Input label="Company Name" />
                <Textarea label="Address" rows={2} />
                <Input label="Phone No." />
                <Input label="Fax No." />
                <Input label="Country" />
                <Input label="Default Currency" />
              </div>
            )}

            {activeTab === 'period' && (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                <Input label="Start Date" type="date" />
                <Input label="Fiscal Year" />
                <Input label="Fiscal Month Start In" />
                <Input label="Default Period" />
                <Input label="Warn if before" placeholder="MM-DD" />
                <Input label="or after" placeholder="MM-DD" />
                <Input label="Error if before" />
                <Input label="or after" />
              </div>
            )}

            {activeTab === 'tax' && (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                <Input label="Form Serial Number" />
                <Input label="Tax Registration Number" />
                <Input label="Taxable Company's No" />
                <Input label="Tax Office Service" />
                <Input label="Type" />
                <Input label="KLU" />
              </div>
            )}
          </div>

          {/* Simpan Button */}
          <div className="text-end mt-6">
            <button className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-6 py-2 rounded-lg">
              Simpan
            </button>
          </div>
        </div>
      </div>
    </Layout>
  );
}

// Reusable components
const Input = ({ label, type = 'text', placeholder = '' }) => (
  <div>
    <label className="block text-sm font-medium">{label}:</label>
    <input
      type={type}
      placeholder={placeholder}
      className="form-input mt-1 block w-full border-gray-300 rounded-md shadow-sm"
    />
  </div>
);

const Textarea = ({ label, rows = 3 }) => (
  <div>
    <label className="block text-sm font-medium">{label}:</label>
    <textarea
      rows={rows}
      className="form-textarea mt-1 block w-full border-gray-300 rounded-md shadow-sm"
    ></textarea>
  </div>
);
