import React, { useState, useEffect } from 'react';
import { Form, Input, Row, Col, Select, DatePicker, InputNumber } from 'antd';

const { Option } = Select;

const TerminTab = ({ form, paymentTerms, currencies, taxes, typeTaxes }) => {
  const [selectedTerm, setSelectedTerm] = useState(null);

  useEffect(() => {
    const paymentTermId = form.getFieldValue('payment_term_id');
    if (paymentTermId && paymentTerms.length > 0) {
      const term = paymentTerms.find((t) => t.id === paymentTermId);
      setSelectedTerm(term);
    }
  }, [paymentTerms, form]);

  const handleTermChange = (value) => {
    const term = paymentTerms.find((t) => t.id === value);
    setSelectedTerm(term);
  };

  return (
    <Row gutter={16}>
      <Col span={24}>
        <Form.Item label="Termin Pembayaran" name="payment_term_id">
          <Select
            placeholder="Pilih Termin"
            onChange={handleTermChange}
            allowClear
          >
            {paymentTerms.map((term) => (
              <Option key={term.id} value={term.id}>
                {term.name}
              </Option>
            ))}
          </Select>
        </Form.Item>

        {selectedTerm && (
          <div
            style={{
              marginBottom: 16,
              padding: 12,
              background: '#f5f5f5',
              borderRadius: 4,
            }}
          >
            <Row gutter={16}>
              <Col span={8}>
                <div>Bayar dalam: {selectedTerm.pay_within_days} hari</div>
              </Col>
              <Col span={8}>
                <div>Diskon: {selectedTerm.discount_percent}%</div>
              </Col>
              <Col span={8}>
                <div>Jatuh tempo: {selectedTerm.due_days} hari</div>
              </Col>
            </Row>
            {selectedTerm.note && (
              <div style={{ marginTop: 8, fontStyle: 'italic' }}>
                Catatan: {selectedTerm.note}
              </div>
            )}
          </div>
        )}
      </Col>

      <Col span={12}>
        <Form.Item label="Mata Uang" name="currency_id">
          <Select placeholder="Pilih Mata Uang" allowClear>
            {currencies.map((currency) => (
              <Option key={currency.id} value={currency.id}>
                {currency.name} ({currency.symbol})
              </Option>
            ))}
          </Select>
        </Form.Item>
      </Col>

      <Col span={12}>
        <Form.Item
          label="Saldo Awal"
          name="saldo_awal"
          rules={[
            {
              validator: (_, value) => {
                if (value && isNaN(value)) {
                  return Promise.reject('Harus berupa angka');
                }
                return Promise.resolve();
              },
            },
          ]}
        >
          <InputNumber style={{ width: '100%' }} />
        </Form.Item>
      </Col>

      <Col span={12}>
        <Form.Item label="Per Tanggal" name="saldo_awal_tanggal">
          <DatePicker style={{ width: '100%' }} format="DD/MM/YYYY" />
        </Form.Item>
      </Col>

      <Col span={24}>
        <Form.Item label="Default Keterangan" name="default_keterangan">
          <Input.TextArea rows={3} />
        </Form.Item>
      </Col>

      <Col span={12}>
        <Form.Item label="Pajak 1" name="pajak1_id">
          <Select placeholder="Pilih Pajak 1" allowClear>
            {taxes.map((tax) => (
              <Option key={tax.id} value={tax.id}>
                {tax.nama} ({tax.nilai}%)
              </Option>
            ))}
          </Select>
        </Form.Item>
      </Col>

      <Col span={12}>
        <Form.Item label="Pajak 2" name="pajak2_id">
          <Select placeholder="Pilih Pajak 2" allowClear>
            {taxes.map((tax) => (
              <Option key={tax.id} value={tax.id}>
                {tax.nama} ({tax.nilai}%)
              </Option>
            ))}
          </Select>
        </Form.Item>
      </Col>

      <Col span={12}>
        <Form.Item
          label="NPWP Pemasok"
          name="npwp_pemasok"
          rules={[{ pattern: /^[0-9.]+$/, message: 'Format NPWP tidak valid' }]}
        >
          <Input />
        </Form.Item>
      </Col>

      <Col span={12}>
        <Form.Item label="Tax Type" name="tax_type_id">
          <Select placeholder="Pilih Tax Type" allowClear>
            {typeTaxes.map((type) => (
              <Option key={type.id} value={type.id}>
                {type.nama}
              </Option>
            ))}
          </Select>
        </Form.Item>
      </Col>

      <Col span={24}>
        <Form.Item label="Pesan" name="pesan">
          <Input.TextArea rows={3} />
        </Form.Item>
      </Col>
    </Row>
  );
};

export default TerminTab;
