import React, { useState } from 'react';

const defaultContact = {
  nama_depan: '',
  nama_belakang: '',
  jabatan: '',
  bisnis1: '',
  ext1: '',
  bisnis2: '',
  ext2: '',
  seluler: '',
  faksimili: '',
  pager: '',
  rumah: '',
  email: '',
  catatan: '',
};

const KontakTab = ({ kontakList = [], onChange }) => {
  const [showModal, setShowModal] = useState(false);
  const [activeTab, setActiveTab] = useState('umum');
  const [newContact, setNewContact] = useState(defaultContact);

  const handleContactChange = (e) => {
    const { name, value } = e.target;
    setNewContact((prev) => ({ ...prev, [name]: value }));
  };

  const addContact = () => {
    const updatedList = [...kontakList, newContact];
    onChange(updatedList);
    closeModal();
  };

  const deleteContact = (index) => {
    const updatedList = [...kontakList];
    updatedList.splice(index, 1);
    onChange(updatedList);
  };

  const closeModal = () => {
    setShowModal(false);
    setNewContact(defaultContact);
    setActiveTab('umum');
  };

  return (
    <div>
      <div className="mb-3 text-end">
        <button className="btn btn-primary" onClick={() => setShowModal(true)}>
          + Tambah Kontak
        </button>
      </div>

      <table className="table table-bordered table-sm">
        <thead>
          <tr>
            <th>Nama Belakang</th>
            <th>Nama Depan</th>
            <th>Jabatan</th>
            <th>Telepon</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {kontakList.length > 0 ? (
            kontakList.map((kontak, index) => (
              <tr key={index}>
                <td>{kontak.nama_belakang}</td>
                <td>{kontak.nama_depan}</td>
                <td>{kontak.jabatan}</td>
                <td>{kontak.seluler || kontak.bisnis1}</td>
                <td>
                  <button
                    className="btn btn-sm btn-danger"
                    onClick={() => deleteContact(index)}
                  >
                    Hapus
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5" className="text-center">
                Belum ada kontak
              </td>
            </tr>
          )}
        </tbody>
      </table>

      {showModal && (
        <div className="modal show d-block" tabIndex="-1" role="dialog">
          <div className="modal-dialog modal-lg" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Tambah Kontak Pemasok</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={closeModal}
                ></button>
              </div>
              <div className="modal-body">
                <ul className="nav nav-tabs">
                  {['umum', 'catatan'].map((tab) => (
                    <li className="nav-item" key={tab}>
                      <button
                        className={`nav-link ${
                          activeTab === tab ? 'active' : ''
                        }`}
                        onClick={() => setActiveTab(tab)}
                      >
                        {tab.charAt(0).toUpperCase() + tab.slice(1)}
                      </button>
                    </li>
                  ))}
                </ul>

                <div className="mt-3">
                  {activeTab === 'umum' && (
                    <div className="row g-2">
                      {[
                        ['Nama Depan', 'nama_depan'],
                        ['Nama Belakang', 'nama_belakang'],
                        ['Jabatan', 'jabatan'],
                        ['Bisnis 1', 'bisnis1'],
                        ['Ext 1', 'ext1'],
                        ['Bisnis 2', 'bisnis2'],
                        ['Ext 2', 'ext2'],
                        ['Seluler', 'seluler'],
                        ['Faksimili', 'faksimili'],
                        ['Pager', 'pager'],
                        ['Rumah', 'rumah'],
                        ['Email', 'email', 'email'],
                      ].map(([label, name, type = 'text']) => (
                        <div className="col-md-6" key={name}>
                          <label>{label}</label>
                          <input
                            type={type}
                            className="form-control"
                            name={name}
                            value={newContact[name]}
                            onChange={handleContactChange}
                          />
                        </div>
                      ))}
                    </div>
                  )}

                  {activeTab === 'catatan' && (
                    <div className="mt-3">
                      <label>Catatan</label>
                      <textarea
                        className="form-control"
                        rows="4"
                        name="catatan"
                        value={newContact.catatan}
                        onChange={handleContactChange}
                      ></textarea>
                    </div>
                  )}
                </div>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={closeModal}>
                  Batal
                </button>
                <button className="btn btn-primary" onClick={addContact}>
                  Simpan Kontak
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default KontakTab;
