import React, { useState } from 'react';

const defaultContact = {
  nama_depan: '',
  nama_belakang: '',
  jabatan: '',
  bisnis1: '',
  ext1: '',
  bisnis2: '',
  ext2: '',
  seluler: '',
  faksimili: '',
  pager: '',
  rumah: '',
  email: '',
  catatan: '',
};

const KontakTab = ({ kontakList = [], onChange }) => {
  const [showModal, setShowModal] = useState(false);
  const [activeTab, setActiveTab] = useState('umum');
  const [newContact, setNewContact] = useState(defaultContact);

  const handleContactChange = (e) => {
    const { name, value } = e.target;
    setNewContact((prev) => ({ ...prev, [name]: value }));
  };

  const addContact = () => {
    if (!newContact.nama_depan.trim() && !newContact.nama_belakang.trim()) {
      alert('Isi minimal Nama Depan atau Nama Belakang!');
      return;
    }

    const updatedList = [...kontakList, newContact];
    onChange(updatedList);
    closeModal();
  };

  const deleteContact = (index) => {
    const updatedList = [...kontakList];
    updatedList.splice(index, 1);
    onChange(updatedList);
  };

  const closeModal = () => {
    setShowModal(false);
    setNewContact(defaultContact);
    setActiveTab('umum');
  };

  const openModal = () => {
    setNewContact(defaultContact); // reset form
    setActiveTab('umum'); // reset tab
    setShowModal(true);
  };

  return (
    <div className="space-y-6">
      {/* Tambah Kontak */}
      <div className="text-right">
        <button
          className="px-4 py-2 bg-blue-600 text-white rounded-md shadow hover:bg-blue-700 transition"
          onClick={openModal}
        >
          + Tambah Kontak
        </button>
      </div>

      {/* Tabel Kontak */}
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200 border">
          <thead className="bg-gray-100">
            <tr>
              <th className="px-4 py-2 text-left text-sm font-semibold text-gray-600">Nama Belakang</th>
              <th className="px-4 py-2 text-left text-sm font-semibold text-gray-600">Nama Depan</th>
              <th className="px-4 py-2 text-left text-sm font-semibold text-gray-600">Jabatan</th>
              <th className="px-4 py-2 text-left text-sm font-semibold text-gray-600">Telepon</th>
              <th className="px-4 py-2 text-left text-sm font-semibold text-gray-600">Aksi</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200 text-sm">
            {kontakList.length > 0 ? (
              kontakList.map((kontak, index) => (
                <tr key={index}>
                  <td className="px-4 py-2">{kontak.nama_belakang}</td>
                  <td className="px-4 py-2">{kontak.nama_depan}</td>
                  <td className="px-4 py-2">{kontak.jabatan}</td>
                  <td className="px-4 py-2">{kontak.seluler || kontak.bisnis1}</td>
                  <td className="px-4 py-2">
                    <button
                      className="text-red-600 hover:text-red-800"
                      onClick={() => deleteContact(index)}
                    >
                      Hapus
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="text-center px-4 py-4 text-gray-500">
                  Belum ada kontak
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 z-50 bg-black bg-opacity-30 flex items-center justify-center">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-4xl">
            <div className="px-6 py-4 border-b flex justify-between items-center">
              <h2 className="text-lg font-semibold">Tambah Kontak Pemasok</h2>
              <button onClick={closeModal} className="text-gray-500 hover:text-gray-700 text-2xl">&times;</button>
            </div>

            {/* Tab */}
            <div className="px-6 pt-4">
              <div className="flex space-x-4 border-b">
                {['umum', 'catatan'].map((tab) => (
                  <button
                    key={tab}
                    onClick={() => setActiveTab(tab)}
                    className={`px-3 py-2 text-sm font-medium ${
                      activeTab === tab
                        ? 'border-b-2 border-blue-600 text-blue-600'
                        : 'text-gray-500 hover:text-gray-700'
                    }`}
                  >
                    {tab.charAt(0).toUpperCase() + tab.slice(1)}
                  </button>
                ))}
              </div>

              {/* Tab Content */}
              <div className="mt-4">
                {activeTab === 'umum' && (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {[
                      ['Nama Depan', 'nama_depan'],
                      ['Nama Belakang', 'nama_belakang'],
                      ['Jabatan', 'jabatan'],
                      ['Bisnis 1', 'bisnis1'],
                      ['Ext 1', 'ext1'],
                      ['Bisnis 2', 'bisnis2'],
                      ['Ext 2', 'ext2'],
                      ['Seluler', 'seluler'],
                      ['Faksimili', 'faksimili'],
                      ['Pager', 'pager'],
                      ['Rumah', 'rumah'],
                      ['Email', 'email', 'email'],
                    ].map(([label, name, type = 'text']) => (
                      <div key={name}>
                        <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
                        <input
                          type={type}
                          name={name}
                          value={newContact[name]}
                          onChange={handleContactChange}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
                        />
                      </div>
                    ))}
                  </div>
                )}

                {activeTab === 'catatan' && (
                  <div className="mt-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Catatan</label>
                    <textarea
                      name="catatan"
                      value={newContact.catatan}
                      onChange={handleContactChange}
                      rows={4}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
                    ></textarea>
                  </div>
                )}
              </div>
            </div>

            {/* Footer */}
            <div className="px-6 py-4 border-t flex justify-end space-x-3">
              <button
                onClick={closeModal}
                className="px-4 py-2 border rounded-md text-gray-700 hover:bg-gray-100"
              >
                Batal
              </button>
              <button
                onClick={addContact} // ✅ ini penting: tidak pakai addContact()
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                Simpan Kontak
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default KontakTab;
