import React, { useEffect, useState } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const ChartOfAccounts = () => {
  const [tipeAkuns, setTipeAkuns] = useState([]);
  const [chartAccounts, setChartAccounts] = useState([]);
  const [formData, setFormData] = useState({
    nama: '',
    tipe_akun_id: '',
    nomor_akun: '',
    sub_account_of: '',
    saldo_awal: '',
    kalender: '',
    isSub: false,
  });

  const [subAccounts, setSubAccounts] = useState([]);

  useEffect(() => {
    api.get('/api/tipe-akun').then((res) => setTipeAkuns(res.data));
    fetchAccounts();
  }, []);

  const fetchAccounts = () => {
    api.get('/api/chart-of-accounts').then((res) => {
      setChartAccounts(res.data);
      setSubAccounts(res.data);
    });
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleTipeAkunChange = (e) => {
    const tipeId = e.target.value;
    const tipe = tipeAkuns.find((t) => t.id === parseInt(tipeId));
    if (tipe) {
      const prefix = tipe.kode.slice(0, -4);
      const newNumber = prefix + '1000';
      setFormData((prev) => ({
        ...prev,
        tipe_akun_id: tipeId,
        nomor_akun: newNumber,
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const payload = { ...formData };
    if (!formData.isSub) payload.sub_account_of = null;

    api.post('/api/chart-of-accounts', payload).then(() => {
      fetchAccounts();
      setFormData({
        nama: '',
        tipe_akun_id: '',
        nomor_akun: '',
        sub_account_of: '',
        saldo_awal: '',
        kalender: '',
        isSub: false,
      });
    });
  };

  return (
    <Layout>
      <div>
        <h2>Chart of Accounts</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Nama:</label>
            <input
              name="nama"
              value={formData.nama}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Tipe Akun:</label>
            <select
              name="tipe_akun_id"
              value={formData.tipe_akun_id}
              onChange={handleTipeAkunChange}
              required
            >
              <option value="">-- Pilih Tipe --</option>
              {tipeAkuns.map((tipe) => (
                <option key={tipe.id} value={tipe.id}>
                  {tipe.nama}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label>Nomor Akun:</label>
            <input
              name="nomor_akun"
              value={formData.nomor_akun}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>
              <input
                type="checkbox"
                name="isSub"
                checked={formData.isSub}
                onChange={handleChange}
              />
              Sub Account of:
            </label>
            {formData.isSub && (
              <select
                name="sub_account_of"
                value={formData.sub_account_of}
                onChange={handleChange}
              >
                <option value="">-- Pilih Akun Induk --</option>
                {subAccounts.map((acc) => (
                  <option key={acc.id} value={acc.id}>
                    {acc.nama}
                  </option>
                ))}
              </select>
            )}
          </div>
          <div>
            <label>Saldo Awal:</label>
            <input
              type="number"
              name="saldo_awal"
              value={formData.saldo_awal}
              onChange={handleChange}
            />
          </div>
          <div>
            <label>Kalender:</label>
            <input
              type="date"
              name="kalender"
              value={formData.kalender}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit">Simpan</button>
        </form>

        <hr />
        <h3>Daftar Akun</h3>
        <table>
          <thead>
            <tr>
              <th>Account No</th>
              <th>Name</th>
              <th>Type</th>
              <th>Balance</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {chartAccounts.map((account) => (
              <tr key={account.id}>
                <td>{account.nomor_akun}</td>
                <td>{account.nama}</td>
                <td>{account.tipe_akun?.nama}</td>
                <td>{account.saldo_awal || 0}</td>
                <td>
                  <button
                    onClick={() =>
                      api
                        .delete(`/api/chart-of-accounts/${account.id}`)
                        .then(fetchAccounts)
                    }
                  >
                    Hapus
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </Layout>
  );
};

export default ChartOfAccounts;
