import React, { useState } from 'react';
import { Modal } from 'react-bootstrap';
import AlamatTab from './PemasokTab/AlamatTab';
import TerminTab from './PemasokTab/TerminTab';
import KontakTab from './PemasokTab/KontakTab';
import CatatanTab from './PemasokTab/CatatanTab';
import api from '../../axios';

const PemasokForm = ({ data = null, onClose }) => {
  const [activeTab, setActiveTab] = useState('alamat');
  const [formData, setFormData] = useState(
    data || {
      is_active: true,
      no_pemasok: '',
      nama: '',
      alamat: '',
      kota: '',
      provinsi: '',
      kode_pos: '',
      negara: '',
      telepon: '',
      faksimili: '',
      kontak: '',
      email: '',
      website: '',
      termin_id: '',
      currency_id: '',
      saldo_awal: 0,
      saldo_awal_tanggal: '',
      default_keterangan: '',
      pajak_1_id: '',
      pajak_2_id: '',
      npwp: '',
      tax_type_id: '',
      pesan: '',
      catatan: '',
      kontak_pemasok: [],
    }
  );

  const handleSave = async () => {
    if (data?.id) {
      await api.put(`/api/pemasok/${data.id}`, formData);
    } else {
      await api.post(`/api/pemasok`, formData);
    }
    onClose();
  };

  return (
    <Modal show onHide={onClose} size="xl">
      <Modal.Header closeButton>
        <Modal.Title>{data ? 'Edit' : 'Tambah'} Pemasok</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <ul className="nav nav-tabs">
          <li className="nav-item">
            <button
              className={`nav-link ${activeTab === 'alamat' ? 'active' : ''}`}
              onClick={() => setActiveTab('alamat')}
            >
              Alamat
            </button>
          </li>
          <li className="nav-item">
            <button
              className={`nav-link ${activeTab === 'termin' ? 'active' : ''}`}
              onClick={() => setActiveTab('termin')}
            >
              Termin
            </button>
          </li>
          <li className="nav-item">
            <button
              className={`nav-link ${activeTab === 'kontak' ? 'active' : ''}`}
              onClick={() => setActiveTab('kontak')}
            >
              Kontak
            </button>
          </li>
          <li className="nav-item">
            <button
              className={`nav-link ${activeTab === 'catatan' ? 'active' : ''}`}
              onClick={() => setActiveTab('catatan')}
            >
              Catatan
            </button>
          </li>
        </ul>

        <div className="tab-content p-3 border border-top-0">
          {activeTab === 'alamat' && (
            <AlamatTab formData={formData} setFormData={setFormData} />
          )}
          {activeTab === 'termin' && (
            <TerminTab formData={formData} setFormData={setFormData} />
          )}
          {activeTab === 'kontak' && (
            <KontakTab formData={formData} setFormData={setFormData} />
          )}
          {activeTab === 'catatan' && (
            <CatatanTab formData={formData} setFormData={setFormData} />
          )}
        </div>
      </Modal.Body>
      <Modal.Footer>
        <button className="btn btn-secondary" onClick={onClose}>
          Batal
        </button>
        <button className="btn btn-primary" onClick={handleSave}>
          Simpan
        </button>
      </Modal.Footer>
    </Modal>
  );
};

export default PemasokForm;
