import { useEffect, useState } from 'react';
import api from '../axios';
import Layout from '../components/Layout';
import { useAuth } from './AuthContext';

function Users() {
  const [users, setUsers] = useState([]);
  const { role } = useAuth();
  const [showModal, setShowModal] = useState(false);
  const [isEdit, setIsEdit] = useState(false);
  const [form, setForm] = useState({
    id: null,
    name: '',
    email: '',
    password: '',
    role: 'admin',
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await api.get('/api/users');
      setUsers(res.data);
    } catch (err) {
      console.error('Gagal mengambil data user', err);
    }
  };

  const deleteUser = async (id) => {
    if (!window.confirm('Yakin ingin menghapus user ini?')) return;

    try {
      await api.delete(`/api/users/${id}`);
      fetchUsers();
    } catch (err) {
      console.error('Gagal menghapus user', err);
    }
  };

  const openAddModal = () => {
    setIsEdit(false);
    setForm({ id: null, name: '', email: '', password: '', role: 'admin' });
    setErrors({});
    setShowModal(true);
  };

  const openEditModal = (user) => {
    setIsEdit(true);
    setForm({ ...user, password: '' }); // kosongkan password saat edit
    setErrors({});
    setShowModal(true);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      const data = { ...form };
      if (isEdit && data.password === '') {
        delete data.password; // jangan kirim password kosong
      }

      setErrors({}); // reset error
      if (isEdit) {
        await api.put(`/api/users/${form.id}`, data);
      } else {
        await api.post('/api/users', data);
      }

      setShowModal(false);
      fetchUsers();
    } catch (err) {
      if (err.response?.status === 422) {
        setErrors(err.response.data.errors || {});
      } else {
        console.error('Gagal menyimpan data user', err);
        alert('Terjadi kesalahan saat menyimpan data.');
      }
    }
  };

  if (role !== 'admin') {
    return (
      <Layout>
        <div className="p-4">
          <h1 className="text-xl font-semibold text-red-600">
            Akses ditolak: Hanya admin yang bisa melihat halaman ini.
          </h1>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-2xl font-bold">Manajemen User</h1>
          <button
            onClick={openAddModal}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Tambah User
          </button>
        </div>

        <table className="min-w-full bg-white border border-gray-300 rounded">
          <thead>
            <tr className="bg-gray-100">
              <th className="py-2 px-4 border-b">Nama</th>
              <th className="py-2 px-4 border-b">Email</th>
              <th className="py-2 px-4 border-b">Role</th>
              <th className="py-2 px-4 border-b">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id} className="text-center">
                <td className="py-2 px-4 border-b">{user.name}</td>
                <td className="py-2 px-4 border-b">{user.email}</td>
                <td className="py-2 px-4 border-b capitalize">{user.role}</td>
                <td className="py-2 px-4 border-b space-x-2">
                  <button
                    onClick={() => openEditModal(user)}
                    className="bg-yellow-500 text-white px-3 py-1 rounded hover:bg-yellow-600"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => deleteUser(user.id)}
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                  >
                    Hapus
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Modal */}
        {showModal && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40 z-50 pointer-events-auto">
            <div className="bg-white p-6 rounded w-full max-w-md shadow-lg">
              <h2 className="text-xl font-semibold mb-4">
                {isEdit ? 'Edit User' : 'Tambah User'}
              </h2>
              <div className="space-y-4">
                <div>
                  <input
                    type="text"
                    name="name"
                    placeholder="Nama"
                    value={form.name}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                  />
                  {errors.name && (
                    <p className="text-red-500 text-sm">{errors.name[0]}</p>
                  )}
                </div>
                <div>
                  <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={form.email}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                  />
                  {errors.email && (
                    <p className="text-red-500 text-sm">{errors.email[0]}</p>
                  )}
                </div>
                <div>
                  <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={form.password}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                  />
                  {!isEdit && errors.password && (
                    <p className="text-red-500 text-sm">{errors.password[0]}</p>
                  )}
                </div>
                <div>
                  <select
                    name="role"
                    value={form.role}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                  >
                    <option value="admin">Admin</option>
                    <option value="user">User</option>
                  </select>
                  {errors.role && (
                    <p className="text-red-500 text-sm">{errors.role[0]}</p>
                  )}
                </div>
              </div>

              <div className="flex justify-end mt-6 space-x-2">
                <button
                  onClick={() => setShowModal(false)}
                  className="bg-gray-300 px-4 py-2 rounded"
                >
                  Batal
                </button>
                <button
                  onClick={handleSubmit}
                  className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
                >
                  Simpan
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
}

export default Users;
