import { useState, useEffect, useCallback } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

// Kategori akun berdasarkan kelompok akuntansi
const accountTypeGroups = {
  Aset: [
    'Kas/Bank',
    'Piutang Usaha',
    'Persediaan',
    'Aset Lancar Lainnya',
    'Aset Tetap',
    'Akumulasi Penyusutan',
  ],
  Liabilitas: [
    'Hutang Usaha',
    'Kewajiban Lancar Lainnya',
    'Kewajiban Jangka Panjang',
  ],
  Ekuitas: ['Ekuitas'],
  Pendapatan: ['Pendapatan', 'Pendapatan Lainnya'],
  Beban: ['Harga Pokok Penjualan', 'Beban', 'Beban Lainnya'],
};

const allAccountTypes = Object.values(accountTypeGroups).flat();

export default function ChartOfAccount() {
  const today = new Date().toISOString().split('T')[0];

  const [accounts, setAccounts] = useState([]);
  const [form, setForm] = useState({
    name: '',
    account_type: '',
    is_sub_account: false,
    parent_id: '',
    opening_balance: '',
    as_of_date: today,
  });

  const [editingId, setEditingId] = useState(null);
  const [filterType, setFilterType] = useState('');
  const [search, setSearch] = useState('');
  const [parents, setParents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);

  const fetchAccounts = useCallback(async () => {
    try {
      const res = await api.get('/api/chart-of-accounts', {
        params: { type: filterType, search },
      });
      setAccounts(res.data);
      setParents(res.data.filter((a) => !a.is_sub_account));
    } catch (err) {
      console.error(err);
      alert('Gagal memuat data akun.');
    }
  }, [filterType, search]);

  useEffect(() => {
    fetchAccounts();
  }, [fetchAccounts]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name.trim()) return alert('Nama akun wajib diisi.');
    if (!form.account_type) return alert('Tipe akun wajib dipilih.');
    if (!form.as_of_date) return alert('Tanggal wajib diisi.');
    if (form.is_sub_account && !form.parent_id)
      return alert('Pilih akun induk jika ini sub akun.');
    if (form.opening_balance && isNaN(parseFloat(form.opening_balance)))
      return alert('Saldo awal harus berupa angka.');

    const payload = {
      ...form,
      opening_balance:
        form.opening_balance === '' ? 0 : parseFloat(form.opening_balance),
    };

    try {
      if (editingId) {
        await api.put(`/api/chart-of-accounts/${editingId}`, payload);
      } else {
        await api.post('/api/chart-of-accounts', payload);
      }

      setForm({
        name: '',
        account_type: '',
        is_sub_account: false,
        parent_id: '',
        opening_balance: '',
        as_of_date: today,
      });
      setEditingId(null);
      setShowModal(false);
      fetchAccounts();
    } catch (err) {
      console.error(err);
      alert('Terjadi kesalahan saat menyimpan akun.');
    }
  };

  const handleEdit = (account) => {
    setForm({
      name: account.name,
      account_type: account.account_type,
      is_sub_account: account.is_sub_account,
      parent_id: account.parent_id || '',
      opening_balance: account.opening_balance,
      as_of_date: account.as_of_date,
    });
    setEditingId(account.id);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    try {
      await api.delete(`/api/chart-of-accounts/${confirmDeleteId}`);
      setConfirmDeleteId(null);
      fetchAccounts();
    } catch (err) {
      console.error(err);
      alert('Gagal menghapus akun.');
    }
  };

  const buildAccountTree = (list, parentId = null, level = 0) => {
    return list
      .filter((a) => a.parent_id === parentId)
      .flatMap((a) => [
        { ...a, level },
        ...buildAccountTree(list, a.id, level + 1),
      ]);
  };

  const treeData = buildAccountTree(accounts);

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Daftar Akun</h2>
          <button
            onClick={() => {
              setEditingId(null);
              setForm({
                name: '',
                account_type: '',
                is_sub_account: false,
                parent_id: '',
                opening_balance: '',
                as_of_date: today,
              });
              setShowModal(true);
            }}
            className="bg-blue-600 text-white px-4 py-2 rounded"
          >
            + Tambah Akun
          </button>
        </div>

        <div className="mb-4 flex gap-2">
          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="border p-2"
          >
            <option value="">Semua Tipe</option>
            {allAccountTypes.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
          <input
            placeholder="Cari nama akun"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="border p-2 w-full"
          />
        </div>

        <table className="w-full border text-sm">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2">No Akun</th>
              <th className="border p-2">Nama</th>
              <th className="border p-2">Tipe</th>
              <th className="border p-2">Saldo</th>
              <th className="border p-2 text-center">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {treeData.length > 0 ? (
              treeData.map((a) => (
                <tr key={a.id}>
                  <td className="border p-2">
                    {' '.repeat(a.level) + (a.account_no || '')}
                  </td>
                  <td className="border p-2">{' '.repeat(a.level) + a.name}</td>
                  <td className="border p-2">{a.account_type}</td>
                  <td className="border p-2">
                    {parseFloat(a.opening_balance || 0).toLocaleString()}
                  </td>
                  <td className="border p-2 text-center space-x-2">
                    <button
                      onClick={() => handleEdit(a)}
                      className="text-blue-600 hover:underline"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(a.id)}
                      className="text-red-600 hover:underline"
                    >
                      Hapus
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="text-center text-gray-500 py-4">
                  Tidak ada data akun
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Modal Tambah/Edit Akun */}
        {showModal && (
          <div className="fixed inset-0 bg-black bg-opacity-30 flex justify-center items-center z-50">
            <div className="bg-white p-6 rounded-lg w-full max-w-md relative shadow-lg">
              <button
                className="absolute top-2 right-2 text-gray-600"
                onClick={() => setShowModal(false)}
              >
                ✕
              </button>
              <h3 className="text-lg font-semibold mb-4">
                {editingId ? 'Edit Akun' : 'Tambah Akun Baru'}
              </h3>
              <form onSubmit={handleSubmit} className="space-y-3">
                <input
                  name="name"
                  value={form.name}
                  onChange={handleChange}
                  placeholder="Nama Akun"
                  className="border p-2 w-full"
                  required
                />
                <select
                  name="account_type"
                  value={form.account_type}
                  onChange={handleChange}
                  className="border p-2 w-full"
                  required
                >
                  <option value="">-- Pilih Tipe Akun --</option>
                  {Object.entries(accountTypeGroups).map(([group, types]) => (
                    <optgroup key={group} label={group}>
                      {types.map((type) => (
                        <option key={type} value={type}>
                          {type}
                        </option>
                      ))}
                    </optgroup>
                  ))}
                </select>
                <label className="flex items-center gap-2">
                  <input
                    type="checkbox"
                    name="is_sub_account"
                    checked={form.is_sub_account}
                    onChange={handleChange}
                  />
                  Akun Turunan (Sub-Akun)
                </label>

                {form.is_sub_account && (
                  <select
                    name="parent_id"
                    value={form.parent_id}
                    onChange={handleChange}
                    className="border p-2 w-full"
                    required
                  >
                    <option value="">-- Pilih Akun Induk --</option>
                    {parents
                      .filter((p) => p.account_type === form.account_type)
                      .map((p) => (
                        <option key={p.id} value={p.id}>
                          {p.name}
                        </option>
                      ))}
                  </select>
                )}

                <input
                  type="number"
                  name="opening_balance"
                  value={form.opening_balance}
                  onChange={handleChange}
                  placeholder="Saldo Awal (opsional)"
                  className="border p-2 w-full"
                />
                <input
                  type="date"
                  name="as_of_date"
                  value={form.as_of_date}
                  onChange={handleChange}
                  className="border p-2 w-full"
                  required
                />
                <div className="flex justify-end gap-2">
                  <button
                    type="button"
                    className="text-gray-600"
                    onClick={() => setShowModal(false)}
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="bg-blue-600 text-white px-4 py-2 rounded"
                  >
                    Simpan
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Modal Konfirmasi Hapus */}
        {confirmDeleteId && (
          <div className="fixed inset-0 bg-black bg-opacity-30 flex justify-center items-center z-50">
            <div className="bg-white p-6 rounded-lg max-w-sm w-full text-center">
              <h3 className="text-red-600 font-semibold text-lg mb-3">
                Konfirmasi Hapus
              </h3>
              <p className="text-sm text-gray-700 mb-5">
                Anda yakin ingin menghapus akun ini?
              </p>
              <div className="flex justify-center gap-4">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 text-gray-600"
                >
                  Batal
                </button>
                <button
                  onClick={confirmDelete}
                  className="bg-red-600 text-white px-4 py-2 rounded"
                >
                  Hapus
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
}
