import React, { useEffect, useState } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

export default function MstTax() {
  const [form, setForm] = useState({
    nama: '',
    nilai: '',
    kode: '',
    keterangan: '',
    akun_pajak_penjualan: '',
    akun_pajak_pembelian: '',
  });

  const [taxes, setTaxes] = useState([]);
  const [coaList, setCoaList] = useState([]);
  const [editingId, setEditingId] = useState(null);

  // Fetch data pajak dan COA
  const fetchData = async () => {
    const [taxRes, coaRes] = await Promise.all([
      api.get('/api/taxes'),
      api.get('/coa'),
    ]);
    setTaxes(taxRes.data);
    setCoaList(coaRes.data);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const getNomorAkun = (id) => {
    const coa = coaList.find((item) => item.id === parseInt(id));
    return coa ? coa.nomor_akun : '';
  };

  const resetForm = () => {
    setForm({
      nama: '',
      nilai: '',
      kode: '',
      keterangan: '',
      akun_pajak_penjualan: '',
      akun_pajak_pembelian: '',
    });
    setEditingId(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await api.put(`/taxes/${editingId}`, form);
      } else {
        await api.post('/taxes', form);
      }
      resetForm();
      fetchData();
    } catch (error) {
      console.error('Gagal menyimpan data', error);
      alert('Terjadi kesalahan saat menyimpan data.');
    }
  };

  const handleEdit = (tax) => {
    setForm({
      nama: tax.nama,
      nilai: tax.nilai,
      kode: tax.kode,
      keterangan: tax.keterangan,
      akun_pajak_penjualan: tax.akun_pajak_penjualan,
      akun_pajak_pembelian: tax.akun_pajak_pembelian,
    });
    setEditingId(tax.id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Yakin ingin menghapus data ini?')) {
      await api.delete(`/taxes/${id}`);
      fetchData();
    }
  };

  return (
    <Layout>
      <h2>Master Data Pajak</h2>

      <form onSubmit={handleSubmit} style={{ marginBottom: 30 }}>
        <div>
          <label>Nama:</label>
          <br />
          <input
            type="text"
            name="nama"
            value={form.nama}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Nilai (%):</label>
          <br />
          <input
            type="number"
            name="nilai"
            value={form.nilai}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Kode:</label>
          <br />
          <input
            type="text"
            name="kode"
            value={form.kode}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Keterangan:</label>
          <br />
          <textarea
            name="keterangan"
            value={form.keterangan}
            onChange={handleChange}
          />
        </div>

        <div>
          <label>Akun Pajak Penjualan:</label>
          <br />
          <select
            name="akun_pajak_penjualan"
            value={form.akun_pajak_penjualan}
            onChange={handleChange}
            required
          >
            <option value="">-- Pilih Akun --</option>
            {coaList.map((coa) => (
              <option key={coa.id} value={coa.id}>
                {coa.nama}
              </option>
            ))}
          </select>
          <input
            type="text"
            readOnly
            value={getNomorAkun(form.akun_pajak_penjualan)}
            placeholder="Nomor Akun"
          />
        </div>

        <div>
          <label>Akun Pajak Pembelian:</label>
          <br />
          <select
            name="akun_pajak_pembelian"
            value={form.akun_pajak_pembelian}
            onChange={handleChange}
            required
          >
            <option value="">-- Pilih Akun --</option>
            {coaList.map((coa) => (
              <option key={coa.id} value={coa.id}>
                {coa.nama}
              </option>
            ))}
          </select>
          <input
            type="text"
            readOnly
            value={getNomorAkun(form.akun_pajak_pembelian)}
            placeholder="Nomor Akun"
          />
        </div>

        <button type="submit">{editingId ? 'Update' : 'Simpan'}</button>
        {editingId && <button onClick={resetForm}>Batal</button>}
      </form>

      <table border="1" cellPadding="6" cellSpacing="0" width="100%">
        <thead>
          <tr>
            <th>Nama</th>
            <th>Nilai (%)</th>
            <th>Kode</th>
            <th>Akun Penjualan</th>
            <th>Akun Pembelian</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {taxes.map((tax) => (
            <tr key={tax.id}>
              <td>{tax.nama}</td>
              <td>{tax.nilai}</td>
              <td>{tax.kode}</td>
              <td>
                {tax.akun_penjualan?.nama} ({tax.akun_penjualan?.nomor_akun})
              </td>
              <td>
                {tax.akun_pembelian?.nama} ({tax.akun_pembelian?.nomor_akun})
              </td>
              <td>
                <button onClick={() => handleEdit(tax)}>Edit</button>
                <button onClick={() => handleDelete(tax.id)}>Hapus</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Layout>
  );
}
