// import Sidebar from './Sidebar'; // atau sementara kosongkan
import { ReactNode } from 'react';

function Layout({ children }) {
  return (
    <div className="flex">
      {/* Jika belum buat Sidebar, hapus atau ganti dummy */}
      <div className="w-[60px] bg-gray-200">Sidebar</div>
      <div className="flex-grow">
        <Header />
        <main className="p-4">{children}</main>
      </div>
    </div>
  );
}

export default Layout;
