import { useState, useEffect } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const categories = [
  { value: 'ASET', label: 'ASET' },
  { value: 'KEWAJIBAN', label: 'KEWAJIBAN' },
  { value: 'MODAL', label: 'MODAL' },
  { value: 'PENDAPATAN', label: 'PENDAPATAN' },
  { value: 'BIAYA', label: 'BIAYA' },
  { value: 'PENDAPATAN_LAIN', label: 'PENDAPATAN LAIN' },
];

const accountTypes = {
  ASET: [
    'Kas/Bank',
    'Akun Piutang',
    'Persediaan',
    'Aktiva Lancar lainnya',
    'Aktiva Tetap',
    'Akumulasi Penyusutan',
  ],
  KEWAJIBAN: ['Akun Hutang', 'Hutang Lancar Lainnya', 'Hutang Jangka Panjang'],
  MODAL: ['Ekuitas'],
  PENDAPATAN: ['Pendapatan'],
  BIAYA: ['Harga Pokok Penjualan', 'Beban', 'Beban Lain-lain'],
  PENDAPATAN_LAIN: ['Pendapatan Lain'],
};

export default function ChartOfAccount() {
  const today = new Date().toISOString().split('T')[0];
  const [accounts, setAccounts] = useState([]);
  const [form, setForm] = useState({
    name: '',
    category: '',
    account_type: '',
    is_group: false,
    parent_id: '',
    opening_balance: '',
    as_of_date: today,
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchAccounts = async () => {
    try {
      setLoading(true);
      const res = await api.get('/api/chart-of-accounts');
      setAccounts(res.data);
      setError(null);
    } catch (err) {
      setError('Gagal memuat data akun');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAccounts();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));

    // Reset dependent fields when category changes
    if (name === 'category') {
      setForm((prev) => ({
        ...prev,
        account_type: '',
        parent_id: '',
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!form.name.trim()) {
      setError('Nama akun wajib diisi');
      return;
    }
    if (!form.category) {
      setError('Kategori wajib dipilih');
      return;
    }
    if (!form.account_type) {
      setError('Tipe akun wajib dipilih');
      return;
    }

    try {
      const payload = {
        ...form,
        opening_balance:
          form.opening_balance === '' ? 0 : parseFloat(form.opening_balance),
      };

      const response = await api.post('/api/chart-of-accounts', payload);

      if (response.data.success) {
        fetchAccounts(); // Refresh data
        setForm({
          name: '',
          category: '',
          account_type: '',
          is_group: false,
          parent_id: '',
          opening_balance: '',
          as_of_date: today,
        });
        alert('Akun berhasil disimpan!');
      } else {
        setError(response.data.message || 'Gagal menyimpan akun');
      }
    } catch (err) {
      console.error(err);
      setError(
        err.response?.data?.message || 'Terjadi kesalahan saat menyimpan akun'
      );
    }
  };

  const renderAccountTree = (nodes, level = 0) => {
    return nodes.map((node) => (
      <>
        <tr
          key={node.id}
          className={`${level > 0 ? 'bg-gray-50' : 'bg-white'}`}
        >
          <td className="border p-2">
            {' '.repeat(level * 2)}
            {node.account_no}
          </td>
          <td className="border p-2">
            {' '.repeat(level * 2)}
            {node.name}
          </td>
          <td className="border p-2">{node.category}</td>
          <td className="border p-2">{node.account_type}</td>
          <td className="border p-2 text-right">
            {parseFloat(node.opening_balance || 0).toLocaleString('id-ID')}
          </td>
        </tr>
        {node.children && renderAccountTree(node.children, level + 1)}
      </>
    ));
  };

  return (
    <Layout>
      <div className="p-4">
        <h1 className="text-2xl font-bold mb-6">Chart of Accounts</h1>

        {/* Form Input */}
        <div className="bg-white p-6 rounded-lg shadow-md mb-6">
          <h2 className="text-xl font-semibold mb-4">Tambah Akun Baru</h2>

          {error && (
            <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-4">
              <p>{error}</p>
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Nama Akun
                </label>
                <input
                  name="name"
                  value={form.name}
                  onChange={handleChange}
                  className="border p-2 w-full rounded"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Kategori
                </label>
                <select
                  name="category"
                  value={form.category}
                  onChange={handleChange}
                  className="border p-2 w-full rounded"
                  required
                >
                  <option value="">Pilih Kategori</option>
                  {categories.map((cat) => (
                    <option key={cat.value} value={cat.value}>
                      {cat.label}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Tipe Akun
                </label>
                <select
                  name="account_type"
                  value={form.account_type}
                  onChange={handleChange}
                  className="border p-2 w-full rounded"
                  required
                  disabled={!form.category}
                >
                  <option value="">Pilih Tipe Akun</option>
                  {form.category &&
                    accountTypes[form.category].map((type) => (
                      <option key={type} value={type}>
                        {type}
                      </option>
                    ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Parent Akun
                </label>
                <select
                  name="parent_id"
                  value={form.parent_id}
                  onChange={handleChange}
                  className="border p-2 w-full rounded"
                  disabled={!form.category || form.is_group}
                >
                  <option value="">Pilih Parent (opsional)</option>
                  {accounts
                    .filter((a) => a.category === form.category && a.is_group)
                    .map((parent) => (
                      <option key={parent.id} value={parent.id}>
                        {parent.account_no} - {parent.name}
                      </option>
                    ))}
                </select>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Saldo Awal
                </label>
                <input
                  type="number"
                  name="opening_balance"
                  value={form.opening_balance}
                  onChange={handleChange}
                  className="border p-2 w-full rounded"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Tanggal
                </label>
                <input
                  type="date"
                  name="as_of_date"
                  value={form.as_of_date}
                  onChange={handleChange}
                  className="border p-2 w-full rounded"
                  required
                />
              </div>
            </div>

            <div className="flex items-center gap-2">
              <input
                type="checkbox"
                name="is_group"
                checked={form.is_group}
                onChange={handleChange}
                id="is_group"
                className="h-4 w-4"
              />
              <label
                htmlFor="is_group"
                className="text-sm font-medium text-gray-700"
              >
                Akun Group (untuk kategori/sub-kategori)
              </label>
            </div>

            <div className="flex justify-end pt-4">
              <button
                type="submit"
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
              >
                Simpan Akun
              </button>
            </div>
          </form>
        </div>

        {/* Daftar Akun */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-4">Daftar Akun</h2>

          {loading ? (
            <p>Memuat data...</p>
          ) : error ? (
            <p className="text-red-500">{error}</p>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full border">
                <thead>
                  <tr className="bg-gray-100">
                    <th className="border p-2">Nomor Akun</th>
                    <th className="border p-2">Nama Akun</th>
                    <th className="border p-2">Kategori</th>
                    <th className="border p-2">Tipe</th>
                    <th className="border p-2">Saldo</th>
                  </tr>
                </thead>
                <tbody>
                  {accounts.length > 0 ? (
                    renderAccountTree(accounts)
                  ) : (
                    <tr>
                      <td colSpan="5" className="text-center py-4">
                        Tidak ada data akun
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
}
