import React, { useEffect, useState } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';
import TypeTaxForm from './TypeTaxForm';

const TypeTaxList = () => {
  const [typeTaxes, setTypeTaxes] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedTypeTax, setSelectedTypeTax] = useState(null);

  const fetchData = async () => {
    try {
      const res = await api.get('/api/type-tax');
      setTypeTaxes(res.data);
    } catch (err) {
      console.error('Gagal mengambil data:', err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Yakin ingin menghapus data ini?')) {
      try {
        await api.delete(`/api/type-tax/${id}`);
        fetchData();
      } catch (err) {
        console.error('Gagal menghapus data:', err);
      }
    }
  };

  const handleEdit = (item) => {
    setSelectedTypeTax(item);
    setModalOpen(true);
  };

  const handleAdd = () => {
    setSelectedTypeTax(null);
    setModalOpen(true);
  };

  return (
    <Layout>
      <div className="p-4">
        <h1 className="text-xl font-bold mb-4">Master Data - Type Tax</h1>
        <button
          onClick={handleAdd}
          className="bg-blue-500 text-white px-4 py-2 rounded mb-4"
        >
          Tambah Type Tax
        </button>
        <table className="w-full border border-gray-300">
          <thead className="bg-gray-100">
            <tr>
              <th className="border p-2 text-center">No</th>
              <th className="border p-2">Nama</th>
              <th className="border p-2 text-center">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {typeTaxes.map((item, index) => (
              <tr key={item.id}>
                <td className="border p-2 text-center">{index + 1}</td>
                <td className="border p-2">{item.nama}</td>
                <td className="border p-2 text-center">
                  <button
                    onClick={() => handleEdit(item)}
                    className="bg-yellow-400 text-white px-3 py-1 rounded mr-2"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(item.id)}
                    className="bg-red-500 text-white px-3 py-1 rounded"
                  >
                    Hapus
                  </button>
                </td>
              </tr>
            ))}
            {typeTaxes.length === 0 && (
              <tr>
                <td colSpan="3" className="text-center p-4">
                  Data belum tersedia.
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {modalOpen && (
          <TypeTaxForm
            onClose={() => setModalOpen(false)}
            fetchData={fetchData}
            data={selectedTypeTax}
          />
        )}
      </div>
    </Layout>
  );
};

export default TypeTaxList;
