import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../../../axios';

const PaymentTermForm = () => {
  const [terms, setTerms] = useState([]);
  const [form, setForm] = useState({
    name: '',
    pay_within_days: '',
    discount_percent: '',
    due_days: '',
    note: '',
  });

  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    // Load list dan form
    const fetchData = async () => {
      try {
        const listRes = await api.get('/api/payment-terms');
        setTerms(listRes.data);
      } catch (err) {
        alert('Gagal memuat data list!');
      }

      if (id) {
        try {
          const formRes = await api.get(`/api/payment-terms/${id}`);
          setForm(formRes.data);
        } catch (err) {
          alert('Gagal memuat data untuk diedit!');
        }
      } else {
        // Reset form jika bukan mode edit
        setForm({
          name: '',
          pay_within_days: '',
          discount_percent: '',
          due_days: '',
          note: '',
        });
      }
    };

    fetchData();
  }, [id]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (id) {
        await api.put(`/api/payment-terms/${id}`, form);
        alert('Berhasil mengupdate!');
      } else {
        await api.post('/api/payment-terms', form);
        alert('Berhasil menambahkan!');
      }
      setForm({
        name: '',
        pay_within_days: '',
        discount_percent: '',
        due_days: '',
        note: '',
      });

      // Refresh list
      const listRes = await api.get('/api/payment-terms');
      setTerms(listRes.data);

      navigate('/master/payment-terms');
    } catch (err) {
      console.error(err);
      alert('Gagal menyimpan data!');
    }
  };

  const handleEdit = (termId) => {
    navigate(`/master/payment-terms/edit/${termId}`);
  };

  const handleDelete = async (termId) => {
    if (window.confirm('Yakin ingin menghapus data ini?')) {
      try {
        await api.delete(`/api/payment-terms/${termId}`);
        alert('Data berhasil dihapus!');
        const res = await api.get('/api/payment-terms');
        setTerms(res.data);
      } catch (err) {
        alert('Gagal menghapus data!');
      }
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>{id ? 'Edit' : 'Tambah'} Syarat Pembayaran</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: '30px' }}>
        <div>
          <label>Nama:</label>
          <br />
          <input
            name="name"
            value={form.name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Jika membayar antara:</label>
          <br />
          <input
            type="number"
            name="pay_within_days"
            value={form.pay_within_days}
            onChange={handleChange}
            required
          />{' '}
          hari
        </div>
        <div>
          <label>Akan dapat diskon:</label>
          <br />
          <input
            type="number"
            name="discount_percent"
            value={form.discount_percent}
            onChange={handleChange}
            required
          />{' '}
          %
        </div>
        <div>
          <label>Jatuh tempo:</label>
          <br />
          <input
            type="number"
            name="due_days"
            value={form.due_days}
            onChange={handleChange}
            required
          />{' '}
          hari
        </div>
        <div>
          <label>Keterangan:</label>
          <br />
          <textarea name="note" value={form.note} onChange={handleChange} />
        </div>
        <button type="submit" style={{ marginTop: '10px' }}>
          {id ? 'Update' : 'Simpan'}
        </button>
      </form>

      <hr />
      <h3>Daftar Syarat Pembayaran</h3>
      <table border="1" cellPadding="8" cellSpacing="0">
        <thead>
          <tr>
            <th>Nama</th>
            <th>Bayar (hari)</th>
            <th>Diskon (%)</th>
            <th>Jatuh Tempo</th>
            <th>Keterangan</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {terms.length === 0 ? (
            <tr>
              <td colSpan="6">Belum ada data</td>
            </tr>
          ) : (
            terms.map((term) => (
              <tr key={term.id}>
                <td>{term.name}</td>
                <td>{term.pay_within_days}</td>
                <td>{term.discount_percent}</td>
                <td>{term.due_days}</td>
                <td>{term.note}</td>
                <td>
                  <button onClick={() => handleEdit(term.id)}>Edit</button>{' '}
                  <button onClick={() => handleDelete(term.id)}>Hapus</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default PaymentTermForm;
