import React from 'react';
import { Form, Input } from 'antd';

const CatatanTab = ({ form }) => {
  return (
    <Form.Item
      label="Catatan"
      name="catatan"
      rules={[{ max: 1000, message: 'Catatan maksimal 1000 karakter' }]}
    >
      <Input.TextArea
        rows={6}
        showCount
        maxLength={1000}
        placeholder="Tambahkan catatan tentang pemasok ini..."
      />
    </Form.Item>
  );
};

export default CatatanTab;
