import React, { useState, useEffect } from 'react';
import Layout from '../../components/Layout';
import AddressTab from './tabs/AddressTab';
import TermTab from './tabs/TermTab';
import SalesTab from './tabs/SalesTab';
import ContactTab from './tabs/ContactTab';
import ShipToTab from './tabs/ShipToTab';
import CatatanTab from './tabs/CatatanTab'; // ✅ perbaikan di sini
import api from '../../axios';

const CustomerForm = ({ id }) => {
  const [form, setForm] = useState({
    customer_number: '',
    is_active: true,
    name: '',
    address: '',
    city: '',
    province: '',
    postal_code: '',
    country: '',
    phone: '',
    fax: '',
    contact_person: '',
    email: '',
    website: '',
    payment_term_id: '',
    credit_limit: '',
    max_invoice_amount: '',
    currency_id: '',
    opening_balance: 0,
    balance_date: '',
    payment_message: '',
    tax1_id: '',
    tax2_id: '',
    tax_number: '',
    tax_code: '',
    tax_address1: '',
    tax_address2: '',
    customer_type_id: '',
    price_level: 1,
    default_sales_discount: 0,
    notes: '',
    customer_contacts: [], // ✅ disesuaikan agar sama dengan backend
    ship_to_addresses: [], 
  });

  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('alamat');

  useEffect(() => {
    if (id) {
      api.get(`/customers/${id}`).then((res) => {
        setForm(res.data);
      });
    }
  }, [id]);

  const handleSubmit = async () => {
    setLoading(true);
    try {
      if (id) {
        await api.put(`/customers/${id}`, form);
      } else {
        await api.post('/customers', form);
      }
      alert('Data berhasil disimpan!');
    } catch (err) {
      console.error(err);
      alert('Terjadi kesalahan saat menyimpan.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Layout title="Form Pelanggan">
      {/* Tabs Navigasi */}
      <div className="tabs">
        {['alamat', 'termin', 'penjualan', 'kontak', 'shipto', 'catatan'].map(
          (tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`mr-2 px-4 py-2 ${
                activeTab === tab ? 'bg-blue-600 text-white' : 'bg-gray-200'
              }`}
            >
              {tab.charAt(0).toUpperCase() + tab.slice(1)}
            </button>
          )
        )}
      </div>

      {/* Isi Tab */}
      <div className="p-4 border mt-4 bg-white rounded shadow">
        {activeTab === 'alamat' && <AddressTab form={form} setForm={setForm} />}
        {activeTab === 'termin' && <TermTab form={form} setForm={setForm} />}
        {activeTab === 'penjualan' && (
          <SalesTab form={form} setForm={setForm} />
        )}
        {activeTab === 'kontak' && <ContactTab form={form} setForm={setForm} />}
        {activeTab === 'shipto' && <ShipToTab form={form} setForm={setForm} />}
        {activeTab === 'catatan' && (
          <CatatanTab form={form} setForm={setForm} />
        )}{' '}
      </div>

      {/* Tombol Simpan */}
      <div className="mt-4 flex justify-end">
        <button
          onClick={handleSubmit}
          disabled={loading}
          className="bg-blue-600 text-white px-6 py-2 rounded"
        >
          {loading ? 'Menyimpan...' : 'Simpan'}
        </button>
      </div>
    </Layout>
  );
};

export default CustomerForm;
