import React, { useState, useEffect, useCallback } from 'react';
import { Table, Button, Space, Input, Tag, message, Popconfirm } from 'antd';
import {
  PlusOutlined,
  EditOutlined,
  EyeOutlined,
  DeleteOutlined,
  SearchOutlined,
} from '@ant-design/icons';
import api from '../../axios';
import Layout from '../../components/Layout';
import PemasokForm from './PemasokForm';
import PemasokDetail from './PemasokDetail';

const PemasokList = () => {
  const [pemasoks, setPemasoks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [current, setCurrent] = useState(1);
  const [pageSize, setPageSize] = useState(10);
  const [total, setTotal] = useState(0);
  const [searchText, setSearchText] = useState('');
  const [modalVisible, setModalVisible] = useState(false);
  const [detailVisible, setDetailVisible] = useState(false);
  const [selectedPemasok, setSelectedPemasok] = useState(null);
  const [formType, setFormType] = useState('create');

  const fetchPemasoks = useCallback(async () => {
    setLoading(true);
    try {
      const response = await api.get('/api/pemasoks', {
        params: {
          page: current,
          per_page: pageSize,
          search: searchText,
        },
      });

      setPemasoks(response.data.data);
      setTotal(response.data.meta.total);
      setCurrent(response.data.meta.current_page);
    } catch (error) {
      console.error('Error fetching pemasoks:', error);
      message.error('Gagal mengambil data pemasok');
    } finally {
      setLoading(false);
    }
  }, [current, pageSize, searchText]);

  useEffect(() => {
    fetchPemasoks();
  }, [fetchPemasoks]);

  const handleTableChange = (pagination) => {
    setCurrent(pagination.current);
    setPageSize(pagination.pageSize);
  };

  const handleCreate = () => {
    setFormType('create');
    setSelectedPemasok(null);
    setModalVisible(true);
  };

  const handleEdit = (pemasok) => {
    setFormType('edit');
    setSelectedPemasok(pemasok);
    setModalVisible(true);
  };

  const handleView = (pemasok) => {
    setSelectedPemasok(pemasok);
    setDetailVisible(true);
  };

  const handleDelete = async (id) => {
    try {
      await api.delete(`/api/pemasoks/${id}`);
      message.success('Pemasok berhasil dihapus');
      fetchPemasoks();
    } catch (error) {
      console.error('Error deleting pemasok:', error);
      message.error('Gagal menghapus pemasok');
    }
  };

  const columns = [
    {
      title: 'No Pemasok',
      dataIndex: 'no_pemasok',
      key: 'no_pemasok',
      sorter: true,
    },
    {
      title: 'Nama',
      dataIndex: 'nama',
      key: 'nama',
      sorter: true,
      render: (text, record) => (
        <>
          {text} {record.non_aktif && <Tag color="red">Non-Aktif</Tag>}
        </>
      ),
    },
    {
      title: 'Telepon',
      dataIndex: 'telepon',
      key: 'telepon',
    },
    {
      title: 'Kontak',
      key: 'kontak',
      render: (_, record) => record.kontak || '-',
    },
    {
      title: 'Saldo Awal',
      dataIndex: 'saldo_awal',
      key: 'saldo_awal',
      align: 'right',
      render: (value, record) =>
        `${value?.toLocaleString('id-ID') || '0'} ${
          record.currency?.symbol || ''
        }`,
    },
    {
      title: 'Mata Uang',
      key: 'currency',
      render: (_, record) => record.currency?.name || '-',
    },
    {
      title: 'Aksi',
      key: 'actions',
      width: 120,
      render: (_, record) => (
        <Space size="small">
          <Button
            size="small"
            icon={<EyeOutlined />}
            onClick={() => handleView(record)}
            title="Detail"
          />
          <Button
            size="small"
            icon={<EditOutlined />}
            onClick={() => handleEdit(record)}
            title="Edit"
          />
          <Popconfirm
            title="Apakah Anda yakin ingin menghapus pemasok ini?"
            onConfirm={() => handleDelete(record.id)}
            okText="Ya"
            cancelText="Tidak"
          >
            <Button
              size="small"
              icon={<DeleteOutlined />}
              danger
              title="Hapus"
            />
          </Popconfirm>
        </Space>
      ),
    },
  ];

  return (
    <Layout>
      <div
        style={{
          marginBottom: 16,
          display: 'flex',
          justifyContent: 'space-between',
        }}
      >
        <Input
          placeholder="Cari pemasok..."
          prefix={<SearchOutlined />}
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
          style={{ width: 300 }}
          allowClear
        />
        <Button type="primary" icon={<PlusOutlined />} onClick={handleCreate}>
          Tambah Pemasok
        </Button>
      </div>

      <Table
        columns={columns}
        dataSource={pemasoks}
        loading={loading}
        rowKey="id"
        pagination={{ current, pageSize, total }}
        onChange={handleTableChange}
        scroll={{ x: true }}
      />

      <PemasokForm
        visible={modalVisible}
        onCancel={() => setModalVisible(false)}
        onSuccess={() => {
          fetchPemasoks();
          setModalVisible(false);
        }}
        pemasok={selectedPemasok}
        type={formType}
      />

      <PemasokDetail
        visible={detailVisible}
        onCancel={() => setDetailVisible(false)}
        pemasok={selectedPemasok}
      />
    </Layout>
  );
};

export default PemasokList;
