import React from 'react';

const ContactTab = ({ form, setForm }) => {
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({
      ...prev,
      contact: {
        ...prev.contact,
        [name]: value,
      },
    }));
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      <div>
        <label className="block mb-1 font-medium">Nama Kontak</label>
        <input
          type="text"
          name="name"
          value={form.contact?.name || ''}
          onChange={handleChange}
          className="w-full border rounded px-3 py-2"
        />
      </div>

      <div>
        <label className="block mb-1 font-medium">Email</label>
        <input
          type="email"
          name="email"
          value={form.contact?.email || ''}
          onChange={handleChange}
          className="w-full border rounded px-3 py-2"
        />
      </div>

      <div>
        <label className="block mb-1 font-medium">Telepon</label>
        <input
          type="text"
          name="phone"
          value={form.contact?.phone || ''}
          onChange={handleChange}
          className="w-full border rounded px-3 py-2"
        />
      </div>

      <div>
        <label className="block mb-1 font-medium">Jabatan</label>
        <input
          type="text"
          name="position"
          value={form.contact?.position || ''}
          onChange={handleChange}
          className="w-full border rounded px-3 py-2"
        />
      </div>
    </div>
  );
};

export default ContactTab;
