import Header from './Header';
import Sidebar from './Sidebar';

function Layout({ children }) {
  return (
    <div className="flex">
      <Sidebar />
      <div className="flex-1 min-h-screen pl-[50px]">
        <Header />
        <main className="pt-[64px] p-4">{children}</main>
      </div>
    </div>
  );
}

export default Layout;
