// src/Auth/Login.jsx
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../axios';
import { useAuth } from './AuthContext';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const { refreshAuth } = useAuth();
  const [loading, setLoading] = useState(false);

  const login = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await api.get('/sanctum/csrf-cookie');
      await api.post('/login', { email, password }, { withCredentials: true });

      // Tunggu sampai refresh selesai dan user dikenali
      const res = await api.get('/api/user', { withCredentials: true });
      if (res.data) {
        refreshAuth(); // update context (tidak wajib tapi bagus)
        navigate('/dashboard');
      } else {
        alert('Login gagal (user tidak ditemukan)');
      }
    } catch (err) {
      console.error('Login error:', err.response || err.message);
      alert('Login gagal');
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form onSubmit={login} className="bg-white p-6 rounded shadow-md w-96">
        <h2 className="text-2xl font-bold mb-4">Login</h2>
        <input
          className="block w-full mb-3 p-2 border rounded"
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          className="block w-full mb-3 p-2 border rounded"
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          disabled={loading}
        >
          {loading ? 'Memproses...' : 'Login'}
        </button>
      </form>
    </div>
  );
}

export default Login;
