import React, { useState, useEffect, useCallback } from 'react';
import {
  Table,
  Button,
  Modal,
  Form,
  Input,
  Tabs,
  message,
  Space,
  Divider,
  Popconfirm,
  Tag,
  Row,
  Col,
} from 'antd';
import {
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  PhoneOutlined,
  MailOutlined,
} from '@ant-design/icons';
import api from '../../../axios';

const { TabPane } = Tabs;

const KontakTab = ({ pemasokId, readOnly, refreshData }) => {
  const [kontaks, setKontaks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [modalVisible, setModalVisible] = useState(false);
  const [form] = Form.useForm();
  const [editId, setEditId] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  const fetchKontaks = useCallback(async () => {
    if (!pemasokId) return;
    setLoading(true);
    try {
      const response = await api.get(`api/pemasoks/${pemasokId}/kontaks`);
      setKontaks(response.data);
    } catch (error) {
      console.error('Error fetching kontaks:', error);
      message.error('Gagal mengambil data kontak');
    } finally {
      setLoading(false);
    }
  }, [pemasokId]);

  useEffect(() => {
    fetchKontaks();
  }, [fetchKontaks]);

  const handleAdd = () => {
    form.resetFields();
    setEditId(null);
    setModalVisible(true);
  };

  const handleEdit = (kontak) => {
    form.setFieldsValue({ ...kontak });
    setEditId(kontak.id);
    setModalVisible(true);
  };

  const handleDelete = async (id) => {
    try {
      await api.delete(`api/pemasoks/${pemasokId}/kontaks/${id}`);
      message.success('Kontak berhasil dihapus');
      fetchKontaks();
      if (refreshData) refreshData();
    } catch (error) {
      console.error('Error deleting kontak:', error);
      message.error('Gagal menghapus kontak');
    }
  };

  const handleSubmit = async () => {
    try {
      setSubmitting(true);
      const values = await form.validateFields();

      if (editId) {
        await api.put(`api/pemasoks/${pemasokId}/kontaks/${editId}`, values);
        message.success('Kontak berhasil diperbarui');
      } else {
        await api.post(`api/pemasoks/${pemasokId}/kontaks`, values);
        message.success('Kontak berhasil ditambahkan');
      }

      setModalVisible(false);
      fetchKontaks();
      if (refreshData) refreshData();
    } catch (error) {
      console.error('Error submitting kontak:', error);
      if (error.response?.data?.errors) {
        const errors = error.response.data.errors;
        Object.keys(errors).forEach((key) => {
          message.error(errors[key][0]);
        });
      }
    } finally {
      setSubmitting(false);
    }
  };

  const columns = [
    {
      title: 'Nama',
      key: 'nama',
      render: (_, record) => (
        <div>
          <strong>
            {record.nama_depan} {record.nama_belakang}
          </strong>
          {record.jabatan && (
            <div style={{ color: '#666' }}>{record.jabatan}</div>
          )}
        </div>
      ),
    },
    {
      title: 'Kontak',
      key: 'kontak',
      render: (_, record) => (
        <div>
          {record.bisnis1 && (
            <div>
              <PhoneOutlined /> {record.bisnis1}
              {record.bisnis1_ext && ` ext. ${record.bisnis1_ext}`}
            </div>
          )}
          {record.seluler && (
            <div>
              <PhoneOutlined /> {record.seluler} <Tag color="blue">Seluler</Tag>
            </div>
          )}
          {record.email && (
            <div>
              <MailOutlined /> {record.email}
            </div>
          )}
        </div>
      ),
    },
    ...(!readOnly
      ? [
          {
            title: 'Aksi',
            key: 'actions',
            width: 100,
            render: (_, record) => (
              <Space>
                <Button
                  type="text"
                  icon={<EditOutlined />}
                  onClick={() => handleEdit(record)}
                />
                <Popconfirm
                  title="Hapus kontak ini?"
                  onConfirm={() => handleDelete(record.id)}
                  okText="Ya"
                  cancelText="Tidak"
                >
                  <Button type="text" icon={<DeleteOutlined />} danger />
                </Popconfirm>
              </Space>
            ),
          },
        ]
      : []),
  ];

  return (
    <div>
      {!readOnly && (
        <div style={{ marginBottom: 16 }}>
          <Button type="primary" icon={<PlusOutlined />} onClick={handleAdd}>
            Tambah Kontak
          </Button>
        </div>
      )}

      <Table
        columns={columns}
        dataSource={kontaks}
        loading={loading}
        rowKey="id"
        pagination={false}
        bordered
      />

      {!readOnly && (
        <Modal
          title={editId ? 'Edit Kontak' : 'Tambah Kontak'}
          open={modalVisible}
          onCancel={() => setModalVisible(false)}
          width={700}
          footer={[
            <Button key="back" onClick={() => setModalVisible(false)}>
              Batal
            </Button>,
            <Button
              key="submit"
              type="primary"
              loading={submitting}
              onClick={handleSubmit}
            >
              Simpan
            </Button>,
          ]}
          destroyOnClose
        >
          <Form form={form} layout="vertical">
            <Tabs defaultActiveKey="1">
              <TabPane tab="Umum" key="1">
                <Row gutter={16}>
                  <Col span={12}>
                    <Form.Item
                      label="Nama Depan"
                      name="nama_depan"
                      rules={[
                        { required: true, message: 'Nama depan wajib diisi' },
                      ]}
                    >
                      <Input />
                    </Form.Item>
                  </Col>
                  <Col span={12}>
                    <Form.Item label="Nama Belakang" name="nama_belakang">
                      <Input />
                    </Form.Item>
                  </Col>
                </Row>

                <Form.Item label="Jabatan" name="jabatan">
                  <Input />
                </Form.Item>

                <Divider orientation="left">Telepon</Divider>

                <Row gutter={16}>
                  <Col span={16}>
                    <Form.Item label="Bisnis 1" name="bisnis1">
                      <Input />
                    </Form.Item>
                  </Col>
                  <Col span={8}>
                    <Form.Item label="Ext." name="bisnis1_ext">
                      <Input />
                    </Form.Item>
                  </Col>
                </Row>

                <Row gutter={16}>
                  <Col span={16}>
                    <Form.Item label="Bisnis 2" name="bisnis2">
                      <Input />
                    </Form.Item>
                  </Col>
                  <Col span={8}>
                    <Form.Item label="Ext." name="bisnis2_ext">
                      <Input />
                    </Form.Item>
                  </Col>
                </Row>

                <Row gutter={16}>
                  <Col span={12}>
                    <Form.Item
                      label="Seluler"
                      name="seluler"
                      rules={[
                        {
                          pattern: /^[0-9+\- ]+$/,
                          message: 'Format nomor tidak valid',
                        },
                      ]}
                    >
                      <Input />
                    </Form.Item>
                  </Col>
                  <Col span={12}>
                    <Form.Item
                      label="Faksimili"
                      name="faksimili"
                      rules={[
                        {
                          pattern: /^[0-9+\- ]+$/,
                          message: 'Format nomor tidak valid',
                        },
                      ]}
                    >
                      <Input />
                    </Form.Item>
                  </Col>
                </Row>

                <Row gutter={16}>
                  <Col span={12}>
                    <Form.Item
                      label="Pager"
                      name="pager"
                      rules={[
                        {
                          pattern: /^[0-9+\- ]+$/,
                          message: 'Format nomor tidak valid',
                        },
                      ]}
                    >
                      <Input />
                    </Form.Item>
                  </Col>
                  <Col span={12}>
                    <Form.Item
                      label="Rumah"
                      name="rumah"
                      rules={[
                        {
                          pattern: /^[0-9+\- ]+$/,
                          message: 'Format nomor tidak valid',
                        },
                      ]}
                    >
                      <Input />
                    </Form.Item>
                  </Col>
                </Row>

                <Form.Item
                  label="E-Mail"
                  name="email"
                  rules={[
                    { type: 'email', message: 'Format email tidak valid' },
                  ]}
                >
                  <Input />
                </Form.Item>
              </TabPane>

              <TabPane tab="Catatan" key="2">
                <Form.Item label="Catatan" name="catatan">
                  <Input.TextArea rows={6} />
                </Form.Item>
              </TabPane>
            </Tabs>
          </Form>
        </Modal>
      )}
    </div>
  );
};

export default KontakTab;
