import React, { useEffect, useState } from 'react';
import Layout from '../../components/Layout';
import api from '../../axios'; // pastikan sudah sesuai path-nya

const NamaTipeAkun = () => {
  const [tipeAkuns, setTipeAkuns] = useState([]);
  const [kategoris, setKategoris] = useState([]);
  const [form, setForm] = useState({ id: null, nama: '', kategori_id: '' });
  const [isEditing, setIsEditing] = useState(false);
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchTipeAkuns();
    fetchKategoris();
  }, []);

  const fetchTipeAkuns = async () => {
    try {
      const res = await api.get('/api/tipe-akun');
      setTipeAkuns(res.data);
    } catch (err) {
      console.error('Gagal fetch tipe akun:', err);
    }
  };

  const fetchKategoris = async () => {
    try {
      const res = await api.get('/api/kategori-akun');
      setKategoris(res.data);
    } catch (err) {
      console.error('Gagal fetch kategori akun:', err);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      nama: form.nama,
      kategori_id: form.kategori_id,
    };

    try {
      if (isEditing) {
        await api.put(`/api/tipe-akun/${form.id}`, payload);
      } else {
        await api.post('/api/tipe-akun', payload);
      }

      fetchTipeAkuns();
      closeModal();
    } catch (err) {
      console.error('Gagal simpan tipe akun:', err);
      alert('Terjadi kesalahan saat menyimpan data.');
    }
  };

  const handleEdit = (item) => {
    setForm({
      id: item.id,
      nama: item.nama,
      kategori_id: item.kategori_id,
    });
    setIsEditing(true);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    try {
      await api.delete(`/api/tipe-akun/${confirmDeleteId}`);
      fetchTipeAkuns();
      setConfirmDeleteId(null);
    } catch (err) {
      console.error('Gagal hapus tipe akun:', err);
      alert('Gagal menghapus data.');
    }
  };

  const openModal = () => {
    setForm({ id: null, nama: '', kategori_id: '' });
    setIsEditing(false);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setForm({ id: null, nama: '', kategori_id: '' });
    setIsEditing(false);
  };

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl">Master Nama Tipe Akun</h2>
          <button
            onClick={openModal}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            + Tambah
          </button>
        </div>

        <table className="w-full border">
          <thead className="bg-gray-100">
            <tr>
              <th className="border p-2">Nama</th>
              <th className="border p-2">Kategori</th>
              <th className="border p-2 text-center">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {tipeAkuns.map((item) => (
              <tr key={item.id}>
                <td className="border p-2">{item.nama}</td>
                <td className="border p-2">
                  {kategoris.find((k) => k.id === item.kategori_id)?.nama ||
                    'Tidak ditemukan'}
                </td>
                <td className="border p-2 flex justify-center gap-2">
                  <button
                    onClick={() => handleEdit(item)}
                    className="text-blue-600 hover:text-blue-800"
                    title="Edit"
                  >
                    <i className="bi bi-pencil-fill"></i>
                  </button>
                  <button
                    onClick={() => handleDelete(item.id)}
                    className="text-red-600 hover:text-red-800"
                    title="Hapus"
                  >
                    <i className="bi bi-trash-fill"></i>
                  </button>
                </td>
              </tr>
            ))}
            {tipeAkuns.length === 0 && (
              <tr>
                <td colSpan="3" className="text-center p-4">
                  Tidak ada data.
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Modal Tambah/Edit */}
        {showModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-md">
              <h2 className="text-lg font-semibold mb-4">
                {isEditing ? 'Edit Tipe Akun' : 'Tambah Tipe Akun'}
              </h2>
              <form onSubmit={handleSubmit}>
                <div className="mb-4">
                  <label className="block mb-1">Nama</label>
                  <input
                    name="nama"
                    value={form.nama}
                    onChange={handleChange}
                    className="w-full border px-2 py-1 rounded"
                    required
                  />
                </div>
                <div className="mb-4">
                  <label className="block mb-1">Kategori</label>
                  <select
                    name="kategori_id"
                    value={form.kategori_id}
                    onChange={handleChange}
                    className="w-full border px-2 py-1 rounded"
                    required
                  >
                    <option value="">Pilih Kategori</option>
                    {kategoris.map((kat) => (
                      <option key={kat.id} value={kat.id}>
                        {kat.nama}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="flex justify-end gap-2">
                  <button
                    type="button"
                    onClick={closeModal}
                    className="px-4 py-2 bg-gray-300 rounded"
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-blue-500 text-white rounded"
                  >
                    {isEditing ? 'Update' : 'Simpan'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Modal Konfirmasi Hapus */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-sm">
              <h2 className="text-lg font-semibold mb-4 text-red-600 flex items-center">
                <i className="bi bi-exclamation-triangle-fill mr-2 text-xl" />
                Konfirmasi Hapus
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Apakah Anda yakin ingin menghapus tipe akun ini?
              </p>
              <div className="flex justify-end gap-3">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 rounded text-gray-600 hover:text-black"
                >
                  Batal
                </button>
                <button
                  onClick={confirmDelete}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
                >
                  Hapus
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default NamaTipeAkun;
