import { useCallback, useState, useEffect } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

export default function ChartOfAccount() {
  const today = new Date().toISOString().split('T')[0];

  const [accounts, setAccounts] = useState([]);
  const [accountTypes, setAccountTypes] = useState([]);
  const [form, setForm] = useState({
    name: '',
    account_type: '',
    is_sub_account: false,
    parent_id: '',
    opening_balance: '',
    as_of_date: today,
    account_no: '', // Field untuk nomor akun opsional
  });

  const [editingId, setEditingId] = useState(null);
  const [filterType, setFilterType] = useState('');
  const [search, setSearch] = useState('');
  const [parents, setParents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchData = useCallback(async () => {
    setLoading(true);
    try {
      // Fetch account types terlebih dahulu
      const typesRes = await api.get('/api/chart-of-accounts/account-types', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          Accept: 'application/json',
        },
      });

      if (typesRes.data && typesRes.data.length > 0) {
        setAccountTypes(typesRes.data);
      } else {
        console.warn('No account types received from server');
        setAccountTypes([]);
      }

      // Fetch accounts
      const accountsRes = await api.get('/api/chart-of-accounts', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });

      setAccounts(accountsRes.data);
      setParents(accountsRes.data.filter((a) => !a.is_sub_account));
    } catch (err) {
      console.error('Fetch error:', {
        message: err.message,
        response: err.response?.data,
        status: err.response?.status,
      });

      if (err.response?.status === 401) {
        // Handle unauthorized
        localStorage.removeItem('token');
        window.location.href = '/login';
      } else {
        alert(err.response?.data?.message || 'Failed to load data');
      }
    } finally {
      setLoading(false);
    }
  }, [filterType, search]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleAccountTypeChange = (e) => {
    const accountTypeId = e.target.value;
    setForm((prev) => ({
      ...prev,
      account_type: accountTypeId,
      parent_id: '', // Reset parent ketika ganti tipe akun
      account_no: '', // Reset account_no ketika ganti tipe akun
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name.trim()) return alert('Nama akun wajib diisi.');
    if (!form.account_type) return alert('Tipe akun wajib dipilih.');
    if (!form.as_of_date) return alert('Tanggal wajib diisi.');
    if (form.is_sub_account && !form.parent_id)
      return alert('Pilih akun induk jika ini sub akun.');
    if (form.opening_balance && isNaN(parseFloat(form.opening_balance)))
      return alert('Opening balance harus berupa angka.');

    try {
      const payload = {
        ...form,
        opening_balance:
          form.opening_balance === '' ? 0 : parseFloat(form.opening_balance),
        account_no: form.account_no || null, // Kirim null jika kosong
      };

      setLoading(true);
      if (editingId) {
        await api.put(`/api/chart-of-accounts/${editingId}`, payload);
      } else {
        await api.post('/api/chart-of-accounts', payload);
      }

      // Reset form setelah submit
      setForm({
        name: '',
        account_type: '',
        is_sub_account: false,
        parent_id: '',
        opening_balance: '',
        as_of_date: today,
        account_no: '',
      });
      setEditingId(null);
      setShowModal(false);
      fetchData();
    } catch (err) {
      console.error('Failed to save account:', err);
      alert(
        err.response?.data?.message || 'Terjadi kesalahan saat menyimpan akun.'
      );
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (account) => {
    setForm({
      name: account.name,
      account_type: account.account_type,
      is_sub_account: account.is_sub_account,
      parent_id: account.parent_id || '',
      opening_balance: account.opening_balance,
      as_of_date: account.as_of_date,
      account_no: account.account_no,
    });
    setEditingId(account.id);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    try {
      setLoading(true);
      await api.delete(`/api/chart-of-accounts/${confirmDeleteId}`);
      setConfirmDeleteId(null);
      fetchData();
    } catch (err) {
      console.error('Failed to delete account:', err);
      alert('Gagal menghapus akun.');
    } finally {
      setLoading(false);
    }
  };

  const buildAccountTree = (list, parentId = null, level = 0) => {
    return list
      .filter((a) => a.parent_id === parentId)
      .flatMap((a) => [
        { ...a, level },
        ...buildAccountTree(list, a.id, level + 1),
      ]);
  };

  const treeData = buildAccountTree(accounts);

  const getAccountTypeName = (accountTypeId) => {
    const type = accountTypes.find((t) => t.id == accountTypeId);
    return type ? type.nama : 'Unknown';
  };

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Chart of Accounts</h2>
          <button
            onClick={() => {
              setEditingId(null);
              setForm({
                name: '',
                account_type: '',
                is_sub_account: false,
                parent_id: '',
                opening_balance: '',
                as_of_date: today,
                account_no: '',
              });
              setShowModal(true);
            }}
            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded transition"
            disabled={loading}
          >
            {loading ? 'Loading...' : '+ New Account'}
          </button>
        </div>

        <div className="mb-4 flex flex-col md:flex-row gap-2">
          <select
            onChange={(e) => setFilterType(e.target.value)}
            className="border p-2 rounded flex-1"
            value={filterType}
            disabled={loading}
          >
            <option value="">All Types</option>
            {accountTypes.map((type) => (
              <option key={type.id} value={type.id}>
                {type.nama}
              </option>
            ))}
          </select>
          <input
            placeholder="Search by name"
            onChange={(e) => setSearch(e.target.value)}
            className="border p-2 rounded flex-1"
            disabled={loading}
          />
        </div>

        {loading && !treeData.length ? (
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
            <p className="mt-2">Loading data...</p>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full border rounded-lg overflow-hidden">
              <thead>
                <tr className="bg-gray-100">
                  <th className="border p-2 text-left">Account No</th>
                  <th className="border p-2 text-left">Name</th>
                  <th className="border p-2 text-left">Type</th>
                  <th className="border p-2 text-right">Balance</th>
                  <th className="border p-2 text-center">Action</th>
                </tr>
              </thead>
              <tbody>
                {treeData.length > 0 ? (
                  treeData.map((a) => (
                    <tr key={a.id} className="hover:bg-gray-50">
                      <td className="border p-2">
                        {a.is_sub_account
                          ? ' '.repeat(a.level) + a.account_no
                          : a.account_no}
                      </td>
                      <td className="border p-2">
                        {a.is_sub_account
                          ? ' '.repeat(a.level) + a.name
                          : a.name}
                      </td>
                      <td className="border p-2">
                        {getAccountTypeName(a.account_type)}
                      </td>
                      <td className="border p-2 text-right">
                        {parseFloat(a.opening_balance || 0).toLocaleString(
                          'id-ID',
                          {
                            style: 'currency',
                            currency: 'IDR',
                            minimumFractionDigits: 0,
                          }
                        )}
                      </td>
                      <td className="border p-2 text-center space-x-2">
                        <button
                          onClick={() => handleEdit(a)}
                          className="bg-blue-50 hover:bg-blue-100 text-blue-600 hover:text-blue-800 px-3 py-1 rounded-md transition duration-200"
                          title="Edit"
                          disabled={loading}
                        >
                          <i className="bi bi-pencil-fill"></i>
                        </button>
                        <button
                          onClick={() => handleDelete(a.id)}
                          className="bg-red-50 hover:bg-red-100 text-red-600 hover:text-red-800 px-3 py-1 rounded-md transition duration-200"
                          title="Hapus"
                          disabled={loading}
                        >
                          <i className="bi bi-trash-fill"></i>
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="5" className="text-center text-gray-500 py-4">
                      No accounts found
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        )}

        {/* Modal Form */}
        {showModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
            <div className="bg-white p-6 rounded w-full max-w-lg relative shadow-lg mx-4">
              <button
                className="absolute top-2 right-2 text-gray-500 hover:text-black"
                onClick={() => {
                  setShowModal(false);
                  setEditingId(null);
                }}
                disabled={loading}
              >
                ✕
              </button>
              <h3 className="text-lg font-semibold mb-4">
                {editingId ? 'Edit Account' : 'Create New Account'}
              </h3>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Account Name
                  </label>
                  <input
                    name="name"
                    value={form.name}
                    onChange={handleChange}
                    placeholder="Account name"
                    className="border p-2 w-full rounded"
                    disabled={loading}
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Account Number (optional)
                  </label>
                  <input
                    name="account_no"
                    value={form.account_no}
                    onChange={handleChange}
                    placeholder="Will be generated automatically if empty"
                    className="border p-2 w-full rounded"
                    disabled={loading}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Account Type
                  </label>
                  <select
                    name="account_type"
                    value={form.account_type}
                    onChange={handleAccountTypeChange}
                    className="border p-2 w-full rounded"
                    disabled={loading}
                    required
                  >
                    <option value="">-- Select Account Type --</option>
                    {accountTypes.map((type) => (
                      <option key={type.id} value={type.id}>
                        {type.nama} ({type.kode})
                      </option>
                    ))}
                  </select>
                </div>

                <div className="flex items-center gap-2">
                  <input
                    type="checkbox"
                    name="is_sub_account"
                    checked={form.is_sub_account}
                    onChange={handleChange}
                    className="rounded"
                    disabled={loading || !form.account_type}
                    id="is_sub_account"
                  />
                  <label
                    htmlFor="is_sub_account"
                    className="text-sm font-medium text-gray-700"
                  >
                    This is a sub-account
                  </label>
                </div>

                {form.is_sub_account && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Parent Account
                    </label>
                    <select
                      name="parent_id"
                      value={form.parent_id}
                      onChange={handleChange}
                      className="border p-2 w-full rounded"
                      disabled={loading || !form.account_type}
                      required={form.is_sub_account}
                    >
                      {!form.account_type ? (
                        <option value="">
                          Please select account type first
                        </option>
                      ) : parents.filter(
                          (p) => p.account_type == form.account_type
                        ).length === 0 ? (
                        <option value="">No parent accounts available</option>
                      ) : (
                        <>
                          <option value="">-- Select Parent Account --</option>
                          {parents
                            .filter((p) => p.account_type == form.account_type)
                            .map((p) => (
                              <option key={p.id} value={p.id}>
                                {p.account_no} - {p.name}
                              </option>
                            ))}
                        </>
                      )}
                    </select>
                  </div>
                )}

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Opening Balance (optional)
                  </label>
                  <input
                    type="number"
                    name="opening_balance"
                    value={form.opening_balance}
                    onChange={handleChange}
                    placeholder="0"
                    className="border p-2 w-full rounded"
                    disabled={loading}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    As of Date
                  </label>
                  <input
                    type="date"
                    name="as_of_date"
                    value={form.as_of_date}
                    onChange={handleChange}
                    className="border p-2 w-full rounded"
                    disabled={loading}
                    required
                  />
                </div>

                <div className="flex justify-end gap-2 pt-2">
                  <button
                    type="button"
                    onClick={() => {
                      setShowModal(false);
                      setEditingId(null);
                    }}
                    className="px-4 py-2 rounded text-gray-600 hover:bg-gray-100 transition"
                    disabled={loading}
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded transition"
                    disabled={loading}
                  >
                    {loading ? 'Saving...' : 'Save'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Delete Confirmation Modal */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/10">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-sm mx-4">
              <h2 className="text-lg font-semibold mb-4 text-red-600 flex items-center">
                <i className="bi bi-exclamation-triangle-fill mr-2 text-xl" />
                Confirm Deletion
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Are you sure you want to delete this account? This action cannot
                be undone.
              </p>
              <div className="flex justify-end gap-3">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 rounded text-gray-600 hover:text-black transition"
                  disabled={loading}
                >
                  Cancel
                </button>
                <button
                  onClick={confirmDelete}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded transition"
                  disabled={loading}
                >
                  {loading ? 'Deleting...' : 'Delete'}
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
}
