import React, { useEffect, useState } from 'react';
import Layout from '../../components/Layout';
import api from '../../axios';
import PemasokForm from './PemasokForm';
import PemasokDetail from './PemasokDetail';

const PemasokList = () => {
  const [pemasoks, setPemasoks] = useState([]);
  const [selected, setSelected] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [showDetail, setShowDetail] = useState(false);

  const fetchData = async () => {
    const res = await api.get('/api/pemasok');
    setPemasoks(res.data);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Yakin ingin menghapus?')) {
      await api.delete(`/api/pemasok/${id}`);
      fetchData();
    }
  };

  return (
    <Layout title="Daftar Pemasok">
      <button
        onClick={() => {
          setSelected(null);
          setShowForm(true);
        }}
        className="btn btn-primary mb-3"
      >
        Tambah Pemasok
      </button>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>No Pemasok</th>
            <th>Nama</th>
            <th>Telepon</th>
            <th>Kontak</th>
            <th>Saldo Awal</th>
            <th>Mata Uang</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {pemasoks.map((item) => (
            <tr key={item.id}>
              <td>{item.no_pemasok}</td>
              <td>{item.nama}</td>
              <td>{item.telepon}</td>
              <td>{item.kontak}</td>
              <td>{item.saldo_awal}</td>
              <td>{item.currency?.name}</td>
              <td>
                <button
                  onClick={() => {
                    setSelected(item);
                    setShowForm(true);
                  }}
                  className="btn btn-sm btn-warning me-1"
                >
                  Edit
                </button>
                <button
                  onClick={() => {
                    setSelected(item);
                    setShowDetail(true);
                  }}
                  className="btn btn-sm btn-info me-1"
                >
                  Detail
                </button>
                <button
                  onClick={() => handleDelete(item.id)}
                  className="btn btn-sm btn-danger"
                >
                  Hapus
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {showForm && (
        <PemasokForm
          data={selected}
          onClose={() => {
            setShowForm(false);
            fetchData();
          }}
        />
      )}

      {showDetail && (
        <PemasokDetail data={selected} onClose={() => setShowDetail(false)} />
      )}
    </Layout>
  );
};

export default PemasokList;
