import React, { useEffect, useState } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const ChartOfAccounts = () => {
  const [tipeAkuns, setTipeAkuns] = useState([]);
  const [chartAccounts, setChartAccounts] = useState([]);
  const [formData, setFormData] = useState({
    nama: '',
    tipe_akun_id: '',
    nomor_akun: '',
    sub_account_of: '',
    saldo_awal: '',
    kalender: '',
    isSub: false,
  });

  const [subAccounts, setSubAccounts] = useState([]);
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    api.get('/api/tipe-akun').then((res) => setTipeAkuns(res.data));
    fetchAccounts();
  }, []);

  const fetchAccounts = () => {
    api.get('/api/chart-of-accounts').then((res) => {
      setChartAccounts(res.data);
      setSubAccounts(res.data);
    });
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleTipeAkunChange = (e) => {
    const tipeId = e.target.value;
    const tipe = tipeAkuns.find((t) => t.id === parseInt(tipeId));
    if (tipe) {
      const prefix = tipe.kode.slice(0, -4);
      const newNumber = prefix + '1000';
      setFormData((prev) => ({
        ...prev,
        tipe_akun_id: tipeId,
        nomor_akun: newNumber,
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const payload = { ...formData };
    if (!formData.isSub) payload.sub_account_of = null;

    if (editingId) {
      api.put(`/api/chart-of-accounts/${editingId}`, payload).then(() => {
        fetchAccounts();
        resetForm();
      });
    } else {
      api.post('/api/chart-of-accounts', payload).then(() => {
        fetchAccounts();
        resetForm();
      });
    }
  };

  const resetForm = () => {
    setFormData({
      nama: '',
      tipe_akun_id: '',
      nomor_akun: '',
      sub_account_of: '',
      saldo_awal: '',
      kalender: '',
      isSub: false,
    });
    setEditingId(null);
  };

  const handleEdit = (account) => {
    setEditingId(account.id);
    setFormData({
      nama: account.nama,
      tipe_akun_id: account.tipe_akun_id,
      nomor_akun: account.nomor_akun,
      sub_account_of: account.sub_account_of || '',
      saldo_awal: account.saldo_awal || '',
      kalender: account.kalender || '',
      isSub: !!account.sub_account_of,
    });
  };

  const handleDelete = (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus akun ini?')) {
      api.delete(`/api/chart-of-accounts/${id}`).then(fetchAccounts);
    }
  };

  return (
    <Layout>
      <div>
        <h2 className="text-xl font-bold mb-4">Chart of Accounts</h2>
        <form onSubmit={handleSubmit} className="space-y-4 mb-8">
          <div>
            <label>Nama:</label>
            <input
              name="nama"
              value={formData.nama}
              onChange={handleChange}
              required
              className="border p-2 rounded w-full"
            />
          </div>
          <div>
            <label>Tipe Akun:</label>
            <select
              name="tipe_akun_id"
              value={formData.tipe_akun_id}
              onChange={handleTipeAkunChange}
              required
              className="border p-2 rounded w-full"
            >
              <option value="">-- Pilih Tipe --</option>
              {tipeAkuns.map((tipe) => (
                <option key={tipe.id} value={tipe.id}>
                  {tipe.nama}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label>Nomor Akun:</label>
            <input
              name="nomor_akun"
              value={formData.nomor_akun}
              onChange={handleChange}
              required
              className="border p-2 rounded w-full"
            />
          </div>
          <div>
            <label>
              <input
                type="checkbox"
                name="isSub"
                checked={formData.isSub}
                onChange={handleChange}
                className="mr-2"
              />
              Sub Account of:
            </label>
            {formData.isSub && (
              <select
                name="sub_account_of"
                value={formData.sub_account_of}
                onChange={handleChange}
                className="border p-2 rounded w-full mt-2"
              >
                <option value="">-- Pilih Akun Induk --</option>
                {subAccounts.map((acc) => (
                  <option key={acc.id} value={acc.id}>
                    {acc.nama}
                  </option>
                ))}
              </select>
            )}
          </div>
          <div>
            <label>Saldo Awal:</label>
            <input
              type="number"
              name="saldo_awal"
              value={formData.saldo_awal}
              onChange={handleChange}
              className="border p-2 rounded w-full"
            />
          </div>
          <div>
            <label>Kalender:</label>
            <input
              type="date"
              name="kalender"
              value={formData.kalender}
              onChange={handleChange}
              required
              className="border p-2 rounded w-full"
            />
          </div>
          <button
            type="submit"
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
          >
            {editingId ? 'Update' : 'Simpan'}
          </button>
          {editingId && (
            <button
              type="button"
              onClick={resetForm}
              className="ml-2 bg-gray-400 hover:bg-gray-500 text-white px-4 py-2 rounded"
            >
              Batal
            </button>
          )}
        </form>

        <hr className="mb-4" />
        <h3 className="text-lg font-semibold mb-2">Daftar Akun</h3>
        <table className="w-full border-collapse border">
          <thead>
            <tr className="bg-gray-100">
              <th className="border px-3 py-2">Account No</th>
              <th className="border px-3 py-2">Name</th>
              <th className="border px-3 py-2">Type</th>
              <th className="border px-3 py-2">Balance</th>
              <th className="border px-3 py-2">Action</th>
            </tr>
          </thead>
          <tbody>
            {chartAccounts.map((a) => (
              <tr key={a.id}>
                <td className="border px-3 py-2">{a.nomor_akun}</td>
                <td className="border px-3 py-2">{a.nama}</td>
                <td className="border px-3 py-2">{a.tipe_akun?.nama}</td>
                <td className="border px-3 py-2">{a.saldo_awal || 0}</td>
                <td className="border px-3 py-2 space-x-2">
                  <button
                    onClick={() => handleEdit(a)}
                    className="bg-blue-50 hover:bg-blue-100 text-blue-600 hover:text-blue-800 px-3 py-2 rounded-md transition duration-200"
                    title="Edit"
                  >
                    <i className="bi bi-pencil-fill"></i>
                  </button>
                  <button
                    onClick={() => handleDelete(a.id)}
                    className="bg-red-50 hover:bg-red-100 text-red-600 hover:text-red-800 px-3 py-2 rounded-md transition duration-200"
                    title="Hapus"
                  >
                    <i className="bi bi-trash-fill"></i>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </Layout>
  );
};

export default ChartOfAccounts;
