import React, { useEffect, useState } from 'react';
import Layout from '../../components/Layout';

const API_BASE = 'http://192.168.1.26:8000/api';

const NamaTipeAkun = () => {
  const [tipeAkuns, setTipeAkuns] = useState([]);
  const [kategoris, setKategoris] = useState([]);
  const [form, setForm] = useState({ id: null, nama: '', kategori_id: '' });
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    fetch(`${API_BASE}/sanctum/csrf-cookie`, {
      credentials: 'include',
    }).then(() => {
      fetchTipeAkuns();
      fetchKategoris();
    });
  }, []);
  

  const fetchTipeAkuns = async () => {
    const res = await fetch(`${API_BASE}/tipe-akun`, {
      credentials: 'include',
      headers: { Accept: 'application/json' },
    });

    if (!res.ok) {
      console.error('Gagal fetch tipe akun:', res.status);
      return;
    }

    const data = await res.json();
    setTipeAkuns(data);
  };

  const fetchKategoris = async () => {
    const res = await fetch(`${API_BASE}/kategoris`, {
      credentials: 'include',
      headers: { Accept: 'application/json' },
    });

    if (!res.ok) {
      console.error('Gagal fetch kategori:', res.status);
      return;
    }

    const data = await res.json();
    setKategoris(data.data || data); // tergantung struktur respons Laravel
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = isEditing ? 'PUT' : 'POST';
    const url = isEditing
      ? `${API_BASE}/tipe-akun/${form.id}`
      : `${API_BASE}/tipe-akun`;

    try {
      const res = await fetch(url, {
        method,
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
          Accept: 'application/json',
        },
        body: JSON.stringify({
          nama: form.nama,
          kategori_id: form.kategori_id,
        }),
      });

      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(`Gagal simpan: ${res.status} - ${errorText}`);
      }

      await fetchTipeAkuns();
      setForm({ id: null, nama: '', kategori_id: '' });
      setIsEditing(false);
    } catch (err) {
      console.error(err.message);
      alert('Gagal menyimpan data. Cek konsol untuk detail.');
    }
  };

  const handleEdit = (tipeAkun) => {
    setForm({
      id: tipeAkun.id,
      nama: tipeAkun.nama,
      kategori_id: tipeAkun.kategori_id,
    });
    setIsEditing(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Yakin hapus data?')) {
      const res = await fetch(`${API_BASE}/tipe-akun/${id}`, {
        method: 'DELETE',
        credentials: 'include',
      });

      if (!res.ok) {
        console.error('Gagal hapus:', await res.text());
        alert('Gagal menghapus data');
        return;
      }

      fetchTipeAkuns();
    }
  };

  return (
    <Layout>
      <div className="p-6">
        <h2 className="text-2xl font-bold mb-4">Master Tipe Akun</h2>

        <form onSubmit={handleSubmit} className="space-y-4 mb-6">
          <input
            type="text"
            name="nama"
            value={form.nama}
            onChange={handleChange}
            placeholder="Nama Tipe Akun"
            className="border p-2 w-full rounded"
            required
          />
          <select
            name="kategori_id"
            value={form.kategori_id}
            onChange={handleChange}
            className="border p-2 w-full rounded"
            required
          >
            <option value="">-- Pilih Kategori --</option>
            {kategoris.map((kat) => (
              <option key={kat.id} value={kat.id}>
                {kat.nama}
              </option>
            ))}
          </select>
          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded"
          >
            {isEditing ? 'Update' : 'Simpan'}
          </button>
        </form>

        <table className="w-full border border-collapse">
          <thead>
            <tr className="bg-gray-200 text-left">
              <th className="border px-2 py-1">No</th>
              <th className="border px-2 py-1">Nama Tipe Akun</th>
              <th className="border px-2 py-1">Kategori</th>
              <th className="border px-2 py-1">Kode</th>
              <th className="border px-2 py-1">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {tipeAkuns.map((item, index) => (
              <tr key={item.id} className="hover:bg-gray-50">
                <td className="border px-2 py-1 text-center">{index + 1}</td>
                <td className="border px-2 py-1">{item.nama}</td>
                <td className="border px-2 py-1">{item.kategori_nama}</td>
                <td className="border px-2 py-1">{item.kode}</td>
                <td className="border px-2 py-1 space-x-2">
                  <button
                    onClick={() => handleEdit(item)}
                    className="bg-yellow-500 text-white px-3 py-1 rounded"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(item.id)}
                    className="bg-red-600 text-white px-3 py-1 rounded"
                  >
                    Hapus
                  </button>
                </td>
              </tr>
            ))}
            {tipeAkuns.length === 0 && (
              <tr>
                <td colSpan="5" className="text-center py-4">
                  Tidak ada data.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </Layout>
  );
};

export default NamaTipeAkun;
