import React, { useEffect, useState } from 'react';
import api from '../../../axios';

const TerminTab = ({ formData, setFormData }) => {
  const [paymentTerms, setPaymentTerms] = useState([]);
  const [currencies, setCurrencies] = useState([]);
  const [taxes, setTaxes] = useState([]);
  const [taxTypes, setTaxTypes] = useState([]);
  const [selectedTermDetail, setSelectedTermDetail] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const res1 = await api.get('/api/payment_terms');
    setPaymentTerms(res1.data);

    const res2 = await api.get('/api/currencies');
    setCurrencies(res2.data);

    const res3 = await api.get('/api/mst_tax');
    setTaxes(res3.data);

    const res4 = await api.get('/api/type_taxes');
    setTaxTypes(res4.data);
  };

  useEffect(() => {
    const selected = paymentTerms.find(
      (t) => t.id === parseInt(formData.termin_id)
    );
    setSelectedTermDetail(selected);
  }, [formData.termin_id, paymentTerms]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="row g-3">
      <div className="col-md-6">
        <label>Termin</label>
        <select
          className="form-select"
          name="termin_id"
          value={formData.termin_id}
          onChange={handleChange}
        >
          <option value="">-- Pilih Termin --</option>
          {paymentTerms.map((term) => (
            <option key={term.id} value={term.id}>
              {term.name}
            </option>
          ))}
        </select>
      </div>

      {selectedTermDetail && (
        <div className="col-12">
          <div className="alert alert-secondary">
            <div>
              <strong>Bayar Dalam Hari:</strong>{' '}
              {selectedTermDetail.pay_within_days}
            </div>
            <div>
              <strong>Diskon (%):</strong> {selectedTermDetail.discount_percent}
            </div>
            <div>
              <strong>Jatuh Tempo (hari):</strong> {selectedTermDetail.due_days}
            </div>
          </div>
        </div>
      )}

      <div className="col-md-6">
        <label>Mata Uang</label>
        <select
          className="form-select"
          name="currency_id"
          value={formData.currency_id}
          onChange={handleChange}
        >
          <option value="">-- Pilih Mata Uang --</option>
          {currencies.map((c) => (
            <option key={c.id} value={c.id}>
              {c.name}
            </option>
          ))}
        </select>
      </div>

      <div className="col-md-6">
        <label>Saldo Awal</label>
        <input
          type="number"
          className="form-control"
          name="saldo_awal"
          value={formData.saldo_awal}
          onChange={handleChange}
        />
      </div>

      <div className="col-md-6">
        <label>Per Tanggal</label>
        <input
          type="date"
          className="form-control"
          name="saldo_awal_tanggal"
          value={formData.saldo_awal_tanggal}
          onChange={handleChange}
        />
      </div>

      <div className="col-12">
        <label>Default Keterangan</label>
        <textarea
          className="form-control"
          name="default_keterangan"
          rows="2"
          value={formData.default_keterangan}
          onChange={handleChange}
        ></textarea>
      </div>

      <div className="col-md-6">
        <label>Pajak 1</label>
        <select
          className="form-select"
          name="pajak_1_id"
          value={formData.pajak_1_id}
          onChange={handleChange}
        >
          <option value="">-- Pilih Pajak 1 --</option>
          {taxes.map((tax) => (
            <option key={tax.id} value={tax.id}>
              {tax.nama}
            </option>
          ))}
        </select>
      </div>

      <div className="col-md-6">
        <label>Pajak 2</label>
        <select
          className="form-select"
          name="pajak_2_id"
          value={formData.pajak_2_id}
          onChange={handleChange}
        >
          <option value="">-- Pilih Pajak 2 --</option>
          {taxes.map((tax) => (
            <option key={tax.id} value={tax.id}>
              {tax.nama}
            </option>
          ))}
        </select>
      </div>

      <div className="col-md-6">
        <label>NPWP Pemasok</label>
        <input
          type="text"
          className="form-control"
          name="npwp"
          value={formData.npwp}
          onChange={handleChange}
        />
      </div>

      <div className="col-md-6">
        <label>Tax Type</label>
        <select
          className="form-select"
          name="tax_type_id"
          value={formData.tax_type_id}
          onChange={handleChange}
        >
          <option value="">-- Pilih Tax Type --</option>
          {taxTypes.map((t) => (
            <option key={t.id} value={t.id}>
              {t.nama}
            </option>
          ))}
        </select>
      </div>

      <div className="col-12">
        <label>Pesan</label>
        <textarea
          className="form-control"
          name="pesan"
          rows="2"
          value={formData.pesan}
          onChange={handleChange}
        ></textarea>
      </div>
    </div>
  );
};

export default TerminTab;
