import React, { useEffect, useState, useCallback } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const ChartOfAccounts = () => {
  const today = new Date().toISOString().split('T')[0];
  const [tipeAkuns, setTipeAkuns] = useState([]);
  const [currencies, setCurrencies] = useState([]);
  const [chartAccounts, setChartAccounts] = useState([]);
  const [subAccounts, setSubAccounts] = useState([]);
  const [filterTipe, setFilterTipe] = useState('');
  const [search, setSearch] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [sortBy, setSortBy] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc');
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);
  const [selectedTipeAkun, setSelectedTipeAkun] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const [formData, setFormData] = useState({
    nama: '',
    tipe_akun_id: '',
    nomor_akun: '',
    sub_account_of: null,
    saldo_awal: '',
    currency_id: '',
    kalender: today,
    isSub: false,
  });

  // Fetch data functions
  const fetchAccounts = useCallback(async () => {
    try {
      const res = await api.get('/api/chart-of-accounts');
      setChartAccounts(res.data);
      setSubAccounts(res.data);
    } catch (error) {
      console.error('Gagal memuat akun:', error);
      alert('Gagal memuat data akun');
    }
  }, []);

  const fetchInitialData = useCallback(async () => {
    setIsLoading(true);
    try {
      const [tipeRes, currencyRes] = await Promise.all([
        api.get('/api/tipe-akun'),
        api.get('/api/currencies'),
      ]);
      setTipeAkuns(tipeRes.data);
      setCurrencies(currencyRes.data);
      await fetchAccounts();
    } catch (error) {
      console.error('Gagal memuat data awal:', error);
      alert('Gagal memuat data awal');
    } finally {
      setIsLoading(false);
    }
  }, [fetchAccounts]);

  // Initial load
  useEffect(() => {
    fetchInitialData();
  }, [fetchInitialData]);

  // Search and filter
  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      if (search || filterTipe) {
        setIsLoading(true);
        api
          .get('/api/chart-of-accounts', {
            params: { search, tipe: filterTipe },
          })
          .then((res) => {
            setChartAccounts(res.data);
            setSubAccounts(res.data);
          })
          .catch((error) => {
            console.error('Gagal memfilter akun:', error);
            alert('Gagal memfilter data akun');
          })
          .finally(() => setIsLoading(false));
      } else {
        fetchAccounts();
      }
    }, 500);

    return () => clearTimeout(delayDebounce);
  }, [search, filterTipe, fetchAccounts]);

  // Handle form changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    setFormData((prev) => {
      const updatedForm = {
        ...prev,
        [name]: type === 'checkbox' ? checked : value,
      };

      // Handle account type change
      if (name === 'tipe_akun_id') {
        const tipe = tipeAkuns.find((t) => t.id === parseInt(value));
        setSelectedTipeAkun(tipe);

        if (!updatedForm.isSub) {
          updatedForm.nomor_akun = generateNomorAkun(value, false);
        }

        if (!tipe?.is_currency_required) {
          updatedForm.currency_id = '';
        }

        if (!tipe?.is_opening_balance_required) {
          updatedForm.saldo_awal = '';
        }
      }

      // Handle sub account toggle
      if (name === 'isSub') {
        updatedForm.sub_account_of = null;
        updatedForm.nomor_akun = updatedForm.tipe_akun_id
          ? generateNomorAkun(
              updatedForm.tipe_akun_id,
              checked,
              checked ? updatedForm.sub_account_of : null
            )
          : '';
      }

      // Handle parent account change
      if (name === 'sub_account_of') {
        updatedForm.nomor_akun = generateNomorAkun(
          updatedForm.tipe_akun_id,
          true,
          value
        );
      }

      return updatedForm;
    });

    // Clear error when field changes
    if (errors[name]) {
      setErrors((prev) => ({ ...prev, [name]: null }));
    }
  };

  // Generate account number
  const generateNomorAkun = (tipeId, isSub = false, parentId = null) => {
    if (!tipeId) return '';

    const tipe = tipeAkuns.find((t) => t.id === parseInt(tipeId));
    if (!tipe) return '';

    if (isSub && parentId) {
      const parent = chartAccounts.find((a) => a.id === parseInt(parentId));
      if (!parent) return '';

      const [seg1, seg2] = parent.nomor_akun.split('-');
      const subAccounts = chartAccounts.filter(
        (a) => a.sub_account_of === parent.id
      );

      const lastSubNumber =
        subAccounts.length > 0
          ? Math.max(
              ...subAccounts.map((a) => {
                const parts = a.nomor_akun.split('-');
                return parseInt(parts[2] || '0');
              })
            )
          : parseInt(parent.nomor_akun.split('-')[2] || '0');

      const nextNumber = lastSubNumber + 1;
      return `${seg1}-${seg2}-${nextNumber.toString().padStart(3, '0')}`;
    } else {
      const kode = tipe.kode || '1-1';
      const [seg1, seg2] = kode.split('-');

      const mainAccounts = chartAccounts.filter(
        (a) => a.tipe_akun_id === parseInt(tipeId) && !a.sub_account_of
      );

      const lastMainNumber =
        mainAccounts.length > 0
          ? Math.max(
              ...mainAccounts.map((a) => {
                const parts = a.nomor_akun.split('-');
                return parseInt(parts[2] || '0');
              })
            )
          : 0;

      const nextNumber = lastMainNumber + 1;
      return `${seg1}-${seg2}-${nextNumber.toString().padStart(3, '0')}`;
    }
  };

  // Form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrors({});

    // Frontend validation
    const newErrors = {};
    if (!formData.nama.trim()) newErrors.nama = 'Nama akun wajib diisi';
    if (!formData.tipe_akun_id)
      newErrors.tipe_akun_id = 'Tipe akun wajib dipilih';
    if (!formData.nomor_akun) newErrors.nomor_akun = 'Nomor akun wajib diisi';
    if (!formData.kalender) newErrors.kalender = 'Tanggal wajib diisi';

    if (formData.isSub && !formData.sub_account_of) {
      newErrors.sub_account_of = 'Akun induk wajib dipilih untuk sub akun';
    }

    if (selectedTipeAkun?.is_opening_balance_required && !formData.saldo_awal) {
      newErrors.saldo_awal = 'Saldo awal wajib diisi untuk tipe akun ini';
    }

    if (selectedTipeAkun?.is_currency_required && !formData.currency_id) {
      newErrors.currency_id = 'Mata uang wajib dipilih untuk tipe akun ini';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      setIsLoading(false);
      return;
    }

    // Prepare payload
    const payload = {
      nama: formData.nama,
      tipe_akun_id: formData.tipe_akun_id,
      nomor_akun: formData.nomor_akun,
      sub_account_of: formData.isSub ? formData.sub_account_of : null,
      saldo_awal: formData.saldo_awal ? parseFloat(formData.saldo_awal) : 0,
      currency_id: selectedTipeAkun?.is_currency_required
        ? formData.currency_id
        : null,
      kalender: formData.kalender,
    };

    try {
      let response;
      if (editingId) {
        response = await api.put(
          `/api/chart-of-accounts/${editingId}`,
          payload
        );
      } else {
        response = await api.post('/api/chart-of-accounts', payload);
      }

      if (response.data.success) {
        // Reset form
        setFormData({
          nama: '',
          tipe_akun_id: '',
          nomor_akun: '',
          sub_account_of: null,
          saldo_awal: '',
          currency_id: '',
          kalender: today,
          isSub: false,
        });
        setEditingId(null);
        setShowModal(false);
        setSelectedTipeAkun(null);

        // Refresh data
        await fetchAccounts();

        alert(response.data.message);
      } else {
        throw new Error(response.data.message || 'Gagal menyimpan akun');
      }
    } catch (err) {
      console.error('Gagal menyimpan akun:', err);

      if (err.response?.data?.errors) {
        setErrors(err.response.data.errors);
      } else {
        alert(
          err.response?.data?.message || err.message || 'Gagal menyimpan akun'
        );
      }
    } finally {
      setIsLoading(false);
    }
  };

  // Edit account
  const handleEdit = (akun) => {
    const tipe = tipeAkuns.find((t) => t.id === akun.tipe_akun_id);
    setSelectedTipeAkun(tipe);

    setFormData({
      nama: akun.nama,
      tipe_akun_id: akun.tipe_akun_id,
      nomor_akun: akun.nomor_akun,
      sub_account_of: akun.sub_account_of || null,
      saldo_awal: akun.saldo_awal?.toString() || '',
      currency_id: akun.currency_id || '',
      kalender: akun.kalender,
      isSub: !!akun.sub_account_of,
    });
    setEditingId(akun.id);
    setShowModal(true);
  };

  // Delete account
  const handleDelete = (id) => {
    setConfirmDeleteId(id);
  };

  const confirmDelete = async () => {
    setIsLoading(true);
    try {
      const response = await api.delete(
        `/api/chart-of-accounts/${confirmDeleteId}`
      );

      if (response.data.success) {
        setConfirmDeleteId(null);
        await fetchAccounts();
        alert(response.data.message);
      } else {
        throw new Error(response.data.message || 'Gagal menghapus akun');
      }
    } catch (err) {
      console.error('Gagal menghapus akun:', err);
      alert(
        err.response?.data?.message || err.message || 'Gagal menghapus akun'
      );
    } finally {
      setIsLoading(false);
    }
  };

  // Sorting
  const handleSort = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('asc');
    }
  };

  // Build account tree structure
  const buildAccountTree = (accounts, parentId = null, level = 0) => {
    return accounts
      .filter((account) => account.sub_account_of === parentId)
      .flatMap((account) => [
        { ...account, level },
        ...buildAccountTree(accounts, account.id, level + 1),
      ]);
  };

  // Filter and sort data
  const filteredAccounts = chartAccounts.filter((account) => {
    const matchesType = filterTipe
      ? account.tipe_akun_id.toString() === filterTipe
      : true;
    const matchesSearch = search
      ? account.nama.toLowerCase().includes(search.toLowerCase()) ||
        account.nomor_akun.toLowerCase().includes(search.toLowerCase())
      : true;
    return matchesType && matchesSearch;
  });

  const treeData = buildAccountTree(filteredAccounts);

  const sortedData = [...treeData].sort((a, b) => {
    if (!sortBy) return 0;

    const getValue = (item) => {
      if (sortBy === 'tipe') return item.tipe_akun?.nama || '';
      if (sortBy === 'saldo_awal') return parseFloat(item.saldo_awal) || 0;
      return item[sortBy] || '';
    };

    const valA = getValue(a);
    const valB = getValue(b);

    if (typeof valA === 'string') {
      return sortOrder === 'asc'
        ? valA.localeCompare(valB)
        : valB.localeCompare(valA);
    }

    return sortOrder === 'asc' ? valA - valB : valB - valA;
  });

  return (
    <Layout>
      <div className="p-4">
        {/* Header and controls */}
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-4 gap-4">
          <h1 className="text-2xl font-bold">Daftar Akun</h1>

          <div className="flex flex-col md:flex-row gap-2 w-full md:w-auto">
            <select
              value={filterTipe}
              onChange={(e) => setFilterTipe(e.target.value)}
              className="border p-2 rounded w-full md:w-48"
              disabled={isLoading}
            >
              <option value="">Semua Tipe Akun</option>
              {tipeAkuns.map((tipe) => (
                <option key={tipe.id} value={tipe.id}>
                  {tipe.nama}
                </option>
              ))}
            </select>

            <input
              type="text"
              placeholder="Cari nama/nomor akun..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="border p-2 rounded w-full"
              disabled={isLoading}
            />

            <button
              onClick={() => {
                setEditingId(null);
                setSelectedTipeAkun(null);
                setFormData({
                  nama: '',
                  tipe_akun_id: '',
                  nomor_akun: '',
                  sub_account_of: null,
                  saldo_awal: '',
                  currency_id: '',
                  kalender: today,
                  isSub: false,
                });
                setShowModal(true);
              }}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded whitespace-nowrap"
              disabled={isLoading}
            >
              + Tambah Akun
            </button>
          </div>
        </div>

        {/* Account table */}
        <div className="bg-white rounded-lg shadow overflow-hidden">
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th
                    className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer"
                    onClick={() => handleSort('nomor_akun')}
                  >
                    <div className="flex items-center">
                      Nomor Akun
                      {sortBy === 'nomor_akun' && (
                        <span className="ml-1">
                          {sortOrder === 'asc' ? '↑' : '↓'}
                        </span>
                      )}
                    </div>
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Nama Akun
                  </th>
                  <th
                    className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer"
                    onClick={() => handleSort('tipe')}
                  >
                    <div className="flex items-center">
                      Tipe Akun
                      {sortBy === 'tipe' && (
                        <span className="ml-1">
                          {sortOrder === 'asc' ? '↑' : '↓'}
                        </span>
                      )}
                    </div>
                  </th>
                  <th
                    className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer"
                    onClick={() => handleSort('saldo_awal')}
                  >
                    <div className="flex items-center justify-end">
                      Saldo Awal
                      {sortBy === 'saldo_awal' && (
                        <span className="ml-1">
                          {sortOrder === 'asc' ? '↑' : '↓'}
                        </span>
                      )}
                    </div>
                  </th>
                  <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Mata Uang
                  </th>
                  <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Aksi
                  </th>
                </tr>
              </thead>

              <tbody className="bg-white divide-y divide-gray-200">
                {isLoading && sortedData.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="px-6 py-4 text-center">
                      <div className="flex justify-center">
                        <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500"></div>
                      </div>
                    </td>
                  </tr>
                ) : sortedData.length > 0 ? (
                  sortedData.map((account) => (
                    <tr key={account.id} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          <div
                            style={{ marginLeft: `${account.level * 20}px` }}
                          >
                            {account.nomor_akun}
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          <div
                            style={{ marginLeft: `${account.level * 20}px` }}
                          >
                            {account.nama}
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {account.tipe_akun?.nama}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-right">
                        {account.currency?.symbol || ''}{' '}
                        {parseFloat(account.saldo_awal || 0).toLocaleString(
                          'id-ID'
                        )}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-center">
                        {account.currency?.symbol || '-'}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-center">
                        <div className="flex justify-center space-x-2">
                          <button
                            onClick={() => handleEdit(account)}
                            className="text-blue-600 hover:text-blue-900"
                            title="Edit"
                            disabled={isLoading}
                          >
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              className="h-5 w-5"
                              viewBox="0 0 20 20"
                              fill="currentColor"
                            >
                              <path d="M13.586 3.586a2 2 0 112.828 2.828l-.793.793-2.828-2.828.793-.793zM11.379 5.793L3 14.172V17h2.828l8.38-8.379-2.83-2.828z" />
                            </svg>
                          </button>
                          <button
                            onClick={() => handleDelete(account.id)}
                            className="text-red-600 hover:text-red-900"
                            title="Hapus"
                            disabled={isLoading}
                          >
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              className="h-5 w-5"
                              viewBox="0 0 20 20"
                              fill="currentColor"
                            >
                              <path
                                fillRule="evenodd"
                                d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z"
                                clipRule="evenodd"
                              />
                            </svg>
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td
                      colSpan="6"
                      className="px-6 py-4 text-center text-gray-500"
                    >
                      Tidak ada data ditemukan
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* Add/Edit Account Modal */}
        {showModal && (
          <div className="fixed inset-0 z-50 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
              <div
                className="fixed inset-0 transition-opacity"
                aria-hidden="true"
              >
                <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
              </div>

              <span
                className="hidden sm:inline-block sm:align-middle sm:h-screen"
                aria-hidden="true"
              >
                &#8203;
              </span>

              <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
                <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                  <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                    {editingId ? 'Edit Akun' : 'Tambah Akun Baru'}
                  </h3>

                  <form onSubmit={handleSubmit}>
                    <div className="space-y-4">
                      {/* Account Name */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Nama Akun <span className="text-red-500">*</span>
                        </label>
                        <input
                          type="text"
                          name="nama"
                          value={formData.nama}
                          onChange={handleChange}
                          className={`w-full border rounded-md px-3 py-2 ${
                            errors.nama ? 'border-red-500' : 'border-gray-300'
                          }`}
                          disabled={isLoading}
                        />
                        {errors.nama && (
                          <p className="mt-1 text-sm text-red-600">
                            {errors.nama}
                          </p>
                        )}
                      </div>

                      {/* Account Type */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Tipe Akun <span className="text-red-500">*</span>
                        </label>
                        <select
                          name="tipe_akun_id"
                          value={formData.tipe_akun_id}
                          onChange={handleChange}
                          className={`w-full border rounded-md px-3 py-2 ${
                            errors.tipe_akun_id
                              ? 'border-red-500'
                              : 'border-gray-300'
                          }`}
                          disabled={isLoading}
                        >
                          <option value="">Pilih Tipe Akun</option>
                          {tipeAkuns.map((tipe) => (
                            <option key={tipe.id} value={tipe.id}>
                              {tipe.nama}
                            </option>
                          ))}
                        </select>
                        {errors.tipe_akun_id && (
                          <p className="mt-1 text-sm text-red-600">
                            {errors.tipe_akun_id}
                          </p>
                        )}
                      </div>

                      {/* Account Number */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Nomor Akun <span className="text-red-500">*</span>
                        </label>
                        <input
                          type="text"
                          name="nomor_akun"
                          value={formData.nomor_akun}
                          onChange={handleChange}
                          className={`w-full border rounded-md px-3 py-2 ${
                            errors.nomor_akun
                              ? 'border-red-500'
                              : 'border-gray-300'
                          }`}
                          disabled={isLoading}
                        />
                        {errors.nomor_akun && (
                          <p className="mt-1 text-sm text-red-600">
                            {errors.nomor_akun}
                          </p>
                        )}
                      </div>

                      {/* Sub Account Toggle */}
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          id="isSub"
                          name="isSub"
                          checked={formData.isSub}
                          onChange={handleChange}
                          className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                          disabled={isLoading || !formData.tipe_akun_id}
                        />
                        <label
                          htmlFor="isSub"
                          className="ml-2 block text-sm text-gray-700"
                        >
                          Sub Akun
                        </label>
                      </div>

                      {/* Parent Account */}
                      {formData.isSub && (
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-1">
                            Akun Induk <span className="text-red-500">*</span>
                          </label>
                          <select
                            name="sub_account_of"
                            value={formData.sub_account_of || ''}
                            onChange={handleChange}
                            className={`w-full border rounded-md px-3 py-2 ${
                              errors.sub_account_of
                                ? 'border-red-500'
                                : 'border-gray-300'
                            }`}
                            disabled={isLoading || !formData.tipe_akun_id}
                          >
                            <option value="">Pilih Akun Induk</option>
                            {subAccounts
                              .filter(
                                (account) =>
                                  account.tipe_akun_id ===
                                    parseInt(formData.tipe_akun_id) &&
                                  !account.sub_account_of
                              )
                              .map((account) => (
                                <option key={account.id} value={account.id}>
                                  {account.nomor_akun} - {account.nama}
                                </option>
                              ))}
                          </select>
                          {errors.sub_account_of && (
                            <p className="mt-1 text-sm text-red-600">
                              {errors.sub_account_of}
                            </p>
                          )}
                        </div>
                      )}

                      {/* Opening Balance (conditional) */}
                      {selectedTipeAkun?.is_opening_balance_required && (
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-1">
                            Saldo Awal <span className="text-red-500">*</span>
                          </label>
                          <input
                            type="number"
                            name="saldo_awal"
                            value={formData.saldo_awal}
                            onChange={handleChange}
                            className={`w-full border rounded-md px-3 py-2 ${
                              errors.saldo_awal
                                ? 'border-red-500'
                                : 'border-gray-300'
                            }`}
                            disabled={isLoading}
                            step="0.01"
                          />
                          {errors.saldo_awal && (
                            <p className="mt-1 text-sm text-red-600">
                              {errors.saldo_awal}
                            </p>
                          )}
                        </div>
                      )}

                      {/* Currency (conditional) */}
                      {selectedTipeAkun?.is_currency_required && (
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-1">
                            Mata Uang <span className="text-red-500">*</span>
                          </label>
                          <select
                            name="currency_id"
                            value={formData.currency_id}
                            onChange={handleChange}
                            className={`w-full border rounded-md px-3 py-2 ${
                              errors.currency_id
                                ? 'border-red-500'
                                : 'border-gray-300'
                            }`}
                            disabled={isLoading}
                          >
                            <option value="">Pilih Mata Uang</option>
                            {currencies.map((currency) => (
                              <option key={currency.id} value={currency.id}>
                                {currency.name} ({currency.symbol})
                              </option>
                            ))}
                          </select>
                          {errors.currency_id && (
                            <p className="mt-1 text-sm text-red-600">
                              {errors.currency_id}
                            </p>
                          )}
                        </div>
                      )}

                      {/* Date */}
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Tanggal <span className="text-red-500">*</span>
                        </label>
                        <input
                          type="date"
                          name="kalender"
                          value={formData.kalender}
                          onChange={handleChange}
                          className={`w-full border rounded-md px-3 py-2 ${
                            errors.kalender
                              ? 'border-red-500'
                              : 'border-gray-300'
                          }`}
                          disabled={isLoading}
                        />
                        {errors.kalender && (
                          <p className="mt-1 text-sm text-red-600">
                            {errors.kalender}
                          </p>
                        )}
                      </div>
                    </div>

                    <div className="mt-5 sm:mt-6 sm:grid sm:grid-cols-2 sm:gap-3 sm:grid-flow-row-dense">
                      <button
                        type="submit"
                        className={`w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-blue-600 text-base font-medium text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:col-start-2 sm:text-sm ${
                          isLoading ? 'opacity-70 cursor-not-allowed' : ''
                        }`}
                        disabled={isLoading}
                      >
                        {isLoading ? (
                          <>
                            <svg
                              className="animate-spin -ml-1 mr-3 h-5 w-5 text-white"
                              xmlns="http://www.w3.org/2000/svg"
                              fill="none"
                              viewBox="0 0 24 24"
                            >
                              <circle
                                className="opacity-25"
                                cx="12"
                                cy="12"
                                r="10"
                                stroke="currentColor"
                                strokeWidth="4"
                              ></circle>
                              <path
                                className="opacity-75"
                                fill="currentColor"
                                d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                              ></path>
                            </svg>
                            Menyimpan...
                          </>
                        ) : (
                          'Simpan'
                        )}
                      </button>
                      <button
                        type="button"
                        onClick={() => {
                          setShowModal(false);
                          setEditingId(null);
                          setSelectedTipeAkun(null);
                        }}
                        className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:mt-0 sm:col-start-1 sm:text-sm"
                        disabled={isLoading}
                      >
                        Batal
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Delete Confirmation Modal */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
              <div
                className="fixed inset-0 transition-opacity"
                aria-hidden="true"
              >
                <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
              </div>

              <span
                className="hidden sm:inline-block sm:align-middle sm:h-screen"
                aria-hidden="true"
              >
                &#8203;
              </span>

              <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
                <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                  <div className="sm:flex sm:items-start">
                    <div className="mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full bg-red-100 sm:mx-0 sm:h-10 sm:w-10">
                      <svg
                        className="h-6 w-6 text-red-600"
                        xmlns="http://www.w3.org/2000/svg"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                        aria-hidden="true"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth="2"
                          d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
                        />
                      </svg>
                    </div>
                    <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
                      <h3 className="text-lg leading-6 font-medium text-gray-900">
                        Konfirmasi Hapus Akun
                      </h3>
                      <div className="mt-2">
                        <p className="text-sm text-gray-500">
                          Apakah Anda yakin ingin menghapus akun ini? Data yang
                          sudah dihapus tidak dapat dikembalikan.
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
                <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                  <button
                    type="button"
                    onClick={confirmDelete}
                    className={`w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-red-600 text-base font-medium text-white hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 sm:ml-3 sm:w-auto sm:text-sm ${
                      isLoading ? 'opacity-70 cursor-not-allowed' : ''
                    }`}
                    disabled={isLoading}
                  >
                    {isLoading ? (
                      <>
                        <svg
                          className="animate-spin -ml-1 mr-3 h-5 w-5 text-white"
                          xmlns="http://www.w3.org/2000/svg"
                          fill="none"
                          viewBox="0 0 24 24"
                        >
                          <circle
                            className="opacity-25"
                            cx="12"
                            cy="12"
                            r="10"
                            stroke="currentColor"
                            strokeWidth="4"
                          ></circle>
                          <path
                            className="opacity-75"
                            fill="currentColor"
                            d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                          ></path>
                        </svg>
                        Menghapus...
                      </>
                    ) : (
                      'Hapus'
                    )}
                  </button>
                  <button
                    type="button"
                    onClick={() => setConfirmDeleteId(null)}
                    className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
                    disabled={isLoading}
                  >
                    Batal
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default ChartOfAccounts;
