import React, { useEffect, useState } from 'react';
import api from '../../axios';
import Layout from '../../components/Layout';

const CustomerList = () => {
  const [customers, setCustomers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editCustomer, setEditCustomer] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    contact_person: '',
    opening_balance: '',
    currency_id: '',
  });
  const [confirmDeleteId, setConfirmDeleteId] = useState(null);

  const fetchCustomers = async () => {
    try {
      const res = await api.get('/api/customers');
      setCustomers(res.data.data);
    } catch (error) {
      console.error('Gagal mengambil data pelanggan:', error);
    }
  };

  useEffect(() => {
    fetchCustomers();
  }, []);

  const openModal = () => {
    setFormData({
      name: '',
      phone: '',
      contact_person: '',
      opening_balance: '',
      currency_id: '',
    });
    setEditCustomer(null);
    setShowModal(true);
  };

  const handleEdit = (cust) => {
    setFormData({
      name: cust.name || '',
      phone: cust.phone || '',
      contact_person: cust.contact_person || '',
      opening_balance: cust.opening_balance || '',
      currency_id: cust.currency?.id || '',
    });
    setEditCustomer(cust);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setEditCustomer(null);
    setFormData({
      name: '',
      phone: '',
      contact_person: '',
      opening_balance: '',
      currency_id: '',
    });
  };

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editCustomer) {
        await api.put(`/api/customers/${editCustomer.id}`, formData);
      } else {
        await api.post('/api/customers', formData);
      }
      fetchCustomers();
      closeModal();
    } catch (error) {
      console.error('Gagal menyimpan data:', error);
    }
  };

  const confirmDelete = async () => {
    try {
      await api.delete(`/api/customers/${confirmDeleteId}`);
      fetchCustomers();
      setConfirmDeleteId(null);
    } catch (error) {
      console.error('Gagal menghapus data:', error);
    }
  };

  return (
    <Layout>
      <div className="p-4">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold">Daftar Pelanggan</h2>
          <button
            onClick={openModal}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            + Tambah
          </button>
        </div>

        <table className="w-full border">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2 text-center">No</th>
              <th className="border p-2">Nama</th>
              <th className="border p-2">Telepon</th>
              <th className="border p-2">Kontak</th>
              <th className="border p-2 text-center">Saldo</th>
              <th className="border p-2 text-center">Mata Uang</th>
              <th className="border p-2 text-center">Aksi</th>
            </tr>
          </thead>
          <tbody>
            {customers.length > 0 ? (
              customers.map((cust, index) => (
                <tr key={cust.id}>
                  <td className="border p-2 text-center">{index + 1}</td>
                  <td className="border p-2">{cust.name}</td>
                  <td className="border p-2">{cust.phone}</td>
                  <td className="border p-2">{cust.contact_person}</td>
                  <td className="border p-2 text-center">
                    {cust.opening_balance}
                  </td>
                  <td className="border p-2 text-center">
                    {cust.currency
                      ? `${cust.currency.name} (${cust.currency.symbol})`
                      : '-'}
                  </td>
                  <td className="border p-2 text-center">
                    <div className="flex justify-center gap-2">
                      <button
                        onClick={() => handleEdit(cust)}
                        className="bg-blue-50 hover:bg-blue-100 text-blue-600 hover:text-blue-800 px-3 py-2 rounded-md transition duration-200"
                        title="Edit"
                      >
                        <i className="bi bi-pencil-fill"></i>
                      </button>
                      <button
                        onClick={() => setConfirmDeleteId(cust.id)}
                        className="bg-red-50 hover:bg-red-100 text-red-600 hover:text-red-800 px-3 py-2 rounded-md transition duration-200"
                        title="Hapus"
                      >
                        <i className="bi bi-trash-fill"></i>
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" className="text-center p-4">
                  Tidak ada data pelanggan.
                </td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Modal Form Tambah/Edit */}
        {showModal && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/20">
            <div className="bg-white p-6 rounded w-full max-w-lg relative shadow-lg">
              <h2 className="text-lg mb-4 font-semibold">
                {editCustomer ? 'Edit Pelanggan' : 'Tambah Pelanggan'}
              </h2>
              <form onSubmit={handleSubmit} className="space-y-3">
                <div>
                  <label className="block text-sm font-medium mb-1">Nama</label>
                  <input
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">
                    Telepon
                  </label>
                  <input
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">
                    Kontak
                  </label>
                  <input
                    name="contact_person"
                    value={formData.contact_person}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">
                    Saldo Awal
                  </label>
                  <input
                    type="number"
                    name="opening_balance"
                    value={formData.opening_balance}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-1">
                    ID Mata Uang
                  </label>
                  <input
                    name="currency_id"
                    value={formData.currency_id}
                    onChange={handleChange}
                    className="w-full border px-3 py-2 rounded"
                  />
                </div>
                <div className="flex justify-end gap-2 pt-4">
                  <button
                    type="button"
                    onClick={closeModal}
                    className="px-4 py-2 bg-gray-300 rounded"
                  >
                    Batal
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-blue-600 text-white rounded"
                  >
                    {editCustomer ? 'Update' : 'Simpan'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Modal Konfirmasi Hapus */}
        {confirmDeleteId && (
          <div className="fixed inset-0 z-50 flex items-center justify-center backdrop-blur-sm bg-black/20">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-sm">
              <h2 className="text-lg font-semibold mb-4 text-red-600 flex items-center">
                <i className="bi bi-exclamation-triangle-fill mr-2 text-xl" />
                Konfirmasi Hapus
              </h2>
              <p className="mb-6 text-sm text-gray-700">
                Apakah Anda yakin ingin menghapus pelanggan ini?
              </p>
              <div className="flex justify-end gap-3">
                <button
                  onClick={() => setConfirmDeleteId(null)}
                  className="px-4 py-2 rounded text-gray-600 hover:text-black"
                >
                  Batal
                </button>
                <button
                  onClick={confirmDelete}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
                >
                  Hapus
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default CustomerList;
