import { useState } from 'react';
import Layout from '../../components/Layout';
import api from '../../axios';

const accountTypes = [
  'Cash/Bank',
  'Account Receivable',
  'Inventory',
  'Other Current Asset',
  'Fixed Asset',
  'Accumulated Depreciation',
  'Account Payable',
  'Other Current Liability',
];

export default function ChartOfAccount() {
  const [accounts, setAccounts] = useState([]);
  const [form, setForm] = useState({
    name: '',
    account_type: '',
    is_sub_account: false,
    parent_id: '',
    opening_balance: '',
    as_of_date: '',
  });
  const [filterType, setFilterType] = useState('');
  const [search, setSearch] = useState('');
  const [parents, setParents] = useState([]);

  const fetchAccounts = async () => {
    const res = await api.get('/api/chart-of-accounts', {
      params: { type: filterType, search },
    });
    setAccounts(res.data);
    setParents(res.data.filter((a) => !a.is_sub_account));
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await api.post('/api/chart-of-accounts', form);
    setForm({
      name: '',
      account_type: '',
      is_sub_account: false,
      parent_id: '',
      opening_balance: '',
      as_of_date: '',
    });
    fetchAccounts();
  };

  useEffect(() => {
    fetchAccounts();
  }, [filterType, search]);

  return (
    <Layout>
      <div className="p-4">
        <h2 className="text-xl mb-4">Chart of Accounts</h2>

        <form onSubmit={handleSubmit} className="mb-4 space-y-2">
          <input
            name="name"
            value={form.name}
            onChange={handleChange}
            placeholder="Name"
            className="border p-2 w-full"
          />
          <select
            name="account_type"
            value={form.account_type}
            onChange={handleChange}
            className="border p-2 w-full"
          >
            <option value="">-- Select Account Type --</option>
            {accountTypes.map((type) => (
              <option key={type}>{type}</option>
            ))}
          </select>

          <label className="flex items-center gap-2">
            <input
              type="checkbox"
              name="is_sub_account"
              checked={form.is_sub_account}
              onChange={handleChange}
            />
            Sub Account of
          </label>

          {form.is_sub_account && (
            <select
              name="parent_id"
              value={form.parent_id}
              onChange={handleChange}
              className="border p-2 w-full"
            >
              <option value="">-- Select Parent Account --</option>
              {parents.map((p) => (
                <option key={p.id} value={p.id}>
                  {p.name}
                </option>
              ))}
            </select>
          )}

          <input
            type="number"
            name="opening_balance"
            value={form.opening_balance}
            onChange={handleChange}
            placeholder="Opening Balance"
            className="border p-2 w-full"
          />
          <input
            type="date"
            name="as_of_date"
            value={form.as_of_date}
            onChange={handleChange}
            className="border p-2 w-full"
          />

          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Save
          </button>
        </form>

        <div className="mb-2">
          <select
            onChange={(e) => setFilterType(e.target.value)}
            className="border p-2 mr-2"
          >
            <option value="">All Types</option>
            {accountTypes.map((type) => (
              <option key={type}>{type}</option>
            ))}
          </select>
          <input
            placeholder="Search by name"
            onChange={(e) => setSearch(e.target.value)}
            className="border p-2"
          />
        </div>

        <table className="w-full border">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2">Account No</th>
              <th className="border p-2">Name</th>
              <th className="border p-2">Type</th>
              <th className="border p-2">Balance</th>
            </tr>
          </thead>
          <tbody>
            {accounts.map((a) => (
              <tr key={a.id}>
                <td className="border p-2">{a.account_no}</td>
                <td className="border p-2">{a.name}</td>
                <td className="border p-2">{a.account_type}</td>
                <td className="border p-2">
                  {parseFloat(a.opening_balance).toLocaleString()}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </Layout>
  );
}
